    text-decoration: none;
    background-color: #D58E84;
    border-color: #D58E84;
}
.PagineDopoOver {
    font-size: 13px;
    padding: 0 3px;
    border-radius: 4px;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    -khtml-border-radius: 4px;
    display: inline-block;
    text-align: center;
    color: #ffffff;
    text-decoration: none;
    background-color: #D58E84;
    border-color: #D58E84;
}
}

