css:
@-moz-document domain("moodle.donnelly.edu") {
/* old Background*/
body {
    font-family: "Open Sans","Helvetica Neue",Arial,sans-serif;
    font-size: 1rem;
    line-height: 1.5;
    color: #d2d2d2;
    background-color: #383838;
}


/*Card Blocks*/
.card, #page-enrol-users #filterform, .path-calendar .maincalendar .calendar-event-panel, .block .calendar-event-panel .yui3-overlay-content, .que .history, .userprofile .profile_tree section, .groupinfobox, .forumpost, .yui-skin-sam .yui-layout.path-mod-chat-gui_ajax .yui-layout-unit-right, .yui-skin-sam .yui-layout.path-mod-chat-gui_ajax .yui-layout-unit-bottom, .yui-skin-sam .yui-layout.path-mod-chat-gui_ajax #chat-messages .chat-message.course-theme, .well {
    position: relative;
    display: block;
    margin-bottom: .75rem;
    background-color: rgb(39, 36, 36);
    border-radius: 0;
    border: 10px solid rgba(255, 0, 0, 0.32);
}

/*Even Card Block Pattern*/
.courses .coursebox.even {
    background-color: rgb(115, 74, 74);
}

/*Font Color*/
a {
    color: #ffffff;
    text-decoration: none;
}

/*Navbar Style*/
.navbar-light {
    background-color: #000;
    border-bottom: 3px solid rgba(255, 0, 0, 0.45);
}
/*Navbar Text*/
.navbar-light .navbar-nav .nav-link {
    color: rgb(255, 255, 255);
}

/*Message Pop Up*/
.popover-region-container {
    opacity: 1;
    visibility: visible;
    position: absolute;
    right: 0;
    top: 0;
    height: 500px;
    width: 380px;
    border: 1px solid #f00;
    -webkit-transition: height .25s;
    -o-transition: height .25s;
    transition: height .25s;
    background-color: rgba(0, 0, 0, 0.50);
    z-index: 1;
}
.popover-region-footer-container {
    height: 30px;
    text-align: center;
    border-top: 1px solid #f00;
    background-color: rgba(19, 18, 18, 0.50);
    padding-top: 3px;
}

/*Button Style*/
.btn-secondary, .btn-default {
    color: #ff0808;
    background-color: #928a8a;
    border-color: #f00;
}

/*Funny Footer*/
#page-footer {
    background-color: rgb(74, 105, 78)!important;
}

/*Test Block*/
.alert-info, #page-admin-index .releasenoteslink, #adminsettings .form-overridden, .que .formulation {
    background-color: #bf7e7e;
    border-color: #ffffff;
    color: #ffffff;
}

/*Test Question*/
.que .info {
    float: left;
    width: 7em;
    padding: .5em;
    margin-bottom: 1.8em;
    background-color: #bf7e7e;
    border: 1px solid #ffffff;
    border-radius: 2px;
}

/*Questions number button*/
.path-mod-quiz #mod_quiz_navblock .qnbutton {
    text-decoration: none;
    font-size: 14px;
    line-height: 20px;
    font-weight: 400;
    background-color: #383838;
    background-image: none;
    height: 40px;
    width: 30px;
    border-radius: 3px;
    border: 0;
    overflow: visible;
    margin: 0 6px 6px 0;
}

/*Test Result saved*/
.table-striped tbody tr:nth-of-type(odd), table.collection tbody tr:nth-of-type(odd), table#explaincaps tbody tr:nth-of-type(odd), table#defineroletable tbody tr:nth-of-type(odd), table.grading-report tbody tr:nth-of-type(odd), table#listdirectories tbody tr:nth-of-type(odd), table.rolecaps tbody tr:nth-of-type(odd), table.userenrolment tbody tr:nth-of-type(odd), form#movecourses table tbody tr:nth-of-type(odd), #page-admin-course-index .editcourse tbody tr:nth-of-type(odd), .forumheaderlist tbody tr:nth-of-type(odd), table.flexible tbody tr:nth-of-type(odd), .generaltable tbody tr:nth-of-type(odd) {
    background-color: #5f2323;
}

/*Memo box text?*/
table {
    border-collapse: collapse;
    background-color: #5d3b3b !important;
}

/*TEST TEST TEST TEST TEST*/

body {
background: url(https://i.pinimg.com/736x/5b/5c/27/5b5c271abababa186c749db60e936d90--wallpaper-texture-red-wallpaper.jpg);
    background-color: hsl(254, 0%, 6%);
    background-size: 20%;
}
}
