css:
@-moz-document domain("piazza.com") {
/* Main page */
  
  #page_main .views {
    background: #333 !important;
    color: #ccc !important;
  }
  
  .homescreen .homescreen_box {
    background: #FF0090 !important;
    color: #ccc !important;
  }
  
  /* Resourses */

  .top_bar .navbar-inner {
    background: #FF0090 !important;
  }

  #Page {
	background: #444 !important;
  }
  
  #Page #header {
	background: #444 !important;
    color: #FF0090 !important;
  }

  .alert {
    color: #FFF !important;
    background: #FF0090 !important;
    border: 0 !important;
  }

  .well {
    color: #FFF !important;
    background: #FF0090 !important;
    border: 0 !important;
  }
    
  
  /* Discussions */
  
  .post_region_box {
    background-color: #202020 !important;
  }
  
  .post_region_box .post_region_header .post_title {
    color: #aaa !important;
  }
  
  .post_region_box .post_region_content .clarifying_discussion {
    background-color: #444 !important;
  }
  
  .post_region_box .discussion_replies {
    background-color: #202020 !important;
  }
  
  .post_region_box .post_region_actions {
    background-color: #222222 !important;
  }
  
  .post_region_box .post_region_content .post_region_text {
    color: #d7d7d7 !important;
  }
  
  .post_region_box .post_region_content h1,
  .post_region_box .post_region_content h2,
  .post_region_box .post_region_content h3,
  .post_region_box .post_region_content h4,
  .post_region_box .post_region_content h5,
  .post_region_box .post_region_content h6,
  .post_region_box .post_region_content h7 {
    color: #e7e7e7 !important;
  }

  .post_region_box .post_region_actions .post_action{
    color: #FFFFFF !important;
  }

  .post_region_box .post_region_content .clarifying_discussion{
    background-color: #1b1b1b !important;
  }

  a{
    color: #FF0090 !important;
  }

  a:hover{
    color: #DD0060 !important;
  }

  .post_region_box .post_region_content .post_region_text{
    color: #424242 !important;
  }

  .post_region_box .post_region_content h1{
    color: #424242 !important;
  }

  .post_region_box .resolve_unresolve_actions{
    color: #000000 !important;
  }

  .post_region_box .post_region_content .post_region_text {
	color: #ababab !important;
  }

  .post_region_box .post_region_content h1 {
    color: #FFFFFF !important;
  }

  .homescreen .homescreen_box .notification_wrapper{
	color: #FFF !important;
  }

  .homescreen .homescreen_box .homescreen_box_section .job_label {
	color: #FFF !important;
  }

  .homescreen .homescreen_box .homescreen_box_section .job_label {
 	color: #FFF !important;                      
  }

	.download_on_app_store_popup_container {
    background: #FF0090 !important;
    color: #FFF !important;
  }

  .homescreen .homescreen_box .stat {
    color: #FFF !important;
  }
    
  
  /* user name */
  .post_region_box .post_region_content .clarifying_discussion .discussion_content .discussion_poster {
    color: #FF0090 !important;
  }
  
  
  /* Header */
  .dropdown {
    opacity: 0.4 !important;
  }
  
  .dropdown:hover {
    opacity: 1.0 !important;
  }
  
  .top_bar .career_notification, .top_bar .top_bar_notification {
    opacity: 0.4 !important;
  }

  #top_bar .top_bar_dropdown_wrapper .top_bar_dropdown_menu#my_class_menu li {
    color: #cc00ff !important;
  }
  
  
    /* Loading */
  .blockUI {
    background-color: #FFFFFF !important;
    opacity: 0.7 !important;
  }

  .top_bar .navbar-inner{
    background: #FF0090 !important;
  }
  
  .top_bar .nav.dark_nav{
	background: #FF0090 !important;
  }

  .top_bar .nav>li>a{
    color: #FFFFFF !important;
    text-shadow: #000000 1px 1px 0px !important;
                    }

  /* Sidebar */
  
  .page_feed .feed_item .content .title{
    color: #FFFFFF !important;
  }

  .page_feed .feed_item {
    background-color: #300030;
    border-bottom: 1px solid #171F29 !important;
  }
  
  .page_feed .feed_item.unanswered{
    background-color: #700090;
    box-shadow: none !important;
    text-shadow: none !important;
    border-bottom: 1px solid #2A2A2A !important;
  }
  
  .page_feed .feed_item.selected {
    background-color: #BB0090;
  }
  
  .page_feed .page_feed_bucket_header {
    background: #222 !important;
    box-shadow: none !important;
    text-shadow: none !important;
    color: #777 !important;
    border-bottom: 1px solid #2A2A2A !important;
  }
  
  .page_feed .page_feed_search_wrapper {
    background-color: #171717 !important;
  }
  
  .page_feed .page_feed_search_wrapper .search_bar {
    box-shadow: none !important;
    background: #333 !important;
  }
  
  .btn-primary{
    background: -webkit-linear-gradient(top, #FF0090 0%, #CC0020 100%);
  }

  .page_feed .feed_item:hover .feed_item_dropdown_selector .feed_item_dropdown_arrow, .page_feed .feed_item:hover .feed_item_dropdown_selector .feed_item_dropdown_arrow:hover{
	background: -webkit-linear-gradient(top, #FF0090 0%, #CC0020 100%);
  }
               
  .page_feed .feed_item.has_unresolved_followups .content .messages .unresolved_followups{
    color: #F88 !important;
  }

  .page_feed .feed_item.instructor_endorsed_post .content .messages .instructor_endorsed{
    color: #00FF00 !important;
  }

  .page_feed .feed_item.unanswered.selected{
	background: #3a0084 !important;
  }

  .page_feed .feed_item .content .snippet {
    color: #ababab;
  }

  /* Footer */
  
  .dashboard_toolbar {
    background: #222;
    text-shadow: none !important;
  }
  
  #special_mentions .stats_item .content {
    box-shadow: none !important;
    background: #282828 !important;
    color: #666 !important;
    text-shadow: none !important;
  }
  
  #special_mentions h3 {
    text-shadow: none !important;
  }

  /* resourses */
  .table th, .table td {
    color: #FFF !important;
  }
}
