css:
@-moz-document domain("binance.com") {
input,
.ipt.fillemail,
.ipt.pwd,
.nc_scale,
.verify .ipt
{
 	background-color:#0a0a0a!important;
    color:#CCC!important;
    border-color:#111!important;
}

.kline-para .productSymbol {
 	color:#CCC!important;   
}

/* Black style */
body,
body > div,
.wrap,
#pageLoading,
.noData,
.table,
.table td,
.main,
.hotMarkets li,
.markets-table,
.markets-table .con,
.markets-table .con .table th, 
.markets-table .con .table td,
.markets-table .type li,
.market-box .market-con ul,
.market-box .market-con ul li,
.orderforms,
.orderforms .orderform-main > .f-fl, 
.orderforms .orderform-main > .f-fr,
#kline-con,
.kline-timeline,
#chart_container,
.item-con,
.overvies,
.panel,
.partner,
.detail-lb,
.orderform,
.header,
#klinecon,
.table.table-striped .btn,
.depthMerge > span,
.units,
.verifyLayout,
.inviteTable,
.inviteRule,
.crowdfundinglists .ico_show
{
 	background:#000!important;
    color:#CCC!important;
    border-color:#111!important;
}

/* Grey Style */
.table th,
.accountInfo-lists li.th,
.depths,
.depths .depthstitle,
.tab-box li,
.searchFilter,
.item-title,
.kline-timeline span,
.orderforms .orderform-type span,
.depths .newest,
.market-box .market-t,
.panel .panel-title,
.detail-rb .orderform-type,
.depthbg,
.detail-rb .orderform-type span.cur,
.orderforms-inputs .iptwrap .jiantou,
.orderforms-inputs .iptwrap .jiantou i:first-child,
.orderforms-inputs .field.percent .col,
.main-nav > li .subNav a,
.logined ul a,
.depths .depthstitle .select select,
.kline-timeline ul li,
.accountInfo-lists li .items > div .btn,
.SecurityCenter .item .btn,
.verifyBottom,
.inviteInfo .paiming,
.login,
.crowdfundinglists .ico_title,
.crowdfundinglists .ico_show ul li a
{
 	background:#0a0a0a!important;
    color:#CCC!important;
    border-color:#111!important;
}


/* Text color white only */
.hotMarkets li .volume,
.filed .lable,
.orderforms-inputs label,
.orderforms-hd span.f-fr,
.orderforms-hd label,
.main-con .fee,
.basicInfo .right .fee,
.crowdfundinglists .ico_title_content h3,
.crowdfundinglists .ico_title p,
.crowdfundinglists .ico_show .ico_show_content .ico_show_info,
.crowdfundinglists .ico_show .ico_show_item p
{
 	color:#CCC!important;
}

/* Border change only */
.accountInfo-lists,
.accountInfo-lists li
{
 	border-color:#111!important;   
}

/* Mini chart on homepage top boxes */
.hotMarkets .svg-container svg
{
 	color:#666!important;
    opacity:0.5!important;
}

/* Books bar graphs */
.askTable .zhuzhuang,
.bidTable .zhuzhuang,
.tradefive-inner.ask tr .zhuzhuang,
.tradefive-inner.bid tr .zhuzhuang
{
 	background-color:#222!important;
}

/* basic charts*/
#kline-con .chart canvas:nth-of-type(1),
#kline-con #depth canvas:nth-of-type(1)
{
    background-color:#FFF!important;
    color:#FFF!important;
  	filter: invert(1);
}

#kline-con .chart canvas:nth-of-type(3),
#kline-con .chart canvas:nth-of-type(4)
{
	filter: hue-rotate(25deg);
}

#kline-con .chart canvas:nth-of-type(6)
{
 	filter: invert(1);   
}

#kline-con #depth canvas:nth-of-type(2),
#kline-con #depth canvas:nth-of-type(3) {
 	filter: invert(1) hue-rotate(180deg);   
}

/* advanced charts */
#klinecon .chart canvas:nth-of-type(1) {
    background-color:#000!important;
    color:#FFF!important;
  	filter: grayscale(1) contrast(150%);
}

#klinecon .chart canvas:nth-of-type(3),
#klinecon .chart canvas:nth-of-type(4){
	filter:hue-rotate(25deg);    
}


/* Invert element */
.basicInfo .left .usertx,
.depths img
{
 	filter: invert(1);   
}

.depthctrl .buy,
.depthctrl .buy.cur,
.depthctrl .sell,
.depthctrl .sell.cur,
.depthctrl .sellbuy,
.depthctrl .sellbuy.cur
{
	filter: contrast(150%);
}


/* Highlighted tabs */
.orderforms .orderform-type span.cur,
.detail-rb .orderform-type span.cur,
.tab-box li.active,
.main-nav > li .subNav a:hover,
.logined ul a:hover,
.table.table-striped .btn:hover
{
 	color:#f3ba2e!important;   
}


.kline-timeline .cur 
{
   color:black!important;
}


/* Red highlights */
.depths .table td.magenta:hover span,
.magenta,
.magenta .gray,
.ng-binding.magenta,
.f-left.magenta
{
 	color:red!important;   
}

.orderforms-btn .btn.btn-sell
{
 	border-color:red!important;   
}

/* Green highlights */
.green,
.green .gray,
.ng-binding.green,
.f-left.green
{
 	color:green!important;   
}

.orderforms-btn .btn.btn-buy {
	border-color:green!important;
}

/* Orange Highlights */
.btn-orange,
.SecurityCenter .item .btn.btn-orange
{
	background: #e8b342 !important;
	border-color: #e8b342 !important;
	color: #000 !important;
}
}
