css:
@-moz-document domain("na.op.gg") {
html {
    background: #262626;
}

.l-champion-index-content .l-champion-index-content--main {
    float: left;
    width: 700px;
    background-color: #262626;
    border: solid 0px #000000;
    border-right: none;
    box-sizing: border-box;
}
.champion-list-filter {
    background-color: #262626;
    border-bottom: solid 0px #000000;
}
.champion-index-trend-header {
    background: #262626;
    border-bottom: solid 0px #000000;
}.champion-index-trend-champion__item {
    border-top: solid 1px #000000;
}.champion-index-trend-champion__list {
    background-color: #262626;
    border: solid 0px #000000;
}.champion-index-trend-position__item {
    display: table-cell;
    vertical-align: middle;
    background-color: #262626;
    border: solid 0px #000000;
}.champion-index-trend-position__item.active {
    background: #333333;
}.champion-index-trend-position__item a {
    display: block;
    padding: 11px 0 10px;
    text-decoration: none;
    text-align: center;
    line-height: 15px;
    font-size: 12px;
    font-weight: bold;
    letter-spacing: -0.9px;
    color: #ffffff;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}.champion-index-trend-position__item.active a {
    color: #ffffff;
}.champion-index-trend-content {
    padding: 20px 20px 40px;
    background-color: #262626;
}.l-champion-index-content .l-champion-index-content--side {
    float: right;
    width: 380px;
    background-color: #f9f9fa;
    border: solid 0px #e9eff4;
    box-sizing: border-box;
}.champion-index-trend-header h2 {
    float: left;
    line-height: 17px;
    font-size: 14px;
    font-weight: bold;
    color: #ffffff;
    padding: 17px 0 16px 21px;
}.champion-trend-champion__name {
    color: #ffffff;
}.champion-index-trend-label {
    display: table-cell;
    line-height: 15px;
    font-size: 12px;
    text-align: center;
    color: #ffffff;
    vertical-align: middle;
}.champion-index-trend-header__filter li.active a {
    font-weight: bold;
    color: #ffffff;
    border-bottom-color: #ffffff;
}.champion-index-trend-header__filter a {
    min-width: 40px;
    display: inline-block;
    vertical-align: middle;
    line-height: 17px;
    font-size: 14px;
    text-align: left;
    color: #8c8c8c; 
    font-weight: lighter;
    padding: 16px 5px 14px;
    border-bottom: 2px solid transparent;
    text-decoration: none;
    box-sizing: border-box;
}.champion-list-filter__keyword input {
    height: 36px;
    width: 193px;
    
    background: #4d4d4d;
    line-height: 15px;
    font-size: 12px;
    color: #ffffff;
    padding: 9px 0 10px 10px;
    box-sizing: border-box;
}

.l-header {
    background: #4d4d4d;
}.l-menu {
    background: #333333;
}.menu__item>a {
    color: #ffffff;
    display: block;
    padding: 11px 8px 7px;
    border-bottom: 3px solid transparent;
}.champion-list-filter__type__item--active a {
    font-weight: bold;
    color: #ffffff;
    border-bottom-color: #262626;
}.gnb-list-item__search {
    position: relative;
    height: 32px;
    background: #333333;
    border-radius: 2px;
    width: 260px;
}
.summoner-search-history__title li {
    width: 50%;
    display: table-cell;
    background: #4d4d4d;
    color: #ffffff;
}.champion-trend-champion__value--winratio {
    font-weight: bold;
    color: #ffffff;
}

.SummonerLayout>.ContentWrap {
    background: #262626;
    overflow: hidden;
}
.SummonerLayout>.Menu {
    border-bottom: 0px solid #c6cbcb;
    margin-top: 25px;
}
.SummonerLayout>.Menu>.MenuList>.Item>a {
    display: block;
    min-width: 70px;
    height: 39px;
    border: 0px solid #c6cbcb;
    padding-left: 6px;
    padding-right: 6px;
    background-color: #262626;
    color: #ffffff;
    font-size: 13px;
    line-height: 40px;
    text-decoration: none;
    text-align: center;
}.SummonerLayout>.Menu>.MenuList>.Item.active>a {
    border-bottom: 0px solid #e8eaea;
    background: #4d4d4d;
    color: #ffffff;
}
.Box {
    border: 0px solid #262626;
    box-shadow: 0 0px #262626;
    background: #262626;
    border-radius: 0px;
}
.GameAverageStats tr>.Title {
    padding: 16px 0 14px 20px;
    line-height: 14px;
    font-size: 12px;
    color: #ffffff;
    height: 14px;
    border-left: 0px solid #cdd2d2;
}.GameAverageStats {
    display: table;
    width: 100%;
    table-layout: auto;
    background-color: #262626;
}.GameAverageStats tr>.Summary .WinRatioGraph .Text {
    position: absolute;
    left: 0;
    top: 50%;
    text-align: center;
    width: 100%;
    margin-top: -8px;
    line-height: 16px;
    font-size: 14px;
    color: #fff;
}.GameAverageStats tr>.KDA>.KDARatio>.KDARatio {
    color: #fff;
}.GameAverageStats tr>.MostChampion ul li .Name {
    margin-bottom: 2px;
    line-height: 16px;
    font-size: 14px;
    color: #fff;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}.GameAverageStats tr>.MostChampion ul li .WonLose {
    display: inline-block;
    font-size: 11px;
    line-height: 12px;
    color: #fff;
}.GameAverageStats tr>.MostChampion ul li .KDA {
    display: inline-block;
    line-height: 12px;
    font-size: 11px;
    color: #fff;
    font-weight: bold;
}.GameAverageStats tr>.PositionStats>ul>li .PositionStatContent .Name {
    line-height: 16px;
    font-size: 14px;
    color: #fff;
    margin-bottom: 4px;
}.SummonerRatingLine {
    display: table;
    width: 100%;
    border-top: 1px solid #4d4d4d;
    color: #879292;
    table-layout: fixed;
}.SummonerRatingLine>.TypeTeam>.StatSummaryType {
    color: #fff;
    font-weight: bold;
}.SummonerRatingMedium>.TierRankInfo>.TierRank {
    color: #fff;
    font-size: 13px;
}.SummonerRatingLine>.TierRank>.TierRank {
    color: #fff;
    font-size: 12px;
}.GameAverageStats tr>.PositionStats>ul>li .PositionStatContent .WinRatio {
    line-height: 12px;
    font-size: 11px;
    color: #8c8c8c;
}.GameAverageStats tr>.PositionStats>ul>li .PositionStatContent .WinRatio span {
    color: #808080;
}.SummonerLayout>.Menu>.MenuList>.Item.inGame>a {
    padding-left: 20px;
    padding-right: 20px;
    background-color: #262626;
    color: #fff;
}.Button.White {
    border: 0px solid #ccd1d1;
    background: #262626;
    color: #fff;
}.Button.Blue {
    border: 0px solid #1a78ae;
    background: #4d4d4d;
    color: #ffffff;
}.Box>.Navigation .List>.Item.active>.Link {
    color: #fff;
    font-weight: bold;
}.Box>.Navigation .List>.Item.active {
    border-bottom: 0px solid #1f8ecd;
}.Box>.Navigation .List>.Item>.Link {
    display: inline-block;
    color: #999999;
    font-size: 12px;
    line-height: 36px;
    text-decoration: none;
}.GameListContainer>.Header>.Navigation>.Actions>.Action>.ChampionMatchSearchWrap>.Header>.Input {
    display: block;
    width: 100%;
    color: #fff;
    font-size: 13px;
    border: none;
    outline: none;
    background-color: #4d4d4d;
}.GameListContainer>.Header>.Navigation>.Actions>.Action>.ChampionMatchSearchWrap>.Header {
    position: relative;
    width: 114px;
    height: 17px;
    padding: 8px 30px 9px 9px;
    background-color: #4d4d4d;
    border-bottom: 0px solid #cdd2d2;
}.GameListContainer>.Header>.Navigation>.Actions>.Action>.ChampionMatchSearchWrap {
    position: relative;
    height: 34px;
    border-left: 0px solid #cdd2d2;
}.Box>.Tab>.TabList>.Item.active>.Link {
    color: #262626;
    font-weight: bold;
}.Box>.Tab>.TabList>.Item>.Link {
    width: 100%;
    height: 38px;
    display: block;
    color: #ffffff;
    font-size: 12px;
    text-decoration: none;
    line-height: 38px;
    text-align: center;
}.Box>.Tab>.TabList>.Item {
    display: table-cell;
    border-left: 0px solid #cdd2d2;
    border-bottom: 0px solid #cdd2d2;
    background-color: #262626;
}.ChampionBox {
    display: table;
    width: 100%;
    border-bottom: 0px solid #cdd2d2;
    color: #ffffff;
    text-align: center;
}.Box>.Title+.Content, .Box>.Navigation+.Content, .Box>.Tab+.Content {
    background: #262626;
}.ChampionBox>.ChampionInfo>.ChampionName {
    width: 70px;
    color: #fff;
    font-weight: bold;
    font-size: 13px;
    text-overflow: ellipsis;
    overflow: hidden;
    white-space: nowrap;
}.ChampionWinRatioBox, .PositionWinRatioBox {
    display: table;
    width: 100%;
    border-bottom: 0px solid #cdd2d2;
    background: #262626;
    color: #262626;
    text-align: center;
    font-size: 12px;
    table-layout: fixed;
}.Box>.Title {
    position: relative;
    display: block;
    padding: 10px 15px;
    border-bottom: 0px solid #cdd2d2;
    color: #fff;
    font-size: 12px;
    font-weight: bold;
    text-decoration: none;
}.ChampionWinRatioBox>.ChampionInfo>.ChampionName {
    width: 100%;
    margin-left: 10px;
    color: #fff;
    font-weight: bold;
    text-overflow: ellipsis;
    white-space: nowrap;
    overflow: hidden;
    text-decoration: none;
}.SummonersMostGameTable>.Header>.Row>.HeaderCell {
    padding-top: 11px;
    padding-bottom: 10px;
    background-color: #262626;
    color: #fff;
    font-weight: normal;
    font-size: 12px;
    
}.SummonersMostGameTable>.Body>.Row.Odd>.Cell {
    background-color: #262626;
}.SummonersMostGameTable>.Body>.Row>.Cell.SummonerName>.Link {
    color: #fff;
    font-size: 12px;
    font-weight: bold;
    text-decoration: none;
}.GameItem>.Content {
    display: table;
    width: 689px;
    border-collapse: collapse;
    border: 0px solid #cdd2d2;
}
.l-wrap--index {
    background: #fff;
    background: #262626;
}
.header {
    position: relative;
    max-width: 1300px;
    margin: 0 auto;
    padding-top: 12px;
    padding-bottom: 12px;
    
}
