css:
@-moz-document domain("napchart.com") {
* {
	font-family: Roboto, sans-serif !important;
}

body {
	background: #222;
}

.sidebar, .polyBar, .canvas {
	-webkit-box-shadow: 0 3px 6px rgba(0, 0, 0, 0.16), 0 3px 6px rgba(0, 0, 0, 0.23);
			box-shadow: 0 3px 6px rgba(0, 0, 0, 0.16), 0 3px 6px rgba(0, 0, 0, 0.23);
}

.header {
	background: #222;
	
	z-index: 10;
}

.HeaderElement {
	color: #ddd;
}

.HeaderElement:first-child {
	text-transform: uppercase;
	font-size: 1.1em;
}

.headerBtn .mdi-icon {
	fill: #b5b5b5;
}

.headerBtn.disabled .mdi-icon:hover {
	fill: #585858;
}

.headerBtn.disabled .mdi-icon {
	fill: #585858;
}

.sidebar {
	display: -webkit-box;
	display: -ms-flexbox;
	display: flex;
	   -webkit-box-orient: vertical;
	-webkit-box-direction: normal;
	   -ms-flex-direction: column;
		   flex-direction: column;
	
	background: #333;
	
	z-index: 5;
}

.sidebar .metaInfo > * {
	font-size: 14px;
}

.sidebar input[type="text"],
.sidebar textarea {
	border-bottom: 2px solid #aaa;
	background: transparent;
	
	color: #ddd;
}

.sidebar input[type="text"]:focus,
.sidebar textarea:focus {
	border-bottom: #6dade4 2px solid;
}

.sidebar input[type="text"]:disabled,
.sidebar textarea:disabled {
	border-bottom-color: #555;
	display: none;
}

.sidebar input[type="text"]::-webkit-input-placeholder,
.sidebar textarea::-webkit-input-placeholder {
	color: #888;
}

.sidebar input[type="text"]:-ms-input-placeholder,
.sidebar textarea:-ms-input-placeholder {
	color: #888;
}

.sidebar input[type="text"]::-ms-input-placeholder,
.sidebar textarea::-ms-input-placeholder {
	color: #888;
}

.sidebar input[type="text"]::placeholder,
.sidebar textarea::placeholder {
	color: #888;
}

.sidebar button {
	padding: 4px 8px;
	
	background: #ddd;
	border: none;
	border-radius: 2px;
	-webkit-box-shadow: 0 3px 6px rgba(0, 0, 0, 0.16), 0 3px 6px rgba(0, 0, 0, 0.23);
			box-shadow: 0 3px 6px rgba(0, 0, 0, 0.16), 0 3px 6px rgba(0, 0, 0, 0.23);
}

.sidebar button.active {
	background: #de9090;
}

.sidebar .Element {
	margin-left: -10px;
	margin-top: 20px;
	padding: 0 10px;
	
	position: relative;
	
	background: #e8e8e8;
	-webkit-box-shadow: inset 0px 11px 8px -10px #888, inset 0px -11px 8px -10px #888;
			box-shadow: inset 0px 11px 8px -10px #888, inset 0px -11px 8px -10px #888;
}

.sidebar .Element > *:first-child {
	margin-top: 10px;
}
.sidebar .Element > *:last-child {
	margin-bottom: 10px;
}

.sidebar input.selectedElement {
	width: 100%;
	
	margin-bottom: 12px;
	padding-left: 3px;
	padding-bottom: 5px;
	padding-right: 35px;
	
	border: none;
	border-bottom: #888 2px solid;
	resize: none;
	outline: none;
	-webkit-box-sizing: border-box;
			box-sizing: border-box;
	
	color: #444;
}

input.selectedElement:disabled,
input.selectedElement:disabled ~ button {
	display: none;
}

input.selectedElement ~ button {
	position: absolute;
	top: 4px;
	right: 8px;
	
	padding: 4px 6px 2px;
	
	background: none;
	-webkit-box-shadow: none;
			box-shadow: none;
	border-radius: 0;
}

input.selectedElement ~ button svg {
	width: 20px;
	height: 20px;
	
	fill: #888;
}

input.selectedElement ~ button:hover svg {
	fill: #666;
}

.TypeElement {
	color: #fff;
}

.TypeElement .options {
	height: 24px;
}

.TypeElement .options > input {
	padding: 4px 2px;
	
	border: none;
	border-bottom: 2px solid #888;
	outline: none;
}

.TypeElement .iconlink:hover svg {
	fill: #4499f9;
}

.sidebar > div:last-child {
	-webkit-box-flex: 1;
			-ms-flex: 1;
				flex: 1;
	display: -webkit-box;
	display: -ms-flexbox;
	display: flex;
	   -webkit-box-orient: vertical;
	-webkit-box-direction: normal;
	   -ms-flex-direction: column;
		   flex-direction: column;
	-webkit-box-pack: end;
	   -ms-flex-pack: end;
	 justify-content: flex-end;
	
	color: #888;
}

.sidebar > div:last-child a {
	color: #b0b0b0;
}

.polytoggle {
	font-size: 0.9em !important;
}

.polyBar {
	z-index: 4;
}

.canvas {
	background: #e8e8e8;
	
	z-index: 1;
}
}
