css:
/* Dark Fraz 1.0 mod by Brudka */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.frazpc.pl") {
    
body{
  background:#1E1E1E!important;
  transform: scale(1.17);
  transform-origin: 0 0;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  text-shadow: rgba(0,0,0,.01) 0 0 1px;
  width: 800px;
}
          
ul#linki_top li {
   display: none;
 }  

header {
   display: none;
 }

#infiniteCarousel {
   display: none;
 }     
    
.board-links {
   display: none;
 }    
   
div.welcome-a {
    width: 800px;
 }
   
.search {
    padding-right: 40px;
 }

.search input {
   background-color: #6260604d;
   color: #000000;
 }  
 
#content{
    background: none !important;
 }
    
#content-wrapper-b, #content-wrapper-a, #content-wrapper{
    background:none !important;}
    
#content section{
    margin: auto !important;
    float: none !important;
    padding: 5px !important;
 }

#content > section {
    background: none; 
 }
    
#content-wrapper.board-wrapper #empty, #content-wrapper #empty, #content-wrapper.board-wrapper #empty-bottom {
    background: #343434;
 }
    
#formAddTopic input, #formAddTopic textarea, #formAddTopic select, #formAddComment input, #formAddComment textarea, #formAddComment select, #formAddPost input, #formAddPost textarea, #formAddPost select {
    background-color: #BBBBBB;
}

       
#formAddTopic, #formMessage, #formAddPost, #formComment, #formAddComment, #formEditPost, #formSearch, #formReset, #formFavoriteComment, #formPhotoDescription, #formDescription, #searchForm {
    background-color: #535353;
    border: 1px solid #535353; 
 }
   
div.form_adds {
    border: 1px solid #535353;
    background-color: #535353;
 }

#formAddTopic label {
    font-size: 11px;
    color: #999999;
    line-height: 30px;
 }
    
#formAddPost label {
    font-size: 11px;
    color: #999999;
 }
    
dd#submit-element input {
    background-color: #343434 !important;
 }

div.board_activities {
    padding: 10px;
     background-color: #575757;
 }
 
.board_view td.caption {
    background-color: #262626;
    color: #719595;
    font-size: 13px;
    text-align: left;
    padding-left: 10px;
 }

.board_view .col-c .my_topic {
    font-weight: bold;
    color: #DB5F04;
 }   
    
div.comment p.sign {
    width: 661px;
 }

div.comment blockquote, div.comment p.sign {
    background-color: #2B2B2B;
 }

div.comment-inside div.secondary div.secondary-two {
    width: 624px;
 }
  
div.topic p.topic-info-a a, div.users-stats, .back-to-main a {
    color: #DE5904;
 }
   
.board_view tr.readed td, .board_view tr.readed td a, .board_view tr.readed td.col-c a {
    color: #909090;
 }
   
div.comment.old_post div.comment-inside div.secondary div.secondary-two {
    background-color: #373737;
 }

.board_view tr:hover, .board_view tr.readed td {
    background-color: #363636;
 }
   
div.comment.good-answer div.comment-inside div.secondary div.secondary-one {
    background-color: #9fbc88;
 }
   
}
