css:
@-moz-document domain("tokywoky.com") {
body {
    background-color: darkslategrey;
    color : lightgrey;
}

a{
color : #a2e6ff 
}
a:visited {
    color: #bcedff;
}

/*Sidebar*/
.sidebarContain {
    background-color: #55C5EE;
    color: white;
}
.sidebarProfileRepPts {
    color: white;
}
.sideBarInactiveContent {
    color: white;
}
.friendName{
    color: white;
}
.sidebarProfileInfo 
.userName {
        color: white
}
.profileTutoButton {
    color: darkgray;
    border-color: darkgray;
}
.profileTutoButton:hover {
    color: gray;
    border-color: darkgray;
}
.sidebarProfileIconRep {
    color: white;
}
.sideBarCanBePremiumDisabled {
    background-color: grey;
	color : lightgrey;
}
.categoryTagMini {
    background-color: darkgray;
}
/*NavBar*/
.navbar {
    background-color: darkslategrey;
}
.navItem {
    color: lightblue;
}
.container {
    background-color: darkslategrey;
}

/*Profile*/
.profileMainContain{
    background-color: dimgrey;
}
.profileMainContain .profileInfoContain .profileName{
	color : white;
}
th{
	color : white;
}
td{
	color : white;
}
.profileMainContain .profileInfoContain .profileColumnInfo table td .categoryContain .categoryName{
	color : white;
}
.profileMainContain .profileInfoContain .profileColumnInfo table td .categoryContain .brandCount {
	color : white;
}
.profileMainContain .profileInfoContain .profileColumnInfo table td .groups .groupName span{
	color : white;
}
.profileBio{
    background-color: darkgrey;
}
pre{
	color : white;
}
.profileBoxContent{
    background-color: dimgrey;
}
.profileBoxReputation .profileBoxContent .profileReputationTitle strong{
	color : white;
}
.profileBoxReputation .profileBoxContent .profileReputationTitle{
	color : white;
}
.profileBoxReviews .profileBoxReviewContent .profileReviewCategoriesContain{
    background-color: dimgrey;
}
.profileBoxReviews .profileBoxReviewContent .profileReviewCategoriesContain .profileReviewCategoryRow{
	background-color: darkgrey;
	color : white;
}
.profileBoxReviews .profileBoxReviewContent .profileReviewsContain{
    background-color: dimgrey;
}
.profileReview{
    background-color: lightgrey;
}
.profileReviewDateCategoryLabel{
	color : white;
}

.profileBoxAnswers{
    background-color: dimgrey;
}
.profileFeedDatePost{
	color : lightgrey;
}
.profileFeedTextPost{
	color : white;
}
.profileFeedText{
	color : lightgrey;
}


/*flow*/
.mainBarTitle {
    background-color: lightgrey;
}
.newAnswerWarning {
    background-color: grey;
    color : skyblue;
}
.postContainIsIncoming {
    background-color: #ffc000;
    -moz-animation: backgroundHiglight 2s forwards;
    -moz-animation-delay: 1s;
    -webkit-animation: backgroundHiglight 2s forwards;
    -webkit-animation-delay: 1s;
    -o-animation: backgroundHiglight 2s forwards;
    animation: backgroundHiglight 2s forwards;
    -o-animation-delay: 1s;
    animation-delay: 1s
}
@keyframes backgroundHiglight {
    to {
        background-color: grey
    }
}
@-moz-keyframes backgroundHiglight {
    to {
        background-color: grey
    }
}
@-webkit-keyframes backgroundHiglight {
    to {
        background-color: grey
    }
}
@-o-keyframes backgroundHiglight {
    to {
        background-color: grey;
    }
}
.postContain {
    background-color: grey;
    color: white;
}

.displayIncomingPosts{
    background-color: grey;
}

.c2c{
    background-color: darkgrey;
	color : white;
}
.coachFeedTab.feedTabActive {
    background-color: dimgrey;
	color : white;
}
.coachFeedTab.feedTabActive:visited {
    color: white!important;
    opacity: 0.5;
}
.indexAnswerInput {
    background-color: #e6e6e6;
}
.answerContent {
    background-color: darkgrey;
    color : white ;
}
.feedText {
    color : white;
}
.popup {
    background-color: lightgrey;
    color : white ;
}
.policyContainer {
    background-color: grey;
    color : white ;
}

/*Tchatbox*/
.chatContent {
    background-color: lightgrey;
}
.shellChatBubbleOther{
    background-color: darkgrey;
}
.shellChatBubbleOwn{
    background-color: dimgrey;
}

.isTypingChats{
    background-color: lightgrey;
}

.chatInput{
    background-color: darkgrey;
	color : white;
}
	
/*Feed Tab*/
.statusSendContain {
    background-color: darkgrey;
}
.statusPostLegend {
    color : white;
}
.statusPostImageBtn {
    background-color: darkgrey;
}

.statusNoPostContain {
    background-color: darkgrey;
}
.statusNoPostContain .strong{
color : white;
}
.statusNoPostContain .light{
color : white;
}

.statusContain.newStatus {
    background-color: #ffc000;
}
.statusContain {
    background-color: darkgray;
}
.statusTextContain .statusText {
	color : white;
}
.statusToolbarContain .indexDate{
	color : white;
}
.statusCommentImageBtn{
    background-color: darkgray;
}
.statusCommentsContain{
    background-color: darkgray;
}
.statusCommentsTextContain .statusCommentsText{
	color : white;
}

.statusBottomCommentBox{
    background-color: darkgray;
}


/*Inbox*/
.inboxToolbarContain {
    background-color: darkgray;
}

.inboxText {
    color: white;
}
.chatUserId{
	color : white
}
.userDetailsMini {
    background-color: white;
	opacity : 0.9 ;
}

.inboxChatBubbleOwn a {
    color: darkslategrey;
}
.inboxChatBubbleOwn a:hover {
    color: deepskyblue;
}
.inboxChatBubbleOwn a:visited {
    color: skyblue;
}

.inboxChatBubbleOwn{
	background-color: dimgray;
}
.inboxChatBubbleOther
{
	background-color: darkgray;
}

.userQuestion {
    color: white;
}
.inboxMessageContain.inboxMessageActive {
    background-color: #00b0f0;
}
.inboxMessageContain.inboxMessageRead {
    background-color: dimgray;
}
.inboxMessageContain.inboxMessageRead:hover {
    background-color: #939393;
}
.inboxMessageContain.inboxMessageRead .chatUserId {
    color: #99dff9;
}
.inboxMessageContain.inboxMessageActive .userQuestion {
    color: white;
}
.lastMessage {
     color: skyblue;
}

.inboxChatInput{
	background-color: white;
	color : darkslategrey;
}

.inboxChatInputBar {
	background-color: darkslategrey;
}
.inboxChatInputLegend {
	background-color: darkslategrey;
}

/*Notifications*/
/*DropDown menu Popup*/
.navNotifsFriendsPopupTitle {
    background-color: darkslategrey;
}
.navNotifsPopupContain {
	background-color: lightgrey;
}

.navNotifsFriendsContain {
    background-color: darkgrey;
}
.navNotifsFriendsContain.notificationRead {
    background-color: lightgrey;
}
.notificationsDateContain {
    color : white;
}
/*Notification dedicated page*/
.notificationsFriendsTitle {
    background-color: #2ab6ea;
	Color : white;
}
.notificationsFriendsContain {
    background-color: dimgray;
	color : white;
}
/*Salons*/
.badgePageTitle {
    color : lightgray
}
.badgePageLegend{
    color : lightgray
}
.salonContain {
    background-color: dimgray;
}
.salonLabel{
	color : #2ab6ea;
}
}
