css:
@-moz-document url-prefix("https://ohnotheydidnt.livejournal.com"), domain("ohnotheydidnt.livejournal.com") {
BODY {
    background: #313131;
    font-size: 11.5px;
    line-height: 1.4;
    text-align: justify;
    font-family: Helvetica, Arial, sans-serif;
}
.j-l-content:before {
    display: block;
    position: absolute;
    top: 0;
    bottom: 0;
    right: 0;
    width: 330px;
    background: #e7e5e5;
    border-radius: 0 0 5px;
}
.j-subnav {
    position: relative;
    height: 35px;
    padding: 0 10px;
    clear: both;
    background-color: #f3f0f0;
}
.j-title-item-2 {
    font-size: 19px;
    letter-spacing: .04em;
    color: #ffffff;
}
.j-search INPUT {
    vertical-align: middle;
    height: 26px;
}
.j-w-hotlinks-list A {
    display: inline-block;
    color: #5b5b5b;
}
.j-beta-w .j-w-title,
.j-beta-w .j-w-title A {
    color: #5b5b5b;
}
.w-topentries LI {
    margin: 0 0 8px;
    padding: 5px;
    background: #FFF;
    background-image: -webkit-linear-gradient(bottom, #f3f0f0 2.4em, transparent 2.4em);
    background-image: linear-gradient(to top, #f3f0f0 2.4em, transparent 2.4em);
    border-radius: 5px;
}
.w-topentries .b-posts-head A,
.w-topentries .b-posts-head A:hover {
    text-decoration: none;
    color: #5b5b5b;
}
.i-ljuser-userhead {
    -webkit-filter: grayscale(100%);
    filter: grayscale(100%);
}
.j-social-networks-nav-item A {
    -webkit-filter: grayscale(100%);
    filter: grayscale(100%);
}
.j-e-tags-item A {
    padding: 2px 4px;
    background-color: #ae99c0;
    text-decoration: none;
    font-weight: 700;
    color: #fff;
}
.j-search-submit {
    padding: 3px 11px;
    vertical-align: middle;
    background: #5b5b5b;
    border: 0;
    border-radius: 3px;
    cursor: pointer;
    letter-spacing: .1em;
    font-size: 12px;
    color: #FFF;
}
.j-e-text A {
    font-weight: 700;
    text-decoration: none;
    color: #5b5b5b;
}
.j-e-tags-item A {
    padding: 2px 4px;
    background-color: #555;
    text-decoration: none;
    font-weight: 700;
    color: #fff;
}
A {
    color: #5b5b5b;
}
.j-c-head {
    margin: 0 0 10px;
    width: 600px;
    padding: 10px 10px 10px 10px;
    background: #f3f0f0;
    color: #5b5b5b;
}
.j-c-head A,
.j-c-text A {
    text-decoration: none;
    color: #5b5b5b;
}
.j-l-comments {
    overflow: auto;
    padding-bottom: 50px;
    padding-left: 10px;
    background: #FFF;
    border-radius: 0 0 5px 5px;
}
.j-e-nav-item-facebook.j-e-nav-item.j-e-nav-item-button {
    -webkit-filter: grayscale(100%);
    filter: grayscale(100%);
}
.j-e-nav-item-twitter.j-e-nav-item.j-e-nav-item-button > .js-lj-share-entry {
    -webkit-filter: grayscale(100%);
    filter: grayscale(100%);
}
.j-e-nav-item-email.j-e-nav-item.j-e-nav-item-button > .js-lj-share-entry {
    -webkit-filter: grayscale(100%);
    filter: grayscale(100%);
}
.j-e-nav-item-button A {
    -webkit-filter: grayscale(100%);
    filter: grayscale(100%);
}
.j-c-nav-item A {
    color: #5b5b5b;
}
.j-e-text {
    min-height: 130px;
    text-align: justify;
    margin: 10px 0;
    padding: 60px;
    line-height: 170%;
    word-break: break-word;
    font-size: 1.1em;
    color: #292929;
}
.j-e-user:before {
    display: none;
}
.j-user-pic IMG {
    position: relative;
    border-radius: 55px;
    height: 100px;
    margin-bottom: 0px;
    padding: 4px;
    background: rgba(255, 255, 255, 1.0);
    border: 1px solid #b7b7b7;
}
.j-e-user {
    position: relative;
    float: right;
    height: 100px;
    margin-bottom: 0px;
    padding: 4px;
    background: rgba(235, 235, 235, 0.0);
    text-align: center;
    border: 0px solid #b7b7b7;
    border-radius: 55px;
}
.j-e-user-name A {
    padding-top: 6px;
}
.j-e-title A:link {
    text-decoration: none;
    color: #5b5b5b;
}
.j-p-tagcloud.j-w-list-tags.j-w-list {
    background: #ffffff;
    text-align: justify;
    padding: 20px;
    font-size: 11.5px;
    line-height: 1.4;
    font-family: Helvetica, Arial, sans-serif;
}
.j-p-tagcloud .j-w-item-size-1 {
    font-size: 12px;
}
.j-p-tagcloud .j-w-item-size-2 {
    font-size: 12px;
}
.j-p-tagcloud .j-w-item-size-3 {
    font-size: 12px;
}
.j-p-tagcloud .j-w-item-size-4 {
    font-size: 12px;
}
.j-p-tagcloud .j-w-item-size-5 {
    font-size: 12px;
}
.w-topentries IMG {
    max-width: 100%;
    height: auto;
    border-radius: 50px;
}
.j-w-tags .j-w-link-alltags {
    color: #5b5b5b;
}
.j-page-nav A {
    font-weight: 700;
    text-decoration: none;
    font-size: 14px;
    color: #5b5b5b;
}
.j-e-tags-item A:hover {
    background-color: #5b5b5b;
}
.j-e-nav A:hover {
    color: #5b5b5b;
}
.j-c-partial A {
    color: #5b5b5b;
}
.j-comments-nav A {
    font-weight: 700;
    text-decoration: none;
    color: #5b5b5b;
    padding-right: 145px;
}
.j-e-tags {
    margin: 15px 0;
    text-align: justify;
    font-size: .8em;
}

.j-comments-pages-container A {
    text-decoration: none;
    color: #5b5b5b;
}

.j-comments-pages-container {
    padding: 10px;
    margin-right: 150px;
    background: #f3f0f0;
    border-radius: 5px;
    font-size: 1.2em;
    line-height: 1.8;
    color: #5b5b5b;
}

.j-e-nav {
    padding: 8px 10px;
    background: #f3f0f0;
    border-radius: 0 0 3px 3px;
    font-size: 12px;
    line-height: 22px;
}
.j-c-text {
    width: 573px;
    padding: 25px;
} 
.j-l-comments-inner {
    position: center;
    padding-left: 120px;
}
}
