@namespace html url(http://www.w3.org/1999/xhtml);
@namespace xul url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
/**/
/**/
/**/
/**/
/**/
/*********************************************/
/*     Изменение основного меню + логотип    */
/*********************************************/


.main-menu-logo .mm-logi-pic-wr {
    opacity: 0.0;
}


.main-menu.initial-background .main-menu-head .cube-menu-tail-start {
    background: #4d4d4d !important;
    box-shadow: 0 0 11px 1px #2a2a2a !important;
    /* фон треугольника возле логотипа */
}
.main-menu.initial-background {
    background: #4d4d4d !important;
    border-bottom: 1px solid #3b3b3b !important
    /* фон основного меню */
}
.main-menu.initial-background .main-menu-head {
    background: #2a2a2a !important;
    /* фон логотипа */
}
/**/
/**/
/**/
/**/
/**/
/* фон меню, когда всплывает просмотрщик докуметов */
.main-menu:not(.main-menu), .secondary-top-menu:not(.main-menu) {
    background: #4d4d4d; 
}
.main-menu .main-menu-head:not(.main-menu-head), .secondary-top-menu .main-menu-head:not(.main-menu-head), .main-menu .secondary-menu-head:not(.main-menu-head), .secondary-top-menu .secondary-menu-head:not(.main-menu-head) {
    background: #2a2a2a;
    border-bottom: 1px solid #2a2a2a;
    
}
.main-menu .main-menu-tail:not(.main-menu-tail)::after, .secondary-top-menu .main-menu-tail:not(.main-menu-tail)::after, .main-menu .secondary-menu-tail:not(.main-menu-tail)::after, .secondary-top-menu .secondary-menu-tail:not(.main-menu-tail)::after {
    border-bottom: 1px solid #2a2a2a;
}
/**/
/**/
/**/
/**/
/**/
.td-general-id {
    color: #262626;
    /*  */
}
.tr-taskstatus-style-2 a {
    text-decoration: none !important;
}
.main-menu a:hover {
    color: black;
}
.main-menu-logo {
    width: 130px;
    margin-left: -4px;
    margin-top: 1px;
    background-image: url("http://etim.by/images/encata/logo5.png");
    background-repeat: no-repeat;
    background-size: 100% 65%;
    background-position: 0 7px, 0 0px, 0 0px;
    /* margin: 8px 0 0 15px; */
    /* height: 50px; */
    /* background-size: 130px 45px; */
    /* background-size: cover; */
    /* background-size: contain; */
    /* background: url(http://etim.by/images/encata/logo5.png) no-repeat; */
    /* http://encata.net/custom/2017/images/logo.svg */
}
.main-menu .main-menu-head,
.secondary-top-menu .main-menu-head,
.main-menu .secondary-menu-head,
.secondary-top-menu .secondary-menu-head {
    width: 160px;
    background: #2a2a2a;
    border-bottom: none;
    /* фон меню логотипа, когда всплывает просмотрщик докуметов */
}
.main-menu .main-menu-tail::after {
    border-bottom: none;
}
.tbl-list .tr-item > td,
.tbl-tool-list .tr-item > td,
.tbl-list-floating-head .tr-item > td,
.tbl-list .tr-common-item > td,
.tbl-tool-list .tr-common-item > td,
.tbl-list-floating-head .tr-common-item > td {
    border-top: 1px solid #d9d9d9;
    /* разделительная граница между пунктами задач в левом меню */
}
.mm-logi-pic-wr {
    display: none;
}
.main-menu-logo .mm-logi-pic-wr svg|* {
    display: none;
}
.main-menu-head::after,
.secondary-top-menu .secondary-menu-head::after {
    background: #4d4d4d!important;
    box-shadow: none!important;
    /* фон меню, когда всплывает просмотрщик докуметов */
}
.main-menu .main-menu-head:not(.main-menu-head) .cube-menu-tail-start, .secondary-top-menu .main-menu-head:not(.main-menu-head) .cube-menu-tail-start, .main-menu .secondary-menu-head:not(.main-menu-head) .cube-menu-tail-start, .secondary-top-menu .secondary-menu-head:not(.main-menu-head) .cube-menu-tail-start {
    background: #4d4d4d;
    box-shadow: 0 0 11px 1px #2a2a2a;
    /* фон треугольника, когда всплывает просмотрщик докуметов */
}




.main-menu-items .main-menu-item.main-menu-active-item {
    background: #2a2a2a;
    /* фон активного пункта меню */
}
/**/
/**/
/**/
/**/
/**/
/*********************************************/
/* Подсветка выбранного пункта левого меню */
/*********************************************/

.planfixcardmenu table .active .link,
.planfixcardmenu table .active .edge div,
.cardmenu table .active .edge div,
.cardmenu table .active .link {
    background: #6b6b6b;
    /* #43495d */
    /* верхняя граница активного пункта меню слева */
    /* border-top: solid 1px #f0fef0; */
    /* нижняя граница активного пункта меню слева */
    /* border-bottom: solid 1px #f0fef0; */
    /* фон активного пункта меню слева */
}
.chronicle-item-container.chronicle-item-container-active,
.chronicle-wrapper .tbl-list .summary-item-active td,
.b-main-block .tr-task-active,
.dl-task-microcard-container.task-card-opened {
    /* активный пункт левого меню (например в непрочитанных событиях) */
    /* background-color: #f0fef0 !important; */
}
.chronicle-item-container.chronicle-item-container-active:hover,
.chronicle-wrapper .tbl-list .summary-item-active td:hover,
.b-main-block .tr-task-active:hover,
.dl-task-microcard-container.task-card-opened:hover {
    /* изменение цвета по наведению курсора на активный пункт левого меню (например в непрочитанных событиях) */
    /* background-color: #000000 !important; */
}
/*********************************************/

.b-green-button {
    color: #fff;
    font-size: 8pt;
    background: #4d4d4d;
    /* цвет кнопки выход */
}
/**/
/**/
/**/
/**/
/**/

.link > .menu-text-count-project .b-inside-num-common {
    color: #dde0e2;
    /* цвет цифр количества непрочитанных задач */
}
.planfixcardmenu tr .link .b-inside-num-new {
    color: #dde0e2;
    /* цвет цифр количества непрочитанных задач */
}
