/**/
/*********************************************/
/*           Изменение левого меню           */
/*********************************************/

.planfixcardmenu .leftmenu-topblock {
    background: #4d4d4d;
    /* #4d4d4d */
    
    border-bottom: 0.1px solid #3b3b3b !important
    /* основной фон и цвет тонкой полосы под верхней частью левого меню */
}
.leftmenu-scroller-wrapper {
    background: #4d4d4d;
}
.page-layout-block-first-hideable > .baron_wrapper .baron_scroller {
    background: #4d4d4d;
}
body .page-layout-hidden {
    background: #4d4d4d;
}
