}
/**/
/**/
/**/
/**/
/**/
/*********************************************/
/*        Изменение диаграммы Ганта          */
/*********************************************/

.tbl-list .td-item,
.tbl-tool-list .td-item,
.tbl-list-floating-head .td-item {
    font-size: 9pt;
    padding-bottom: 2px;
    padding-top: 2px;
    border-top: 1px solid transparent;
    border-bottom: 1px solid transparent;
    line-height: 12pt;
}
.gant-task-item {
    display: inline-block;
    position: relative;
    vertical-align: top;
    font-size: 11px;
    height: 12px;
    padding: 5px;
}
.gant-task-item-after {
    margin-left: 7px;
    margin-top: 3px;
}
