css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lifehacker.com"),
               domain("gizmodo.com"), 
               domain("consumerist.com"),
               domain("valleywag.com"),
               domain("defamer.com"),
               domain("deadspin.com"),
               domain("gawker.com"),
               domain("io9.com"),
               domain("jezebel.com"),
               domain("kotaku.com"),
               domain("jalopnik.com"),
               domain("fleshbot.com") {
    #header_container,
    #sidebartopstories,
    #sidebarspacer,
    .spacer,
    #custom-sidebar,
    #custom-menu > a,
    #interruptor,
    #weekly-subscribe,
    #team > h3+p a>img,
    #team > h3+p i,
    #team > h3 > img[title="Lifehacker Shop"] { 
        display: none !important;
    }
    #sidebar {
        border-top: 1px solid #D6D6D6 !important;
        padding-top: 5px !important;
    }
    #menubar { 
        margin-top: 15px !important;
    }
    #custom-menu {
        background-image: url(data:image/jpg;base64,) !important;
    }
}
