css:
@-moz-document domain("www.loverslab.com") {
#community_app_menu>li>a {
    -moz-border-radius: 3px;
    -moz-box-shadow: inset rgba(0, 0, 0, 0.8) 0px 1px 5px, rgba(255, 255, 255, 0.02) 0px 0px 0px 1px, rgba(255, 255, 255, 0.05) 0px 1px 0px;
    -webkit-border-radius: 3px;
    -webkit-box-shadow: inset rgba(0, 0, 0, 0.8) 0px 1px 5px, rgba(255, 255, 255, 0.02) 0px 0px 0px 1px, rgba(255, 255, 255, 0.05) 0px 1px 0px;
    background: rgba(0, 0, 0, 0.4);
    border-radius: 3px;
    box-shadow: inset rgba(0, 0, 0, 0.8) 0px 1px 5px, rgba(255, 255, 255, 0.02) 0px 0px 0px 1px, rgba(255, 255, 255, 0.05) 0px 1px 0px;
    color: #9a9a9a;
    display: block;
    height: 24px;
    line-height: 24px;
    outline: none;
    padding: 0px 15px;
    position: relative;
}
#header > div {
    background: url(http://www.loverslab.com/resources/img/header_bg.jpg) no-repeat;
    background-size: 100%;
    box-shadow: rgba(255, 255, 255, 0.1) 0px 0px 0px 1px;
    height: 64px;
}
#header > div > div {
    margin-top: 5px;
}
#secondary_links img {
    height: 16px;
    margin: 4px -4px 0 -4px;
    opacity: 0.8;
    position: relative;
    vertical-align: top;
    width: 16px;
}
.ipsLayout_container.ipsLayout_minimal {
    width: calc(100% - 10px);
    max-width: 100%;
}
.ipsLayout_container {
    width: calc(100% - 10px);
    max-width: 100%;
}
.ipsLayout_container.ipsLayout_container {
    width: calc(100% - 10px);
    max-width: 100%;
    margin: 0;
}
#secondary_navigation {
    background: #1d1d1d url('http://odium.loverslab.com/public/style_images/carbon_blue/secondary_nav_bg.png') repeat-x 0 0;
    border-bottom: 1px solid #252525;
    line-height: 24px;
    overflow: hidden;
    text-shadow: rgba(0, 0, 0, 0.8) 0px -1px 0px;
}
#secondary_navigation a {
    color: #d7d7d7;
    height: 24px;
    line-height: 24px;
}
#user_navigation .user_photo {
    border: 1px solid rgba(0, 10, 120, 0.1);
    height: 26px;
    margin: -3px -9px 0px -9px;
    position: relative;
    width: auto;
}

.cPost .cAuthorPane .cAuthorPane_author {
line-height: 26px;
    margin: -16px -10px 16px -10px;
    font-size: 1.4rem;
    white-space: nowrap;
    height: 46px;}

.cForumList .ipsDataItem_unread .ipsDataItem_title:before,
.cForumGrid_unread .ipsPhotoPanel h3 a:after {
    background: #4499FF;
    color: #029;
    content: "NEW";
}
.cPost .cPost_contentWrap {
    height: calc(100% - 36px);
    margin-top: -18px;
    padding-bottom: 22px;
    padding-top: 0px;
}

body.ipsApp.cPost.cPost_contentWrap {
    padding-top: 18px;
    padding-bottom: 10px;
}

.focus-post-bar .cPost .cPost_contentWrap {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    flex: 0.6 1 auto;
}

.cPost .ipsComment_controls {
    background: none;
    border-color: rgba(255, 255, 255, 0.1);
    border-radius: 0px;
    border-width: 0px 1px 0 0;
    color: #5490c0;
    height: 17px;
    line-height: 10px;
}
.focus-post-buttons .cTopic .ipsComment_controls {
    color: #dddddd;
    padding: 5px 0;
    line-height: 1.65rem;
    font-weight: bold;
    font-size: 0;
}
.cPost .ipsComment_controls a,
.cPost .ipsComment_controls .ipsButton.cMultiQuote {
    background: none;
    border-color: rgba(255, 255, 255, 0.1);
    border-radius: 0px;
    border-width: 1px 1px 0 0;
    color: #5490c0;
    height: 17px;
    line-height: 14px;
}
.cPost .ipsComment_meta {
    border-bottom: 1px solid transparent;
    font-size: 1.2rem;
    height: 26px;
    line-height: 26px;
    margin-bottom: 2px;
    padding-top: 0;
}
.cke_skin_ips .cke_contents {
    height: 640px;
    margin: 5px;
}
.cke_toolgroup {
    background: #4d4d4d;
    background-image: linear-gradient(to bottom, #122324, #101213);
    border: 1px solid #101213;
    border-bottom-color: #101213;
    border-radius: 3px;
    box-shadow: 0 1px 0 rgba(255, 255, 255, .5), 0 0 2px rgba(255, 255, 255, .15) inset, 0 1px 0 rgba(255, 255, 255, .15) inset;
    float: left;
    margin: 0 6px 5px 0;
}
.col_c_icon {
    padding: 2px 2px 2px 8px;
    vertical-align: middle;
    width: 33px;
}
.ft {
    margin-right: 4px;
}
.ipsApp .ipsButton {
    -moz-user-select: none;
    -ms-user-select: none;
    -o-user-select: none;
    -webkit-font-smoothing: antialiased;
    -webkit-user-select: none;
    border: 1px solid rgba(0, 0, 0, 0.1);
    border-radius: 3px;
    display: inline-block;
    font-size: 13px;
    font-weight: 500;
    padding: 4px 20px;
    padding-bottom: 2px;
    padding-left: 20px;
    padding-right: 20px;
    padding-top: 2px;
    text-align: center;
    text-decoration: none;
    text-shadow: none;
    user-select: none;
    vertical-align: middle;
    white-space: nowrap;
}
.ipsApp .ipsSpacer_bottom,
.ipsApp .ipsSpacer_both {
    margin-bottom: 4px;
}
.ipsComment {
    margin-bottom: 4px;
    padding: 0;
    padding-bottom: 0px;
    padding-left: 0px;
    padding-right: 0px;
    padding-top: 0px;
    position: relative;
}
.ipsDataItem_main,
.ipsDataItem_stats,
.ipsDataItem_lastPoster,
.ipsDataItem_generic,
.ipsDataItem_modCheck,
.ipsDataItem_icon {
    padding-bottom: 1px;
    padding-top: 1px;
}
.ipsDataItem_title {
    margin-bottom: 1px;
}
.ipsDataItem_unread .ipsDataItem_lastPoster a.ipsContained {
    font-style: italic;
    font-weight: bold;
    text-decoration: underline;
}
.ipsDataItem_unread .ipsDataItem_title {
    font-style: italic;
    font-weight: bold;
    text-decoration: underline;
}
.ipsLayout_container {
    max-width: 100%;
}

.ipsApp.ipsApp_front.ipsJS_has.ipsClearfix.ipsApp_noTouch.ipsLayout_sidebarUnused {
    max-width: 100%;
    width: 100%;
}

.ipsLikeRep {
    background: none;
    line-height: 12px;
    padding: 0;
}
.ipsLikeRep .ipsButton {
    line-height: 12px;
    vertical-align: top;
}
.ipsPad_half,
.ipsApp ul.ipsPad_half,
.ipsApp ol.ipsPad_half {
    padding: 2px;
    padding-bottom: 2px;
    padding-left: 7px;
    padding-right: 7px;
    padding-top: 2px;
}
.ipsReputation_count {
    font-size: 1.1rem;
    line-height: 12px;
    vertical-align: top;
}
.ipsStreamItem_condensed.ipsStreamItem {
    margin-bottom: 1px;
    margin-top: 1px;
}
.ipsStreamItem_condensed.ipsStreamItem.ipsStreamItem_contentBlock {
    padding: 1px;
}
.ipsStreamItem_time {
    font-size: 8px;
    line-height: 12px;
}
.ipsStreamItem_unread .ipsStreamItem_title,
.ipsStreamItem_unread .ipsStreamItem_titleSmall {
    font-style: italic;
    font-weight: bold;
    text-decoration: underline;
}
.ipsThumb_small {
    max-height: 32px;
    max-width: 32px;
}
.ipsType_normal.ipsType_richText.ipsContained {
    border-collapse: separate;
    padding-top: 20px;
}

.ipsType_sectionHead.cAuthorPane_author {
    height: 36px;
    padding-top: 16px;
}
.maintitle {
    -moz-border-radius: 4px;
    -moz-box-shadow: inset rgba(255, 255, 255, 0.15) 0px 0px 0px 1px;
    -webkit-border-radius: 4px;
    -webkit-box-shadow: inset rgba(255, 255, 255, 0.15) 0px 0px 0px 1px;
    background: #31485c url('http://odium.loverslab.com/public/style_images/carbon_blue/maintitle.png') repeat-x 50% 0;
    border-radius: 4px;
    box-shadow: inset rgba(255, 255, 255, 0.15) 0px 0px 0px 1px;
    color: #fff;
    font-size: 12px;
    font-weight: bold;
    margin-bottom: 2px;
    overflow: hidden;
    padding: 4px;
    text-shadow: rgba(0, 0, 0, 0.4) 0px -1px 0px;
}
.nav-bar,
.ipsType_sectionTitle {
    background-image: none;
    height: 36px;
    padding-top: 8px;
}
.nav-bar,
.ipsType_sectionTitle,
.cPost:before,
#elMobileNav {
    background-image: none;
    height: 26px;
}
.nav-bar.hide-minimal {
    background-image: none;
    height: 40px;
    padding-top: 1px;
}
.wrapper {
    background-color: rgba(2, 12, 101, 0.3);
    margin: 0 auto;
    max-width: 100%;
    min-width: 980px;
    width: 100%;
}
.ipsApp.cke_combo_button {
    background: linear-gradient(180deg, #152425, #152425, #152425, #101010, #101010) !important;
    border-bottom-color: #979797;
    border-radius: 4px;
    box-shadow: 0 0px 0 rgba(255, 255, 255, .5), 0 0 2px rgba(255, 255, 255, .15) inset, 0 1px 0 rgba(255, 255, 255, .15) inset;
    cursor: default;
    display: inline-block;
    margin: 0 6px 5px 0;
    height: 33px;
    padding-top: 8px;
}
a.cke_combo_button {
    background: linear-gradient(180deg, #152425, #152425, #152425, #101010, #101010) !important;
    border-bottom-color: #979797;
    border-radius: 4px;
    box-shadow: 0 0px 0 rgba(255, 255, 255, .5), 0 0 2px rgba(255, 255, 255, .15) inset, 0 1px 0 rgba(255, 255, 255, .15) inset;
    cursor: default;
    display: inline-block;
    margin: 0 6px 5px 0;
    height: 33px;
    padding-top: 8px;
}
a.ipsContained ipsDataItem_unread {
    color: #226e9f;
    font-weight: bold;
}
a.ipsType_break.ipsContained ipsDataItem_unread {
    color: #44FF99;
}
a[data-mentionid] {
    background: #4499FF;
    border-radius: 2px;
    color: #029;
    display: inline-block;
    font-size: 90%;
    padding: 0px 5px;
    text-decoration: none;
}
div#branding {
    height: 64px;
}
div#logo {
    margin-top: 6px;
}
div#primary_nav {
    float: right;
    font-family: "Trebuchet MS", Arial, Helvetica, sans-serif;
    font-size: 12px;
    font-weight: bold;
    margin-top: 64px;
    text-transform: uppercase;
}
h2.ipsType_sectionTitle.ipsType_reset.cForumTitle {
    background-image: none;
    padding-top: 2px;
}
html .cMessage_members > ol > li {
    border-right: 2px solid #f0f0f0;
    line-height: 12px;
    margin: 1px 1px 1px 0;
    padding-right: 15px;
}
table.ipb_table td {
    padding: 2px;
}
.cke_button_icon.cke_button__ipsspoiler_icon {
    background-image: none;
    width: 16px;
}
.cke_button__ipsspoiler:before {
    color: white;
    content: '';
    width: 16px;
    height: 16px;
    line-height: 16px;
    font-size: 14px;
    text-align: left;
    display: inline-block;
    font-family: 'FontAwesome';
    font-style: normal;
    font-weight: normal;
    padding-top: 12px;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    transform: translate(0, 0);
    margin-left: -16px;
}
}
