css:
@-moz-document domain("blackboard.hanze.nl") {
:root {
         --background-color:#2f3136;
         --panel-color:#202225;
         --panel-border-radius:0;
         --header-background-color:#3e4044;
         --panel-text-color: #64666a;
         --primary-color:#EE7B00;
         --margins: 0rem;
         --margins-home: 1rem;
         --menu-background-color:#2f3136;
         --menu-side-background-color:#2f3136;
         --menu-side-text-color:white;
         --menu-text-color:white;
         --global-width:1;
         --heading-color:white;
    }
     #globalNavPageNavArea {
         position: fixed;
         width: 100%;
         z-index: 100;
         top: 0;
    }

#content {
 	background:var(--panel-color) !important;
}

#content *:not(h3) { 
    color:var(--panel-text-color) !important;   
}


     .global-nav-bar-wrap {
         position:fixed;
         right:0;
    }
     #myGroups {
         display:none;
    }
     .itemDetails, .announcementInfo {
         border:none;
    }
     .collapsible {
         border:none;
    }
     #globalNavPageContentArea {
         margin-top:51.2px !important;
         margin-bottom:var(--margins) !important;
    }
     #breadcrumbs {
         display:none;
         margin:var(--margins) !important;
    }
     #navigationPane {
         margin:0 var(--margins) !important;
    }
     #menuWrap {
         background:var(--menu-side-background-color);
         width:300px;
         margin:0 !important;
         height: 100%;
         position: fixed;
         box-shadow: -300px 0px 0 #2f3136, -600px 0px 0 #2f3136, -900px 0px 0 #2f3136;
         z-index: 9999;
    }
     #contentPanel {
         margin: 0 var(--margins) 0 calc(300px + 2*var(--margins)) !important;
    }
     .divider {
         display:none !important;
    }
     .locationPane {
         margin-top:0;
         padding:0 !important;
         margin-bottom: var(--margins);
    }
     #content, .portlet {
         border-radius: var(--panel-border-radius);
         overflow:hidden;
    }
     #content {
         margin-top:var(--margins);
    }
     .moduleTitle {
         text-shadow:none;
    }
     .path .root, .simplePath .root {
         border:none;
    }
     .navPalette h3, .path li, .simplePath li {
         text-transform: uppercase;
         color: var(--primary-color) !important;
    }
     .contentList>li, .buildList>li, .contentListPlain>li, .announcementList>li, .contentPageItem {
         border-top:0px;
    }
     #courseMenuPalette div.navPaletteContent {
         box-shadow:0 0 0 transparent;
    }
     .localViewToggle {
         height:0;
    }
     div.localViewToggle+div.pageTitle {
         padding: 20px 30px !important;
    }
     .portlet a {
         color:var(--panel-text-color);
    }
