     html>body:not([class='']) {
         font-family: 'Open Sans', sans-serif;
         background: var(--background-color);
    }
     h1, h2, h3, h4, h5, h6, #content h1 *, #content h2 *, #content h3 *, #content h4 *, #content h5 *, #content h6 * {
         font-family: 'Montserrat', sans-serif;
         color: var(--heading-color) !important;
    }
     p,#courseMenuPalette div.navPaletteContent ul li a, #courseMenuPalette h3 a, #previewCourseMenu h3 a, .navPaletteContent h3 a.submenuLink:before, .navPaletteContent h3 a.submenuLink_active:before, #courseMenuPalette div.navPaletteContent ul li a span, #courseMenuPalette .navPaletteContent .subhead {
         color:var(--panel-text-color) !important;
    }
     .hightlight {
         background-color: rgba(56, 80, 118, 0.8);
         color: white;
         padding: 0 0.3rem;
    }
     #topFrame, #actionBarBrand, .actionBar, .contentPaneWide .actionBar, div.localViewToggle, #pageTitleDiv {
         background: var(--header-background-color);
         box-shadow: 0 0 0 transparent;
         border-radius: 0 !important;
    }
     .actionBarMicro {
         display:none;
         background-color:transparent;
         box-shadow: 0 0 0 transparent;
         border-radius: 0 !important;
    }
     #contentPanel {
         margin: 0 12px 0 300px;
         overflow: hidden;
    }
     #globalNavPageNavArea {
         background: var(--menu-background-color);
    }
     #globalNavPageNavArea .appTabs span {
         color: var(--menu-text-color);
    }
