css:
@-moz-document domain("www.cracked.com")
{
/* Transparent */

#homeWrapper,
#header.navbar .navbar-nav>li.nav-icon.write-for-us-icon a,
#header.navbar .navbar-header .search-icon.open-search,
#header.navbar .navbar-header .search-icon:hover,
.bannerAd,
.mainFrame,
.mainFrame .mainFrameModule,
.mainFrameModule article.module,
header.headerWrapper .secondaryNav,
.content-card-meta-absolute li.share-btn-wrapper .share-wrapper,
a[style*="background-color: rgb(255, 255, 255);"],
#btn-search-canvas.collapsed,
.modal-header,
.modal-footer,
.content-cards.content-box,
.viral-cards-module,
.viral-meta-wrapper
{
    background: transparent !important;
}

.alert,
.content-card-meta-absolute li.share-btn-wrapper .share-wrapper
{
    border-color: transparent !important;
}
/* Black */

#header.navbar,
#header.navbar .navbar-nav>li .sub-nav-wrapper,
.contribution-module .btn.btn-contribution-module,
#footer.footer .footer-copyright-wrapper
{
    background-color: #000000 !important;
}
/* Cobalt */

html,
body,
.box-module,
.card-md-vertical .content-card .content-card-content,
.card-sm-vertical .content-card .content-card-content,
.contentInnerWrapper,
.modal.modal-login .modal-dialog .modal-content .modal-header .nav-tabs li a,
#footer.footer .footer-info-wrapper,
.nav-tabs.nav-tabs-profile li a,
.video-filter-wrapper
{
    background-color: #1c1f24 !important;
}
/* Light Cobalt */

#header.navbar .navbar-nav>li.dropdown:hover>a,
#header.navbar .navbar-nav>li.dropdown .sub-nav-wrapper,
.comment .comment-inner,
#loggedInUserDropdown,
.logged-in-user-icon:hover,
#headerSearch,
#headerSearchCanvas,
.article-index-filter-wrapper,
#header.navbar .navbar-header .search-icon:focus,
.modal-content,
.modal.modal-login .modal-dialog .modal-content .modal-header .nav-tabs li.active a,
#footer.footer #newsletter-sign-up-wrapper,
.nav-tabs.nav-tabs-profile li.active a,
.subheading span,
.social-share li a.btn-more-share,
.activeSubMenu,
header.headerWrapper .primaryNav > ul > li.navIcon.noRelativePosition ul,
header.headerWrapper .primaryNav > ul > li.hasSubMenu.noRelativePosition ul,
#header.navbar #navbarNavItemsVideo .sub-nav-wrapper.dropdown-menu .container,
#header.navbar .navbar-nav>li .sub-nav-wrapper .header-featured-browse-wrapper .header-browse-by-wrapper
{
    background-color: #5b6073 !important;
}
/* White */

body,
font,
h1,
h2,
h3,
h4,
h5,
h6,
p,
a,
span,
#headerSearch #headerSearchText,
#headerSearch #headerSearchSubmit,
#headerSearchCanvas .container #headerSearchTextCanvas,
.modal label,
#footer.footer #newsletter-sign-up-wrapper small,
.video-title,
.thumbnails-rr .trc_rbox_header,
.viral-cards-module .viral-cards-wrapper .viral-meta-wrapper .viral-meta,
.ob-widget-header,
.ob-rec-text,
.subheading,
.subheading span,
.content-card-meta-absolute li,
.featured-cards-comments,
.content-cards-comments
{
    color: #f2f2f2 !important;
}

a:hover
{
    color: #ffffff !important;
}

.article-index-filter-wrapper .btn-group .btn-group
{
    background-color: #ffffff !important;
}
/* Grey */

.content-card-content h4,
.content-card-content h4 a
{
    color: #848383 !important;
}
/* Red */

.content-card-content ul
{
    color: #b50000 !important;
}

div,
#header,
#header.navbar .navbar-nav>li.nav-icon.write-for-us-icon,
#header.navbar .navbar-nav>li.nav-icon.subscribe-button,
#header.navbar .navbar-nav>li.nav-icon.subscribe-button a,
#header.navbar .navbar-header .search-icon,
.modal.modal-login .modal-dialog .modal-content .modal-header .nav-tabs li.active a,
.modal.modal-login .modal-dialog .modal-content .modal-header .nav-tabs li a,
.contribution-module .btn.btn-contribution-module,
h2.module-title,
.subheading span,
.content-card-meta-absolute,
#btn-search-canvas
{
    border-color: #b50000 !important;
}

#header.navbar .navbar-nav>li .sub-nav-wrapper .header-featured-browse-wrapper .header-browse-by ul>li a,
.comment-submission .comment-submission-form .add-new-comment,
.comments-header .options a.active,
#headerSearchCanvas .container #headerSearchSubmitCanvas,
.social-share li a.favorites,
.modal .btn.btn-login.btn-login-blue,
.modal.modal-login .modal-dialog .close,
.contribution-module,
#footer.footer #newsletter-sign-up-wrapper .form-group #sign-me-up,
.btn-delete,
#headerBannerAd,
.btn.btn-profile.btn-red,
#closeFooter,
.video-social-share .favorites
{
    background-color: #b50000 !important;
}

article p a
{
    border-bottom: 1px #b50000 dotted;
}
/* Tweaks */

.comments-list>li:last-child:before
{
    display: none !important;
}

.large-btn
{
    background-color: #5b6073 !important;
    border: 3px solid #ffffff !important;
    color: #ffffff !important;
}

.social-share li a.btn-more-share
{
    border: 0px !important;
}

.footer .list-inline-item li,
.footer .list-inline-item a,
.footer .list-inline-item span,
.footer .list-inline li,
.footer .list-inline a,
.footer .list-inline span
{
    color: #5b6073 !important;
}

a.comments.comments-btn,
.persistent-share li span
{
    color: #929292 !important;
}

.article-index-filter-wrapper .btn-group .btn.btn-default.btn-search
{
    border-radius: 1px !important;
}

#upAndDownController a
{
    opacity: .5
}

.ajax-loader
{
    background: url(http://jorgecardoso.eu/static/assets/images/ajax-loader-segcolor.svg) center center no-repeat;
}

#header.navbar .navbar-nav>li.dropdown>a,
#header.navbar .navbar-nav>li.nav-icon.logged-in-user-icon>a
{
    border: 1px solid transparent;
}

#header.navbar .navbar-nav>li.dropdown:hover>a,
#header.navbar .navbar-nav>li.nav-icon.logged-in-user-icon:hover>a
{
    border-top: 1px solid #b50000;
    border-right: 1px solid #b50000;
    border-left: 1px solid #b50000;
    border-bottom: 1px solid #5b6073;
}

#header.navbar .navbar-nav>li.dropdown:hover .sub-nav-wrapper,
#loggedInUserDropdown
{
    border: 1px solid #b50000;
}


.btn.btn-outline-graylight 
{
    background-color: #d2d2d2;
}
.btn.btn-outline-graylight
{
    color: #5b6073 !important;
}
}
