css:
@-moz-document domain(nicehash.com) {
	body {
        background-color: #1b1a1a;
        color: #fff;
    }
    header nav.top {
        color:white;
        background-color: #fba342;
    }
    #intro.index {
        background-color: #1b1a1a;
    }
    #intro .tiles .tile {
        background-color: #333;
    }
    .page.profile .row.overflow #stats .col_4 h2 {
        border: 1px solid #333;
    }
    .page.profile .row.overflow #stats .col_4 .content {
        border: 1px solid #333;
    }
    .page.profile .col_1_3 .payout .payout_content {
        border: 1px solid #333;
    }
    .page .select select {
        background-color: #999;
    }

    .page #stats {
        border: 1px solid #333;
    }
    .page .sorting_row {
        border-bottom: 1px solid #333;
    }
    .page .pages .prev,
    .page .pages .next {
        color: #999;
    }

.page.profile h1{
    color:#fba342;
}

.page #stats ul li {
    display: inline-block;
    border-bottom: 6px solid #3a3939;
    padding: 0 5px 10px 5px;
    text-align: center;
    width: 140px;
    cursor: pointer;
}


.page #stats ul {
    padding: 30px 0 0 0;
    /* border-bottom: 1px solid #dbdbdb; */
    margin: 0;
    list-style: none;
}


.page #stats .search {
    /* border: 1px solid #dbdbdb; */
    border-radius: 3px;
    position: absolute;
    top: 20px;
    right: 30px;
    width: 200px;
}

.page table tr th {
    background: #fba342;
    color: #fff;
    padding: 15px 15px;
    font-size: 14px;
    text-align: left;
}

.page table tr:last-child {
    border-bottom: 1px solid #3a3939;
}
.page table tr:nth-child(2n+1) {
    background: #3a3939;
}

#cookie_message {
    background: #3a3939;
    border-top: 1px solid #3a3939;
    padding: 0;
    color: white;
}

#cookie_message p {
    color: #eeeeee;
    font-weight: 400;
    font-size: 14px;
    text-align: left;
    padding: 22px 0 22px 54px;
    margin: 0;
    display: block;
    float: left;
}

header .logo{
    text-indent: 9999px;
    background: url(https://www.nicehash.com/images/web/sprite.svg) no-repeat;
    background-size: 600px 600px;
    background-position: 0px -49px;
    width: 192px;
    height: 50px;
    display: block;
    margin-bottom: 20px;
}
.page table.inside tr th {
    background: transparent;
    background-image: initial;
    background-position-x: initial;
    background-position-y: initial;
    background-size: initial;
    background-repeat-x: initial;
    background-repeat-y: initial;
    background-attachment: initial;
    background-origin: initial;
    background-clip: initial;
    background-color: transparent;
    font-size: 10px;
    font-weight: 400;
    color: #959595;
    letter-spacing: 1px;
    text-transform: uppercase;
    padding: 0 15px 10px 15px;
    border-bottom: 1px solid #333333;
    cursor: pointer;
}


.page .select select {
    background-color: #1b1a1a;
}

.page.miner-profile .page table.inside tr:first-child {
    border-bottom: 0;
    background: none;
}

.page table tr.workers{
    background:none;
}
}
