css:
@-moz-document domain("glodls.to") {
body {
    font-family: Verdana, Arial, Helvetica, sans-serif;
    font-size: 9pt;
    color: #fff;
}
html, body, div {
    margin: 0;
    padding: 0;
    background-color: #222;
}
A:link {
    color: #eee;
}
A:visited {
    color: #ddd;
}
A:active {
    color: #fff;
}
#root li a:hover {
    color: #eee;
}
#root li ul.sub {
    background: #222;
    border-top: solid 1px #444;
    border-bottom-color: #222;
}
#root li ul.sub li a {
    border-top-color: #444;
    border-bottom-color: #222;  
}
#root li ul.sub li a:hover {
    background: 000;
    border-top-color: #444;
    border-bottom-color: #222;
}
.t-row:hover, .f-row:hover {
    background-color: #111;
    color: #fff;
}
#menu {
background: linear-gradient(to bottom, #444444 0%,#222222 100%);
border-bottom: solid 1px #444444;
}
#mCol .myBlock fieldset legend {
background: linear-gradient(to bottom, #444444 0%,#222222 100%);
}
.ttable_head {
background: linear-gradient(to bottom, #444444 0%,#222222 100%);
}
#logo {
    background-image: url('https://image.ibb.co/iDP9YG/logo.jpg');
    background-repeat: no-repeat;
    background-position: 0px 0px;
    display: block;
    height: 120px;
    width: 520px;
    position: absolute;
    left: 20px;
    top: 0px;
}

#downloadbox {
    background: linear-gradient(to bottom, #444 0%, #222 100%);
}
input[type=reset], input[type=button], input[type=reset], input[type=submit] {
        background: linear-gradient(to bottom, #222 0%, #222 100%);
    color: #fff;
}
.f-title {
   background: linear-gradient(to bottom, #444 0%,#222 100%); 
}
.table_head {
    background: linear-gradient(to bottom, #444 0%,#222 100%);
}
.p-title {
    background: linear-gradient(to bottom, #444 0%,#222 100%);
}
}
