/*********	Fondo	********************/

@-moz-document domain(campusvirtual.uclm.es) {
   body {
    background-image: url('https://i.imgur.com/QjXKjUw.jpg');
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-position: center;
    background-size: cover;
    color: #5fc7fb;
    font-family: "Open Sans", "Helvetica Neue", Helvetica, Arial, sans-serif;
}

/*********	Barra de navegasao	********************/

.navbar-light {
    background-color: rgba(255, 255, 255, 0.81);
    border-bottom: 1px solid rgba(229, 229, 229, 0);
}
/********* me cago en ti ********/

.popover {
    background: rgba(55, 55, 55, 0.81);
}

.card-header:first-child {
    border-radius: 0 0 0 0;
    background: rgba(0, 0, 0, 0.00);
}
/*********	Color Recuadro	********************/

.card,
#page-enrol-users #filterform,
.que .history,
.userprofile .profile_tree section,
.groupinfobox,
.forumpost,
.yui-skin-sam .yui-layout.path-mod-chat-gui_ajax .yui-layout-unit-right,
.yui-skin-sam .yui-layout.path-mod-chat-gui_ajax .yui-layout-unit-bottom,
.yui-skin-sam .yui-layout.path-mod-chat-gui_ajax #chat-messages .chat-message.course-theme,
.well {
    position: relative;
    display: block;
    margin-bottom: .75rem;
    background-color: rgba(0, 0, 0, 0.73);
    border-radius: 0;
    border: 0px solid rgba(238, 238, 238, 1);
}
/*********	Color nombre	********************/

.page-context-header .page-header-headings h1 {
    color: #ffffff;
}
/*********	El puÃ±etero calendario, que algunas se quejaban -.- *********/

.block .minicalendar th,
.block .minicalendar td {
    padding: 2px;
    font-size: .8em;
    text-align: center;
    background: darkslategrey;
    color: darkorange;
}

.text-muted,
a.dimmed,
a.dimmed:link,
a.dimmed:visited,
a.dimmed_text,
a.dimmed_text:link,
a.dimmed_text:visited,
.dimmed_text,
.dimmed_text a,
.dimmed_text a:link,
.dimmed_text a:visited,
.usersuspended,
.usersuspended a,
.usersuspended a:link,
.usersuspended a:visited,
.dimmed_category,
.dimmed_category a,
.tag_feed .media .muted a,
#page-admin-qtypes .disabled,
#page-admin-qbehaviours .disabled,
#page-admin-plugins #plugins-control-panel .pluginname .componentname,
#page-admin-plugins #plugins-control-panel .version .versionnumber,
#page-admin-plugins #plugins-control-panel .notes .requiredby,
#plugins-check-page .page-description,
#plugins-check-page #plugins-check .requires-ok,
#plugins-check-page #plugins-check .displayname .plugindir,
#plugins-check-page #plugins-check-available-dependencies .displayname .component,
.block .minicalendar td.weekend,
.section .activity .stealth,
.course-content .section-summary .section-summary-activities .activity-count,
.course-content ul li.section.hidden .sectionname>span,
.course-content ul li.section.hidden .content>div.summary,
.course-content ul li.section.hidden .activity .activityinstance,
#course-category-listings .listitem[data-visible="0"],
#course-category-listings .listitem[data-visible="0"]>div>a,
#course-category-listings .listing-pagination-totals.dimmed,
.fitem.disabled .fp-btn-choose,
.form-defaultinfo,
.form-label .form-shortname,
.formsettingheading .form-horizontal,
.no-felement.fstatic,
.path-backup .backup_progress .backup_stage {
    color: #787878 !important;
}
popover{
   color:red !important;
}
/*********	Bloque asignaturas	*********/

.block,
#dockeditempanel .dockeditempanel_content {
    background-color: rgba(5, 8, 10, 0);
}
/*********	Bloque derecha	*********/

aside#block-region-side-pre {
    background: rgba(4, 4, 6, 0.73);
}
/*********	Texto Asignaturas	*********/

a {
    color: #FFF;
    text-decoration: none;
}

a:hover {
    /*Para cuando pasa por encima el raton*/
    
    text-decoration-style: wavy;
    color: #5fc7fb;
    font-weight: 200;
}
/*********	Color cursos	*********/

.unlist,
.unlist li,
.inline-list,
.inline-list li,
.block .list,
.block .list li,
.section li.activity,
.section li.movehere,
.tabtree li {
    list-style: none;
    margin: 0;
    color: #5fc7fb;
    padding: 0;
}
/*********	En pagina de entrega de trabajos	*********/

.table-striped tbody tr:nth-of-type(odd),
table.collection tbody tr:nth-of-type(odd),
table#explaincaps tbody tr:nth-of-type(odd),
table#defineroletable tbody tr:nth-of-type(odd),
table.grading-report tbody tr:nth-of-type(odd),
table#listdirectories tbody tr:nth-of-type(odd),
table.rolecaps tbody tr:nth-of-type(odd),
table.userenrolment tbody tr:nth-of-type(odd),
form#movecourses table tbody tr:nth-of-type(odd),
#page-admin-course-index .editcourse tbody tr:nth-of-type(odd),
.forumheaderlist tbody tr:nth-of-type(odd),
table.flexible tbody tr:nth-of-type(odd),
.generaltable tbody tr:nth-of-type(odd):hover {
    background-color: rgba(158, 31, 31, 0.92);
}

td,
th {
    display: table-cell;
    vertical-align: inherit;
    background-color: #334c61;
}

th,
td:hover {
    display: table-cell;
    vertical-align: inherit;
    background-color: rgba(158, 31, 31, 0.92);
}
/*********	Footer	*********/

.bg-inverse {
    color: #f3f2f2!important;
    background-color: rgba(55, 58, 60, 0.69)!important;
}

#page-footer a {
    color: #53b6ff;
}
}
