css:
@-moz-document domain("teambition.com") {
    [data-role="link-button"],
    .sprint-icon,
    [data-role=icon],
    .hover-is-blue,
    [class*='workspace-switcher-creator'] {
        color: #28bea0 !important;
    }
    [data-role=icon] {
        fill: #28bea0 !important;
    }
    .task-detail [data-role="icon"] {
        fill: #a6a6a6 !important;
        color: #a6a6a6 !important;
    }
    [data-category="my"] [class*='item-dashboard'],
    [data-category="calendar"] [class*='item-cal'],
    [data-category="inbox"] [class*='item-inbox'] {
        background: rgba(255, 255, 255, 0.2) !important;
    }
    #navigator .nav-inner,
    #navigator .nav-footer,
    #navigator .nav-footer .nav-footer-handler.active,
    #navigator .nav-badge,
    .message-view .message-actions .unread-activities-count,
    .message-view .message-actions .unread-badge,
    .inbox-view .messages-header .dropdown .inbox-sort .icon-wrapper.unread:after,
    .inbox-view .messages-header .dropdown .inbox-type .icon-wrapper.unread:after,
    .active-bar,
    .member-progress-view .progress-bar {
        background: #28bea0 !important;
    }
    #navigator *,
    #navigator a:hover {
        color: white !important;
    }
    #navigator .with-text::after {
        top: 8px;
    }
    #navigator .dropdown-menu a {
        color: #383838 !important;
    }
    #navigator .nav-inner {
        align-items: center;
        min-height: 60px;
    }
    #content {
        padding-top: 60px;
    }
    #navigator [class^=system-bar-left] * {
        color: #666 !important;
    }
    .search-bar {
        background: #fff;
    }
    .search-bar + svg {
        opacity: 0;
    }
    .sub-navigator,
    .my-view,
    .inbox-view,
    .calendar-view {
        top: 60px;
    }
    .project-panel,
    .board-view {
        top: 110px;
    }
    .btn-primary,
    .btn-primary:focus,
    .sub-nav .nav-underscore > li > a::after,
    .my-view .my-header .my-tab-wrap .my-tab:after {
        border-color: #28bea0;
    }
    .btn-primary,
    .btn-primary:focus,
    .btn-primary:hover,
    .project-navigation .nav-badge {
        background-color: #28bea0;
    }
    a:hover,
    a:focus,
    .member-progress-view a.upgrade-handler,
    .project-navigation .nav-body .nav-wrapper.nav > li .tasklist-pop-handler > .icon:hover,
    .project-navigation .homepage-handler:hover,
    .project-navigation .nav-footer .icon-state-check,
    .project-navigation .nav-footer .nav-menu-handler.on-active,
    .project-navigation .nav-footer .nav-app-menu-handler.on-active,
    .tasklist-bar .tasklist-bar-header .icon-circle-cross,
    .detail-infos-link-view .link-add-wrap .add-link-handler > *,
    .detail-infos-subtask-view .subtask-card .subtask-add-wrap .subtask-add-handler > *,
    .involve-view .add-involvement-handler .icon-circle-cross,
    .activity-creator .activity-handler .notif-info a,
    .tasklist-tab.on-active .tasklist-title,
    .member-bar-view .add-members-handler .add-member-icon,
    .member-bar-view .add-members-handler .add-members-p,
    .essage .essage-url,
    .essage .show-bookmark-hint,
    .essage .essage-content a,
    .essage.essage-success .essage-status,
    .detail-infos-note-view .note-input-set .note-text a,
    .detail-infos-note-view .note-wrap .note-text a,
    .member-menu-view .popover-content li.invite-more a,
    .link-add-handler {
        color: #28bea0 !important;
    }
    /* essage */

    .essage.essage-success {
        border-left-color: #28bea0;
    }
    /* hide */

    .workspace-guide-view,
    .user-satisfaction-view,
    .new-ribbon {
        display: none !important;
    }
    /* tag */

    .task.task-card .task-infos .tag {
        border-radius: 100px !important;
        padding: 0 12px !important;
        text-align: center !important;
        color: white !important;
    }
    .task-infos .tag::before {
        display: none !important;
    }
    .tag-color-none {
        background-color: #a6a6a6 !important;
    }
    .tag-color-blue {
        background-color: #3da8f5 !important;
    }
    .tag-color-yellow {
        background-color: #ffaf38 !important;
    }
    .tag-color-green {
        background-color: #75c940 !important;
    }
    .tag-color-gray,
    .tag-color-cyan {
        background-color: #2fbdb3 !important;
    }
    .tag-color-red {
        background-color: #ff4f3e !important;
    }
    .tag-color-purple {
        background-color: #797ec9 !important;
    }
    .task.task-card .task-infos .icon-wrapper {
        color: #a6a6a6;
    }
    .task.task-card .task-infos .icon-wrapper.overdue {
        color: #ff4f3e;
    }
    .task-card:hover .task-priority,
    .task-card .task-priority {
        width: 6px !important;
        opacity: 1 !important;
    }
    .tasks-priority-0 {
        background-color: #2fbdb3 !important;
    }
    .task-content-set .task-info-wrapper,
    .task-content-set .task-info-footer {
        display: none !important;
    }
    .object-modal-view.fixed,
    .modal .modal-dialog.post-creator {
        width: 75%;
        left: 15%;
        margin: 0;
    }
    .scrum-stage {
        background-color: #fdfdfd;
    }
    .board-scrum-view {
        background-color: #efefef;
    }
    .scrum-stage .task-creator-handler-wrap a {
        color: #aaa !important;
    }
    .scrum-stage .task-creator-handler-wrap {
        background-color: inherit;
    }
    .task.task-card,
    .scrum-stage .task-creator-wrap {
        box-shadow: 0 1px 8px 0 rgba(0, 0, 0, 0.1);
    }
    .task.task-card:hover,
    .scrum-stage .task-creator-wrap:hover {
        box-shadow: 0 2px 13px 1px rgba(0, 0, 0, 0.15);
    }
    .scrum-stage .task.task-card,
    .scrum-stage .task-creator-wrap {
        margin: 0 8px 12px;
    }
    .task.task-card .task-priority {
        width: 8px !important;
        opacity: 1 !important;
    }
    .bg-priority-0 {
        background-color: #8bd3c4;
    }
    .scrum-stage .scrum-stage-content::-webkit-scrollbar {
        display: none;
    }
    .scrum-stage .task.task-card .avatar {
        height: 32px;
        width: 32px;
    }
    .member-menu-view .popover-content ul {
        max-height: 500px;
    }
    .add-members {
        display: none;
    }
    .add-members-handler {
        padding-left: 16px !important;
    }
    .member-progress-view .progress {
        background: rgba(40, 190, 160, 0.22);
    }
    [class^="logo"] {
        height: auto;
        width: auto;
        background: none;
    }
    [class^='logo']:after {
        content: 'teambition';
        font-size: 20px;
        font-family: monospace;
    }
}
