css:
/* Google Patent Search */
@-moz-document url-prefix(http://www.google.com/patents) {

#guser > nobr > b,
#gaia_bar > b
	{display:none !important}
}


/* Google Alerts */
@-moz-document url-prefix(http://www.google.com/alerts) {

[width="100%"][cellspacing="0"][cellpadding="3"][border="0"] > tbody > tr > [align="right"] > 

[size="-1"] > b
	{display:none !important}
}


/* Google Books */
@-moz-document url-prefix(http://books.google.com/),
               url-prefix(http://www.google.com/books) {

#guser > nobr > b,
#gaia_bar > b
	{display:none !important}
}


/* Google Bookmarks */
@-moz-document url-prefix(http://www.google.com/bookmarks),
               url-prefix(https://www.google.com/bookmarks) {

[width="100%"][cellspacing="0"][cellpadding="0"][border="0"] > tbody > tr > [nowrap=""] > [size="-1"] > b
	{display:none !important}
}


/* Google Base */
@-moz-document url-prefix(http://www.google.com/base),
               url-prefix(http://base.google.com/) {

[class="navigation"] > b
	{display:none !important}
}


/* Google Code */
@-moz-document url-prefix(http://code.google.com) {
#gaia > * > b
	{display:none !important}
}


/* Google Finance */
@-moz-document url-prefix(http://finance.google.com),
               url-prefix(http://www.google.com/finance) {

[id="guser"] > * > b
	{display:none !important}
}

/* Google Products */
@-moz-document url-prefix(http://www.google.com/products),
               url-prefix(http://google.com/products),
               url-prefix(http://www.google.com/prdhp?) {

[id="guser"] > nobr > [size="-1"] > b
	{display:none !important}
}


/* Google Images */
@-moz-document url-prefix(http://images.google.com),
               url-prefix(http://www.google.com/imghp) {

#guser > nobr > b
	{display:none !important}
}


/* Google Maps (and Local) */
@-moz-document url-prefix(http://maps.google.com),
               url-prefix(http://www.google.com/maps),
               url-prefix(http://local.google.com),
               url-prefix(http://www.google.com/local) {

#guser > nobr > b
	{display:none !important}
}


/* Google Transit */
@-moz-document url-prefix(http://www.google.com/transit) {

#guser > nobr > b
	{display:none !important}
}


/* Google News */
@-moz-document url-prefix(http://news.google.com),
               url-prefix(http://www.google.com/news) {

#guser > nobr > [size="-1"] > b
	{display:none !important}
}


/* Google Video */
@-moz-document url-prefix(http://video.google.com) {

#guser > nobr > b
	{display:none}
}


/* Google */
@-moz-document url-prefix(http://www.google.com/webhp),
               url(http://www.google.com/),
               url-prefix(http://www.l.google.com/),
               url-prefix(http://www.google.com/search),
               url-prefix(http://www.google.com/#b) {

#gb > nobr > b,
#guser > nobr > b
	{display:none !important}
}


/* Google CoOp */
@-moz-document url-prefix(http://www.google.com/coop) {

#email
	{display:none !important}
}


/* Google Reader */
@-moz-document url-prefix(http://www.google.com/reader),
               url-prefix(https://www.google.com/reader) {

#global-info > b
	{display:none !important}
}


/* Google Notebook */
@-moz-document url-prefix(http://www.google.com/notebook),
	       url-prefix(http://google.com/notebook) {

[id="guser"] > * > b
	{display:none !important}
}


/* Google Documents */
@-moz-document url-prefix(http://docs.google.com),
               url-prefix(https://docs.google.com) {

.gaialinks > * > b,						/* main pages */
[id="navigation"] > * > * > * > * > * + * > b,			/* editing a word processing document */
[valign="middle"] > td + [align="right"] > b			/* editing a presentation */
	{display:none !important}
}

@-moz-document url-prefix(http://spreadsheets.google.com),
               url-prefix(https://spreadsheets.google.com) {
[id="id_userinfo"] > [style*="bold"]				/* editing a spreadsheet */
	{display:none !important}
}


/* Google Personalized Search */
@-moz-document url-prefix(http://www.google.com/searchhistory),
               url-prefix(https://www.google.com/searchhistory),
               url-prefix(https://www.google.com/history),
               url-prefix(http://www.google.com/history) {

[width="100%"][cellspacing="0"][cellpadding="0"][border="0"] > tbody > tr > [nowrap=""] > [size="-1"] > b
	{display:none !important}
}


/* Google Accounts */
@-moz-document url-prefix(http://www.google.com/accounts),
               url-prefix(https://www.google.com/accounts){

[nowrap="nowrap"][align="right"] > [size="-1"][face="Arial, sans-serif"] > b
	{display:none !important;}
}


/* Google Calendar */
@-moz-document url-prefix(http://www.google.com/calendar),
               url-prefix(https://www.google.com/calendar){

#guser > b
/*[class="s"][style="white-space: nowrap; padding-bottom: 2px; text-align: right; position: absolute;"] > b*/
	{display:none !important;}
}


/* Gmail */
@-moz-document url-prefix("http://mail.google.com/"),
               url-prefix("https://mail.google.com/") {

/* OLD VERSION */
#guser > nowrap > b,
[class="trb"][valign="top"][align="right"] > b
	{display: none !important;}

/* NEW VERSION */
[id="guser"] > nobr > b
	{display:none !important;}
}


/* Google Groups */
@-moz-document url-prefix(http://groups.google.com) {

#guser > nobr > b,
.gtopbar > table > tbody > tr > td + td > nobr > b
	{display:none !important}
}


/* Google Pages */
@-moz-document url-prefix(http://pages.google.com) {

[style*="position: absolute; right: 15px; top: 7px; font-size: 83%"] > b
{display:none !important}
}


/* Google Picasa */
@-moz-document url-prefix(http://picasaweb.google.com) {

#guser > nobr > b
	{display:none !important}
}


/* Personalized Google */
@-moz-document url-prefix(http://www.google.com/ig),
               url-prefix(https://www.google.com/ig) {

#guser > nobr > b
	{display:none !important}
}

/* Google Movie listings */
@-moz-document url-prefix(http://www.google.com/movies) {

#guser > nobr > b
	{display:none !important}
}

/* Google blog search */
@-moz-document url-prefix(http://blogsearch.google.com/) {

#guser > nobr > b
	{display:none !important}
}

/* Google support */
@-moz-document url-prefix(http://www.google.com/support/) {

#baseDiv > div:first-child > strong
	{display:none !important}
} 


