css:
@-moz-document domain("cyclesformax.net") {
.navbar-default {
    background-color: #181818 !important;
    border-color: #111 !important;
    box-shadow: 0px 0px 4px rgba(0, 0, 0, 0.5) !important;
}
.site-header {
    background-color: #111 !important;
}
body {
    background-color: #222 !important;
    color: #aaa !important;
}
a {
    color: #fff !important;
}
.entry-meta {
    color: rgba(180, 180, 180, 0.5) !important;
}
.post {
    padding: 10px !important;
    background-color: #333 !important;
    border: 1px solid #111 !important;
    box-shadow: 0px 0px 4px rgba(0, 0, 0, 0.25) !important;
}
/*---------------------------*/
#content {
    border: solid 1px #151515 !important;
    border-radius: 3px !important;
    box-shadow: 0 1px 2px rgba(0, 0, 0, 0.12) !important;
    background-color: #252525 !important;
}
/*Panels*/
.panel-default {
    border-color: #333 !important;
}
.panel {
    background-color: #333 !important;
    border: 1px solid #111 !important;
    border-radius: 4px;
    box-shadow: 0px 0px 4px rgba(0, 0, 0, 0.25) !important;
}
heading {
    color: #EEE !important;
    background-color: #444 !important;
    border-color: #111 !important;
}
.panel-heading {
    border-bottom: 1px solid #111 !important;
    border-bottom-color: transparent;
    border-top-left-radius: 3px !important;
    border-top-right-radius: 3px!important;
}
.panel-title {
    color: #EEE !important;
    background-color: #444 !important;
}
.panel-default > .panel-heading {
    color: #EEE!important;
    background-color: #444 !important;
    border-color: #111 !important;
    border-bottom-color: #111 !important;
}
.panel-info > .panel-heading {
    color: #EEE!important;
    background-color: #444 !important;
    border-color: #111 !important;
    border-bottom-color: #111 !important;
}
.panel-footer {
    background-color: #444 !important;
    border-top: 1px solid #111 !important;
    border-bottom-right-radius: 3px !important;
    border-bottom-left-radius: 3px !important;
}
.panel-warning > .panel-heading {
    color: #EEE !important;
    background-color: #444 !important;
    border-color: #111 !important;
    border-bottom-color: #111 !important;
}
/*__________________________*/
.navbar-default .navbar-nav > .active > a,
.navbar-default .navbar-nav > .active > a:focus,
.navbar-default .navbar-nav > .active > a:hover {
    color: #00AAFF !important;
    background-color: #222 !important;
}
li > a:hover {
    text-decoration: none !important;
    background-color: #333 !important;
}
hr {
    border-top: 1px solid #777 !important;
}
/*PAGER*/
.pager li > a,
.pager li > span {
    background-color: #353535 !important;
    border: 2px solid #EEE !important;
    border-radius: 15px !important;
}
.pager li > a:hover {
    background-color: #1a709a !important;
    border: 2px solid #00aaff !important;
}

/*Button*/


.btn-default {
    color: #ccc !important;
    background-color: #666 !important;
    border-color: #111 !important;
}

.btn-default:hover {
    color: #fff !important;
    background-color: #1a709a !important;
    border-color: #111 !important;
}

/*Overview*/
.wy-nav-content-wrap {
    background: #333 !important;
}
/*
.wy-menu-vertical li.current {
    background: #00f !important;
}*/

.wy-menu-vertical li ul.subnav ul.subnav {
    background: #555 !important;
}

ul.subnav {
    background: rgba(0, 0, 0, .2) !important;
}



.wy-menu-vertical li.current a {
    color: #fff !important;
    border-right: solid 1px #111 !important;
    
}

.wy-menu-vertical li.on a:hover, .wy-menu-vertical li.current > a:hover {
    color: #fff !important;
    background: #1a709a !important;
        background-color: rgb(26, 112, 154) !important;
}

.wy-menu-vertical li.on a, .wy-menu-vertical li.current > a {
    color: #ccc !important;
    background: #666 !important;
    border-bottom: solid 1px #111 !important;
    border-top: solid 1px #111 !important;
}

/*Input*/
input[type="text"], input[type="password"], input[type="email"], input[type="url"], input[type="date"], input[type="month"], input[type="time"], input[type="datetime"], input[type="datetime-local"], input[type="week"], input[type="number"], input[type="search"], input[type="tel"], input[type="color"] {
    border: solid 1px #111 !important;
    box-shadow: inset 0 1px 3px rgba(0, 0, 0, .5) !important;
    border-radius: 0;
}
  /*2018*/
.jumbotron {
    background-color: #282828 !important;
}

div.news-post {
    background-color: #333 !important;
    box-shadow: 0px 0px 4px rgba(0, 0, 0, 0.25) !important;
    border-radius: 6px !important;
}


}
