css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url(http://myanimelist.net/), 
url-prefix(http://myanimelist.net/ab),
url-prefix(http://myanimelist.net/ad),
url-prefix(http://myanimelist.net/anime.),
url-prefix(http://myanimelist.net/anime/),
url-prefix(http://myanimelist.net/b),
url-prefix(http://myanimelist.net/c),
url-prefix(http://myanimelist.net/d),
url-prefix(http://myanimelist.net/e),
url-prefix(http://myanimelist.net/f),
url-prefix(http://myanimelist.net/h),
url-prefix(http://myanimelist.net/i),
url-prefix(http://myanimelist.net/l),
url-prefix(http://myanimelist.net/manga.),
url-prefix(http://myanimelist.net/manga/),
url-prefix(http://myanimelist.net/mo),
url-prefix(http://myanimelist.net/my),
url-prefix(http://myanimelist.net/n),
url-prefix(http://myanimelist.net/p),
url-prefix(http://myanimelist.net/r),
url-prefix(http://myanimelist.net/s),
url-prefix(http://myanimelist.net/t),
url-prefix(http://myanimelist.net/u) {

    /*All blue dialogs and input textareas*/
    #dialog{
        width: 800px !important;
    }

    #dialog form .textarea {
        width: 800px !important;
    }   
    
    .textarea{
        width: 98.5% !important;
    }

	.textarea[class="textarea"][rows="10"][cols="80"][name="msg_text"]{
        width: 940px !important;
    }
    
    /*Ads*/
    .adCenter728, .adCenter300, #surround_nav_ad{
        display:none !important;
    }
    
    /*Smaller space between text lines*/
    BODY *{
        line-height: normal !important;
    }
}

/*Edit Advance CSS File*/
@-moz-document url-prefix(http://myanimelist.net/editprofile.php?go=stylepref&do=cssadv&id){
    
    DIV[style="padding: 0pt 15px;"]{
        width: inherit !important;
        padding-left: 0px !important;
        padding-right: 0px !important;
    }
    .textarea{
        height: 500px !important;
    }
}

/*Forum new reply*/
@-moz-document url-prefix(http://myanimelist.net/forum/?action=message){
    
    .textarea{
        height: 500px !important;
    }
}

/*My recommendation and blog*/
@-moz-document url-prefix(http://myanimelist.net/myrecommendations.php?go=make),
url-prefix(http://myanimelist.net/myblog.php){
    
    DIV[style="padding: 0pt 10px 10px;"]{
        width: inherit !important;
        padding-left: 0px !important;
        padding-right: 0px !important;
    }
    .textarea{
        height: 400px !important;
    }
}

/*My messages*/
@-moz-document url-prefix(http://myanimelist.net/mymessages.php?go=send),
url-prefix(http://myanimelist.net/mymessages.php?toname){
    
    DIV[style="width: 530px; margin-left: auto; margin-right: auto;"]{
        width: inherit !important;
        padding-left: 0px !important;
        padding-right: 0px !important;
    }
    
    .textarea{
        height: 350px !important;
    }
}
