css:
@-moz-document domain("trello.com") {
/* left menu */
/* panel bg */
.boards-drawer,
.boards-drawer-header{background:linear-gradient(90deg,rgba(0,0,0,.2),rgba(0,0,0,0));box-shadow:none;}

/* search */
.boards-drawer input{background-color:rgba(0,0,0,.2);border-color:rgba(255,255,255,.2);}

/* text */
.boards-drawer-header,
.boards-drawer .icon-sm,
.boards-drawer .quiet-button,
.boards-drawer .quiet,
.boards-drawer .quiet a{color:rgba(255,255,255,.8);text-shadow:0 0 2px #000;}

/* board bar */
.compact-board-tile-fade{background:linear-gradient(45deg,rgba(0,0,0,.8),rgba(0,0,0,.2));}
.compact-board-tile-link-thumbnail{opacity:1;}
.sidebar-boards-list a,.sidebar-boards-list .icon-sm{color:#FFF;text-shadow:0 0 2px #000,0 0 8px #000;}

.compact-board-tile-background,
.compact-board-tile-fade,
.compact-board-tile-link{border-radius:3px 3px 20px;}

/* starred icon */
li[class^='compact-board-tile starred'] .icon-sm{opacity:0!important;}
li[class^='compact-board-tile starred selected'] .icon-sm{opacity:1!important;}

/* hover */
.boards-drawer .quiet-button:hover,
.boards-drawer .sidebar-section-header-toggle-icon:hover,
.selected *{text-shadow:none!important;color:#444;}

}
