css:
@-moz-document domain("www.avrfreaks.net") {
.node-type-forum .field-name-body, .node-type-forum .field-name-comment-body, .node-type-forum .forum-post-poll, .node-type-forum .author-signature, .privatemsg-message-body {
    color: #ccc;
     background-color: #666666;
}

body {

    background-color: #333333;
}

.forum-post-upper-panel {
    background: #333333;
}

.forum-author-name-block .author-pane .user-picture {
    background: #333333;
}

.forum-author-name-block .author-pane {
    background: #333333;
}

div.forum-post {
    background: #333333;
}

.forum-post-info {
    background: #333333;
}

.forum-post-content {
    background-color: #333333;
}

.avr-forum-post-wrapper .avr-forum-post-panel-main {
background-color: #333333;
}

#crumbs {
background-color: #83aac5;
}

.header-second > div {
background-color: #83aac5
}

button, [type="reset"], [type="submit"], [type="button"], [class*="action-item"], .commons-login, .forum-post-links .links a {
    background-color: #858585;
}


element.style {
    background-color: #335883 !important; 
}

#footer-second {
    background-color: #333333;
}

.page-forum .pane-avrfreaks-select form .form-type-select label, .page-forum .pane-avrfreaks-select form .form-type-select select, .node-type-forum .pane-avrfreaks-select form .form-type-select select, .node-type-forum .pane-avrfreaks-select form .form-type-select label {
    background-color: #858585;
}

.bb-code-block, pre.text, pre.c_cpp, pre.csharp, pre.css, pre.html, pre.java, pre.javascript, pre.xml {
background-color: #333333;
}

.avr-forum-post-wrapper {
    background: #333333;
}


}
