css:
@-moz-document url-prefix("https://jbzdy.cc/") {
    
/*Przyciski w oczekującej*/
.fb-share, .fb_iframe_widget, a.btn-send-messenger {
    display: none !important;
}


 /*Czarne tło*/
 body, section[role=top-categories] {
  background: #111 !important;
 }
section[role=listing] article>.content-info .title, section[role=listing] article>.content-info .info, section[role=quick-create] .inside form,
section[role=comments] form:not(.editableform)
 {
background: #111 !important;
}

#container #container-right { 
 height: 0px  !important;
 background: #111 !important;
border-top: #111 25px solid !important;
}
  
 #container #container-left {  
 background-color: #111 !important;
 border-right: 0px !important;
}
  
#top .top ul.top-menu li {
   background-color: #111 !important;
   
 }
  
#top .top  {
    background-color: #111 !important;
    border-bottom: 1px solid #111;
 }

/* Napis działu */
ul.breadcrumbs { 
margin-bottom: 25px !important;
color: #ccd0d5 !important; }
  
/* Obecnie wybrane miejsce (najnowsze, oczekujace) */
#top .top ul.top-menu li>a:not(.profile).active {
border-bottom: 1px solid #ffffff !important;
}

.content-info .description {
background: #111 !important;
margin: 0px !important;
}

#infobox.error {
background: #111 !important;
}
  
#container #container-right .box {
display: none !important;
height: 0px !important;
}
    
#footer, footer {
display: none !important;
}
    
/* Przyciski spam i +  itp*/
section[role=listing] article>.content-info>.content-actions a.btn-action.red, section[role=listing] article>.content-info>.content-actions a.btn-plus,
section[role=listing] article .locked>a,

.inside form textarea,
a.btn-next-page,
.paginator a:hover,
.paginator span,
.paginator .scroll_knob,
.data-form input[type=submit],
button,
.control-field input[type=submit]
 {
background: #1f1f1f !important;
border: none !important;
border-radius: 5px !important; 
}


section[role=quick-create] .inside form .mature label,
section[role=quick-create] .inside form section[role=upload-categories] input[type=checkbox]+label, section[role=quick-create] .inside form section[role=upload-categories] input[type=radio]+label {
background: #1f1f1f !important;
}

section[role=quick-create] .inside form .mature input[type=checkbox]:checked+label {
background: #540000 !important;
}

a.random {
background: #111 !important; }
    
nav ul li.header.red, header .sub-menu li strong.red {
color: #8e8e8e !important;
}
    nav ul li a.active {
padding: 2px 3px 3px !important;
border-radius: 5px !important;
border: 1px solid #ffffff;
}
    a {
color: white !important;
}

/*Aktywnie wybrana sekcja */
#container ul.top-choice li a.active ,
section[role=soundboard]>section[role=listing] ul.nav li.active a {
color: white !important;
background: #353535 !important;
border-radius: 5px !important; 
border: 1px double #ffffff;
}

/*Aktywne sekcje bez okraglego obramowania */
section[role=settings] .tabs>ul>li.active>a,

section[role=user-profile-media] .tabs>ul>li.active>a {
background: #353535 !important; }

section[role=settings] .tabs>ul>li>a {
color: white !important;
background: #1f1f1f !important;
}
section[role=user-profile-box] ul.nav li a, .btn.btn-dark {
background: #111 !important; }

section[role=comments]>header .switch a.active {
background: #353535 !important;
padding: 2px 6px 3px !important;
border-radius: 5px !important;
margin-right: 10px !important; }

section[role=comments]>header .switch a {
background: #1f1f1f !important;
padding: 2px 6px 3px !important;
border-radius: 5px !important; 
margin-right: 10px !important; }

#container ul.top-choice li a {
color: white !important;
background: #1f1f1f !important;
border-radius: 5px !important; 
}

/* Czcionka w polach tekstowych */
span, span.error, section[role=user-sign-box] .box-sign .form-group input, section[role=user-sign-box] .box-sign .form-group select  {
color: white !important;
}

.btn-no, .btn-red {
background: #222 !important;
}

#infobox.ok {
background: #222 !important;
}

/*Powiadomienie cookies*/
#cookies-politics, div.openbutton_popup--2R2cF{
display: none !important;
}

/*Reklamy*/
  
div#adMapper13.optAdIns, div#adMapper12.optAdIns, ins.adsbygoogle,  div#adMapper11.optAdIns,
div.adbox, .nextoad.topv, div.nextoad, nextoad, article.dads .doublead, div.sky-inner {
display: none !important;
}

/* Inne*/ 
section[role=user-profile] .inside>.informations>.score a {
background: #222 !important;
}

section[role=user-profile],
.informations header {
background: #111 !important;
}

.data-form input[type=password], .data-form input[type=text], .data-form select {
background: #1f1f1f !important;
border: 1px solid !important;
color: white !important;
border-color: #111 !important;
}


/* Zarządzanie tagami */
section[role=tags] {
background: #111 !important;
}

/*Soundbar*/
.btn-plus, 
section[role=soundboard]>section[role=listing] article .play a span,
.btn, 
section[role=soundboard] section[role=create-soundboard] .form-group label+.upload input[type=file]+span {
background: #1f1f1f !important }


section[role=soundboard]>section[role=listing] article,
section[role=soundboard]>section[role=listing] article .play a,
section[role=soundboard]>.search,
section[role=soundboard] section[role=create-soundboard] {
background: #111 !important ;}


section[role=settings] .tabs>.contents {
border: none !important; 
background: #111 !important ;}

.locked {
background: #111 !important; }

/*Logo facebook*/
section[role=user-sign-box] .box-sign .button-group a.fb {
background: #444 url(https://i.imgur.com/xEmFZQk.png) no-repeat 50%
!important; }

/*Tło komentarzy*/

section[role=comments]>.list, 
section[role=comments]>header,
section[role=comments]>.list article .inside header>.score>span.vote {
background: #111 !important;
}


#container ul.top-choice li a:hover {
background: #333 !important;
}

/* Kolory sekcji logowanie i rejestracji */
section[role=user-sign-box] .tabs>ul>li.active>a{
background: #262626 !important; }

section[role=user-sign-box] .tabs>ul>li>a {
background: #1f1f1f !important; }


/* Blokowanie okna RODO */
.app_gdpr--SPx18r {
display: none !important; }
  
/* mikroblog */
.nav:after, .nav:before { display: none !important; }
header .navbar, header .sub-menu, ul.nav { background: #111 !important; }
.post .wrapper, .comments, .comments .add-new , .new-post{ background: #111 !important; }
.user-account .middle .box, .user-account .middle .footer , .comments .add-new .control-field .file .btn  { background: #222 !important;  border:none !important;
border-radius: 5px !important; }
.user-account .middle { background: #111 !important; }
.post .content, .user-bar { background: #111 !important; }
.markdown .editor ul li a:not(.cancel) { background-color: #222 !important; }
header .navbar #navbar .nav li.mikroblog:before { display:none !important; }
header .navbar #navbar .nav li a:after, main .nav-tabs li a:after { background: white !important; }
label.checkbox input[type=checkbox]:checked+span:before, label.checkbox input[type=radio]:checked+span:before { background: white !important; }
.btn.ion-minus, .btn.ion-plus { background: #111 !important; }
#top>.top ul.top-menu li a.mikroblog:before { display:none !important; }
.error-info  { background: #333 !important; }
a.mention { color: #c23d3a !important; }
.comments .comment-list .comment { border-top: 0px solid hsla(198,4%,45%,.5);
}
.user-bar { border-top: 1px solid hsla(198,4%,45%,.5);
}
.action-tip a.btn-action.follow, .tag-tip a.follow {background: black !important; }
main .nav-tabs { border-bottom: 0px solid #4a4a4a !important;
    margin-bottom: 100px !important; }
.atwho-wrap .atwho-view .atwho-ul .atwho-li.atwho-cur { background: #111 !important; }

/* Tło Ankiety */
.survey ul li span.percent { background: #555 !important; }

/* Rozwijany pasek z kategorami */
#top>.top ul.top-menu li.categories>ul, header .navbar #navbar .nav li.categories>ul {
background: #111 !important; border: 1px solid #ffffff !important; border-radius: 15px !important; }

/* Tło NSFW */
#top>.top ul.top-menu li.categories>ul>li.header.red, header .navbar #navbar .nav li.categories>ul>li.header>strong.red {
color: #6e7578 !important; }

main .nav-tabs li a.btn-range.active {
background: #333 !important; border-radius: 5px !important;  }


#top .top ul.top-menu li>a:not(.profile):hover { border-bottom: 1px solid #ffffff !important; }

/* Przyciski do komentarzy +/- */
.user-bar .counter .btn.ion-plus.active { background: #94b424 !important; 
border-radius: 9px !important; }

.user-bar .counter .btn.ion-minus.active { background: #801614d4 !important; 
border-radius: 9px !important; }

}
