css:
@-moz-document domain("vk.com") {
    /* Main body */
    
    body {
        background: #d7e2ec;
    }
    #side_bar ol {
        background-color: #e8edf4;
        border: 6px solid #e8edf4;
    }
    .audio_rows_header {
        width: 550px!important;
    }
    /* inner player */
    
    .audio_row {
        background: #dddedf!important;
        padding: 1px 0!important;
    }
    .audio_row .audio_row_content {
        background: #e9edf1!important;
    }

    .audio_row.audio_row__current,
    .audio_row.audio_row__playing,
    .audio_row.audio_row__current:hover,
    .audio_row.audio_row__playing:hover,
    .audio_row.audio_row__current .audio_row_content {
        background-color: #507299 !important;
        color: #eee!important;
    }

    .audio_row.audio_row__current .audio_row__performer,
    .audio_row.audio_row__current.audio_has_lyrics .audio_row__title_inner,
    .audio_row.audio_row__current .audio_row__lyrics_inner {
        color: #eee!important;
    }
    .audio_play {
        width: 22px;
        height: 22px;
        border-radius: 25%;
        background-position: 7px 6px;
    }
    .audio_row_playing .audio_play {
        background-position: 7px -16px;
    }
    .audio_row .audio_acts .audio_act {
        padding: 2px 2px;
    }
    /* Header player */
    
    .audio_page_player .audio_page_player_play .icon {
        position: relative;
        width: 24px;
        height: 24px;
        border-radius: 25%;
        background-position: -2px -2px !important;
        left: 2px;
        top: 1px;
    }
    .audio_page_player .audio_page_player_play.audio_playing .icon {
        background-position: -2px -60px !important;
    }
    .slider_back {
        width: 100%!important;
    }
}
