css:
@-moz-document domain(coinigy.com) {
    .tv-side-toolbar, body, .header-chart-panel {
        background: #131721 !important;
    }

    .tv-close-panel.top, .tv-close-panel.left {
        background-color: #131721;   
    }

    .header-chart-panel {
        border: none;
    }

    .header-chart-panel .button, .favored-list-container span.submenu, .favored-list-container span.submenu, .favored-list-container .quick span:first-child, .header-chart-panel .favored-list-container span {
        border: 1px solid #313947;
        background: #131721;
    }

    .header-chart-panel .button.disabled {
        border: 1px solid #313947 !important;
        background: #131721 !important;
    }

    .chart-controls-bar {
        background: #131721;
    }

    .chart-page .chart-container, .header-chart-panel, #library-container, .tv-side-toolbar {
        border: 1px solid #313947 !important; ;   
    }
}
