css:
@-moz-document regexp("https://(intra|prepintra)\\.etna-alternance\\.net/.*") {
html{background-color: #333;}
body, html body, a{background-color: transparent; color: #efefef;}

/* lox opacity */
.vjs-default-skin .vjs-big-play-button{left: 43.5%; top: 39.5%;}
.label-success,
.label-danger,
.panel-success>.panel-heading,
img{opacity:.5;}

.video-js .vjs-tech,
video,
img{opacity:.75;}

/*.label-danger{background-color: #7b4846;}*/

/* menu */
.navbar-default .navbar-collapse,
.navbar-default .navbar-form,
.navbar,
.dropdown-menu,
.panel,
.panel-footer,
.projet .jumbotron
{
    background-color: #3f3f3f;
    border-radius: 0;
    border: none;
    box-shadow:none;
}

hr,
.dropdown-menu .divider,
.divider
{
    background-color: transparent;
    color: transparent;
    border: none;
}

.form-control,
.table-striped>tbody>tr:nth-of-type(odd),
.md-editor>textarea,
.breadcrumb
{
    background-color: rgba(255, 255, 255, .1);
    border-color: rgba(255, 255, 255, .1);
}

.panel-info>.panel-heading,
.panel-default>.panel-heading,
 .md-editor>.md-header
{
    background-color: rgba(255, 255, 255, .1);
    border: none;
    border-radius: 0;
    color: inherit;
}

.list-group-item{background-color: transparent;}

.btn-primary,
.btn-default,
.md-editor
{
    border: none;
    border-radius: 0;
}
/*.form-control::placeholder
{
    color: rgba(255, 255, 255, .1);
    color: #6ec4d8;
}*/


.navbar-default .navbar-brand
{
    font-weight: bold;
    letter-spacing: 6px;
}

/* text main color */
.navbar-default .navbar-toggle,
.navbar-default .navbar-brand,
.navbar-default .navbar-nav>li>a,
.navbar-default .navbar-text,
.dropdown-menu>li>a,
.ui-select-bootstrap .ui-select-choices-row>a
{
    color: #6ec4d8;
}

.navbar-default .navbar-toggle{border-color: #6ec4d8;}

.navbar-default .navbar-toggle:hover,
.navbar-default .navbar-toggle:focus,
.well
{
    border-color: #fff;
    background-color: inherit;
}

.table>tbody>tr.danger > td,
.table>tbody>tr.active > td,
.table>tbody>tr.success > td
{
    background-color: #3f3f3f;
}

svg,
.grid-achievement
{
    display: none;
}


/*hover*/
.navbar-default .navbar-toggle:focus,
.navbar-default .navbar-toggle:hover,
.navbar-default .navbar-brand:focus,
.navbar-default .navbar-brand:hover,
.navbar-default .navbar-nav>li>a:hover,
.navbar-default .navbar-nav>.open>a,
.navbar-default .navbar-nav>.open>a:focus,
.navbar-default .navbar-nav>.open>a:hover,
.navbar-default .navbar-nav>.active>a,
.navbar-default .navbar-nav>.active>a:hover,
.navbar-default .navbar-nav>.active>a:focus,
.nav>li>a:focus,
.nav>li>a:hover,
.dropdown-menu>li>a:focus,
.dropdown-menu>li>a:hover,
.btn-default:active:focus,
.btn-default:active:hover,
.btn-default:focus,
.btn-default:active,
.btn-default:hover,
.btn-default-focus,
.btn-default,
.btn-primary,
.table-hover>tbody>tr.success:hover>td,
.table-hover>tbody>tr:hover,
.well:hover
{
    color: #fff;
    background-color: rgba(110, 196, 216, 0.5);
}

.ui-select-bootstrap>.ui-select-choices,
.ui-select-bootstrap>.ui-select-no-choice {
    width: inherit;
    max-height: inherit;
    	position: initial!important;
}
}
