/*Interpretaci√≥n de Firefox del dominio a aplicar*/
@-moz-document domain(adas.uclm.es) {
	
  	body {
    	background: url('http://www.dondeviajamos.com/wp-content/uploads/2016/01/Molinos.jpg');
    	background-repeat: no-repeat;
    	background-attachment: fixed;
    	background-position: center;
    	background-size: cover;
		font: 14px arial,helvetica,verdana,sans-serif;
		text-align: center;
      	width: 100%;
  }

	#cream_bar_uclm {
		background-color: rgba(255, 255, 255, 0)!important;
		width: 100%;
		height: 0px!important;
		z-index: 2;
		position: relative;
		text-align: left;
    }

	#blue_bar{
		background-color: rgba(102, 102, 102, 0);
		height: 0;
		width: 100%;
    }

	#f_blue_bar_uclm{
		background-color: rgba(102, 102, 102, 0);
		height: 16px;
		width: 100%;
		margin-top: none;
    }

	#main_uclm {
		width: 993px;
		margin: 0 auto;
		text-align: left;
		margin-top: -110px;
	}

	#content_uclm h1 {
		color: #fff;
		margin-top: 40px;
		margin-bottom: 2px;
		padding-bottom: 5px;
		background-color: rgba(0,0,0,0);
    }

	#content_uclm h5 {
		margin-bottom: none;
		margin-bottom: 10px;
		padding-top: 0px;
		color: #d2d2d2;
		font-size: 16px;
		margin-top: 5px;
    }

	.linea_uclm {
    	border-bottom: solid 4px rgba(236, 131, 81, 0);
    	margin-top: 70px;
    	margin-bottom: 30px;
	}

	#userpass div{
		margin-bottom: 20px;
		color: white;
    }

	#par_uclm {
		border-right: solid 3px #f8933a00;
		padding-right: 10px;
	}
  
	h2 {
		color: rgba(0,0,0,0);
		font-size: 15px;
		text-transform: uppercase;
		text-align: center;
    }

	h2::before {
		content: "";
		color: rgb(255, 252, 0);
		font-size: 13px;
		text-transform: none;
		text-align: center;
    }
  
	.more_info_button_cl_uclm a {
		font-size: 17px;
		color: #fff;
		font-weight: bold;
		float: right;
		margin-top: 35px;
		margin-right: 25px;
	}

	.notes_texts_uclm {
		position: absolute;
		top: 650px;
		width: 740px;
    }

	.text-question, p {
		color: rgba(224, 224, 224, 0);
		font-size: 10pt;
		margin-bottom: .3em;
	}
  
	img {
    	border: 0;
    	opacity: 0;
    	filter: alpha(opacity=0);
    }
  
	#nota1 #nota2 {
		color: #fff;
		font-size: 17px;
	}
}
