css:
@-moz-document domain(campusvirtual.uclm.es) {
body {
        background-image: url('https://wallpapercave.com/wp/cODXmz0.jpg');
        background-repeat: no-repeat;
        background-attachment: fixed;
        background-position: center;
        background-size: cover;
        color: #ff6161;
        font-family: "Open Sans", "Helvetica Neue", Helvetica, Arial, sans-serif;
    }
    /* Navegación*/
    
	.navbar-light {
   		background-color: rgba(152, 152, 152, 0.8);
   	 	border-bottom: 1px solid rgba(20, 20, 20, 0);
	}
    /*********	Color Recuadro	********************/
    
    .card,
    #page-enrol-users #filterform,
    .que .history,
    .userprofile .profile_tree section,
    .groupinfobox,
    .forumpost,
    .yui-skin-sam .yui-layout.path-mod-chat-gui_ajax .yui-layout-unit-right,
    .yui-skin-sam .yui-layout.path-mod-chat-gui_ajax .yui-layout-unit-bottom,
    .yui-skin-sam .yui-layout.path-mod-chat-gui_ajax #chat-messages .chat-message.course-theme,
    .well {
        position: relative;
        display: block;
        margin-bottom: .75rem;
        background-color: rgba(0, 0, 0, 0.73);
        border-radius: 0;
        border: 0px solid rgba(238, 238, 238, 1);
    }
    /*********	Color nombre	********************/
    
    .page-context-header .page-header-headings h1 {
        color: #ffffff;
    }
    /*********	Bloque asignaturas	*********/
    
    .block,
    #dockeditempanel .dockeditempanel_content {
        background-color: rgba(5, 8, 10, 0);
    }
    /*********	Bloque derecha	*********/
    
    aside#block-region-side-pre {
        background: rgba(4, 4, 6, 0.73);
    }
    /*********	Texto Asignaturas	*********/
    
    a {
        color: #FFF;
        text-decoration: none;
    }
    a:hover {
        /*Para cuando pasa por encima el raton*/
        
        text-decoration-style: wavy;
        color: #ff6161;
        font-weight: 200;
    }
    /*********	Color cursos	*********/
    
    .unlist,
    .unlist li,
    .inline-list,
    .inline-list li,
    .block .list,
    .block .list li,
    .section li.activity,
    .section li.movehere,
    .tabtree li {
        list-style: none;
        margin: 0;
        color: #5fc7fb;
        padding: 0;
    }
    /*********	En pagina de entrega de trabajos	*********/
    
    .table-striped tbody tr:nth-of-type(odd),
    table.collection tbody tr:nth-of-type(odd),
    table#explaincaps tbody tr:nth-of-type(odd),
    table#defineroletable tbody tr:nth-of-type(odd),
    table.grading-report tbody tr:nth-of-type(odd),
    table#listdirectories tbody tr:nth-of-type(odd),
    table.rolecaps tbody tr:nth-of-type(odd),
    table.userenrolment tbody tr:nth-of-type(odd),
    form#movecourses table tbody tr:nth-of-type(odd),
    #page-admin-course-index .editcourse tbody tr:nth-of-type(odd),
    .forumheaderlist tbody tr:nth-of-type(odd),
    table.flexible tbody tr:nth-of-type(odd),
    .generaltable tbody tr:nth-of-type(odd):hover {
        background-color: rgba(158, 31, 31, 0.92);
    }
    td,
    th {
        display: table-cell;
        vertical-align: inherit;
        background-color: #334c61;
    }
    th,
    td:hover {
        display: table-cell;
        vertical-align: inherit;
        background-color: rgba(158, 31, 31, 0.92);
    }
    /*********	Footer	*********/
    
    .bg-inverse {
        color: #f3f2f2!important;
        background-color: rgba(0,0,0, 0.7)!important;
    }
    #page-footer a {
        color: #ff6161;
    }

	#page-footer {
    	min-height: 70px;
    	text-align: left;
	}
}
