css:
@-moz-document domain("duckduckgo.com") {
.btn.btn--primary {
    color: #f5f5f5 !important;
}
.header--aside__msg {
    color: #f5f5f5 !important
}
.header--aside__twitter {
    color: #64b5f6 !important
}
.btn--icon,
.header__button--menu,
.no-touch .btn--icon,
.no-touch .header__button,
.no-touch .header__button--menu {
    color: #f5f5f5 !important
}
.nav-menu__heading {
    color: #ffd600 !important
}
.nav-menu__icon,
.nav-menu__close,
.nav-menu__item,
.nav-menu__item--secondary,
.nav-menu__item > a,
.nav-menu__item--secondary > a,
.nav-menu__item > a:visited,
.nav-menu__item--secondary > a:visited {
    color: #f5f5f5 !important
}
.feedback-btn__send {
    color: #f5f5f5 !important
}
.feedback-btn {
    background-color: #7cb342 !important
}
.feedback-prompt__link {
    color: #f5f5f5 !important
}
.module__toggle {
    color: #f5f5f5 !important
}
.js-about-module-more {
    background-color: #7cb342 !important
}
.js-about-module-less {
    background-color: #7cb342 !important
}
.module {
    border: 1px solid #C62828;
    border-bottom-color: #C62828;
    background-color: #d32f2f !important
}
.about-info-box__info-row,
.about-info-box__link-row {
    border-top: 1px solid #f5f5f5;
}
.frm__label {
    color: #ffd600 !important
}
.frm__desc {
    color: #f5f5f5 !important
}
.is-checked .btn.frm__switch__label {
    color: #f5f5f5 !important;
    background-color: #7cb342 !important
}
.btn.frm__switch__label {
    color: #f5f5f5 !important;
}
.set-bookmarklet__input,
.set-bookmarklet__data,
.btn,
.btn:visited,
.btn:hover,
.btn.is-active,
.btn:active,
.btn.btn--primary,
.btn--primary:hover,
.frm__input {
    color: #f5f5f5 !important;
    background-color: #7cb342 !important;
    border-color: #7cb342 !important;
}
.set-bookmarklet__title {
    color: #f5f5f5 !important;
}
.set-bookmarklet__detail {
    color: #f5f5f5 !important;
}
.tag-home__link {
    color: #ffd600 !important
}
.zci,
.zci::before,
.tileview .metabar--fixed,
.tileview--grid .metabar--fixed,
.tileview--grid .metabar--fixed.is-stuck {
    background-color: #C62828 !important;
    border-bottom-color: #c72a2a;
}
.zcm__link {
    color: #f5f5f5 !important;
}
.zci__body,
.zci__detail,
.zci__caption,
.zci__header,
.zci__body h1,
.zci__detail h1,
.zci__body h2,
.zci__detail h2,
.zci__body h3,
.zci__detail h3,
.zci__body h4,
.zci__detail h4,
.zci__body h5,
.zci__detail h5,
.zci__body h6,
.zci__detail h6,
.metabar,
.metabar__dropdowns .dropdown .dropdown__button,
.c-detail__title,
.c-detail__title__sub,
.c-detail__desc,
.c-detail__filemeta,
.c-detail__more,
.c-detail__user,
.c-detail__count,
.c-detail__date,
.c-info,
.c-info__title,
.c-icon,
.c-icon__title,
.c-icon__content,
.c-score,
dropdown__selected,
dropdown__selected:hover,
dropdown__selected:focus,
.metabar__primary-text--linked,
.module {
    color: #f5f5f5 !important;
}
.zci__subheader,
.zci__header__sub,
.c-info__sub,
.c-info__title__sub,
.c-icon__sub,
.c-score__head,
.c-score__foot,
.text--secondary,
.tx-clr--grey-dark,
.tx-clr--grey-light,
.tx-clr--grey,
.tx-clr--lt,
.tx-clr--lt2,
.tx-clr--lt3,
.place-detail__subtitle {
    color: #f5f5f5 !important;
}
.attribution--link__icon {
    color: #f5f5f5 !important;
}
.search,
.search--adv {
    background-color: #8f1e1e;
    border-color: #8f1e1e;
    color: #f5f5f5 !important;
}
.search__input,
.search__input--adv {
    color: #f5f5f5 !important;
}
.search__clear,
.search__button,
.search--home.has-text .search__clear {
    color: #f5f5f5 !important;
}
.modal--dropdown .modal__list__link__description {
    color: #f5f5f5 !important;
}
.modal__list__link {
    color: #ffd600 !important
}
}
