css:
@-moz-document domain("youtube.com") {
/*Homepage*/

#columns.ytd-watch-flexy {
    background: none !important;
}

ytd-page-manager > .ytd-page-manager , ytd-watch-flexy[flexy_][flexy-v2_][is-two-columns_] #columns.ytd-watch-flexy {
    background-color: #092436 !important;
}
#channel-header.ytd-c4-tabbed-header-renderer {
    background-color: #092436 !important;
}
#channel-title.ytd-c4-tabbed-header-renderer , #subscriber-count.ytd-c4-tabbed-header-renderer{
    color: #8924aa !important;
}
#tabs-inner-container.ytd-c4-tabbed-header-renderer {
    background-color: transparent !important;
}
#tabs-container.ytd-c4-tabbed-header-renderer {
  background-color: #092436 !important;
}
.arrow.yt-horizontal-list-renderer {
    background-color: #773599 !important;
}

/*color or #9d00ff for title */
#title.ytd-shelf-renderer, paper-tab:not(.iron-selected) > .tab-content.paper-tab , #video-title.ytd-video-renderer ,#video-title.ytd-grid-playlist-renderer,#title.ytd-grid-channel-renderer, .title.ytd-video-primary-info-renderer, #video-title.ytd-compact-video-renderer, #video-title.ytd-grid-radio-renderer{
    color: #7a2eff !important;
}
#video-title.yt-simple-endpoint.ytd-grid-video-renderer, #video-title.ytd-compact-radio-renderer {
    color: #9d00ff !important;
}
a.yt-simple-endpoint.yt-formatted-string , #thumbnail-attribution.ytd-grid-channel-renderer, span.yt-view-count-renderer, .description.ytd-video-secondary-info-renderer yt-formatted-string.ytd-video-secondary-info-renderer , .count-text.ytd-comments-header-renderer, #byline.ytd-video-meta-block{
    color: wheat !important;
}
#metadata-line.ytd-grid-video-renderer {
    color: #f5deb3b3 !important;
}

paper-button * {
    color: wheat !important;
}
ytd-button-renderer.style-destructive[is-paper-button] {
    background-color: #541066 !important;
}
paper-tab.iron-selected.ytd-c4-tabbed-header-renderer {
    color: #b181ff !important;
}

/*left tab homepage*/
#guide-inner-content.ytd-app , ytd-button-renderer #button.ytd-button-renderer , paper-button.yt-next-continuation {
    background-color: #2b0a3c !important;
}
.title.ytd-guide-entry-renderer {
    color: wheat !important;
}
#guide-section-title.ytd-guide-section-renderer ,  #label-text.yt-dropdown-menu {
    color: #9b8151 !important;
}
#sections.ytd-guide-renderer > .ytd-guide-renderer:not(:last-child), #guide-links-primary.ytd-guide-renderer {
    border-bottom: 1px solid !important;
    border-color: #610789 !important;
}


/*video*/
ytd-watch {
    background: #092436 !important;
}
ytd-masthead {
    background: black !important;
}
#content-text.ytd-comment-renderer {
    color: #cfce00 !important;
}

#author-text.yt-simple-endpoint.ytd-comment-renderer {
    color: #fea704 !important;
}

#metadata-line.ytd-video-meta-block span.ytd-video-meta-block , .date.ytd-video-secondary-info-renderer , #icon-label.yt-dropdown-menu, #vote-count-left.ytd-comment-action-buttons-renderer , ytd-toggle-button-renderer.force-icon-button a.ytd-toggle-button-renderer , #simplebox-placeholder.ytd-comment-simplebox-renderer, #upnext.ytd-compact-autoplay-renderer, yt-formatted-string.ytd-guide-signin-promo-renderer{
    color: #aa9c80 !important;
}
yt-icon, .yt-icon-container.yt-icon {
    fill: #aa9c80 !important;
}

#placeholder-area.ytd-comment-simplebox-renderer , ytd-video-secondary-info-renderer , ytd-video-primary-info-renderer , ytd-compact-autoplay-renderer {
    border-color: #5c00ff !important;
}

/*searchbox*/
ytd-searchbox[mode="legacy"] #container.ytd-searchbox input.ytd-searchbox, ytd-searchbox[mode="legacy-centered"] #container.ytd-searchbox input.ytd-searchbox {
    color: #f7ff00 !important;
}
ytd-searchbox[mode="legacy"] #container.ytd-searchbox, ytd-searchbox[mode="legacy-centered"] #container.ytd-searchbox {
    color: #8d8816 !important;
}
ytd-searchbox[mode="legacy"] #container.ytd-searchbox, ytd-searchbox[mode="legacy-centered"] #container.ytd-searchbox {
    background-color:  #170521  !important;
    box-shadow: none !important;
    border-color: #4c07d7  !important;
}
#search-icon-legacy.ytd-searchbox {
    border: 0px solid !important;
    background-color: #2b0a3c !important;
}

#contents.ytd-section-list-renderer > .ytd-section-list-renderer:not(:last-child) {
    border-bottom: 1px solid !important;
    border-color: #77009e !important;
}

.sbsb_a {
    background: #170521 !important;
}
.sbdd_b {
    border-color: #3a0798  !important;
}
.sbsb_d {
    background: #2b0a3c !important;
    color: #ca00ff !important;
}
.gsfs {
    color: #d9ff00;
}

/*playlist*/
.header.ytd-playlist-panel-renderer {
    background-color: #2b0a3c !important;
}
.playlist-items.ytd-playlist-panel-renderer {
    background-color: #27125080 !important;
}
#video-title.ytd-playlist-panel-video-renderer {
    color: #d091ff !important;
}

/*update*/
#name.ytd-author-comment-badge-renderer {
    color: #fea704 !important;
}
#vote-count-middle.ytd-comment-action-buttons-renderer {
    color: #8948ff !important;
}
#result-count.ytd-search-sub-menu-renderer , #filter-group-name.ytd-search-filter-group-renderer, ytd-search-filter-renderer yt-formatted-string.ytd-search-filter-renderer{
    color: #8a30ff !important;
}
#channel-title.ytd-channel-renderer span.ytd-channel-renderer , #description.ytd-channel-renderer , #metadata.ytd-channel-renderer{
    color: #a71bc9 !important;
}
#filter-group-name.ytd-search-filter-group-renderer , ytd-search-sub-menu-renderer , #description-container.ytd-channel-about-metadata-renderer, #description-container.ytd-channel-about-metadata-renderer, #bio-container.ytd-channel-about-metadata-renderer, #photos-container.ytd-channel-about-metadata-renderer, #details-container.ytd-channel-about-metadata-renderer, #links-container.ytd-channel-about-metadata-renderer,#right-column.ytd-channel-about-metadata-renderer .subheadline.ytd-channel-about-metadata-renderer, #message.ytd-message-renderer, #description.ytd-channel-about-metadata-renderer, #bio.ytd-channel-about-metadata-renderer, .subheadline.ytd-channel-about-metadata-renderer, #right-column.ytd-channel-about-metadata-renderer > yt-formatted-string.ytd-channel-about-metadata-renderer{
    border-color: #998a18 !important;
}
#message.ytd-message-renderer , #description.ytd-channel-about-metadata-renderer, #bio.ytd-channel-about-metadata-renderer, .subheadline.ytd-channel-about-metadata-renderer , #right-column.ytd-channel-about-metadata-renderer > yt-formatted-string.ytd-channel-about-metadata-renderer, yt-formatted-string.ytd-sub-feed-option-renderer {
    color: #bcbc1f !important;
}
ytd-app {
    background-color: #092436 !important;
}
#title.ytd-sub-feed-selector-renderer, ytd-sub-feed-option-renderer.ytd-sub-feed-selector-renderer {
    color: #ba9bff  !important;
}
#title.ytd-sub-feed-selector-renderer, ytd-sub-feed-option-renderer.ytd-sub-feed-selector-renderer {
    border-color: #ce02dd !important;
}
a.yt-simple-endpoint.ytd-button-renderer {
    color: wheat  !important;
}
corrected.yt-search-query-correction, .italic.yt-formatted-string, .content-text.ytd-metadata-with-image-row-renderer:first-child , .content-text.ytd-metadata-with-image-row-renderer:not(:first-child), #corrected.yt-search-query-correction {
    color: yellow  !important;
}

#video-title.ytd-radio-renderer , #video-title.ytd-playlist-renderer{
    color: #b817ff !important;
}
#video-title.ytd-child-video-renderer, #length.ytd-child-video-renderer {
    color: #a187bd !important;
}
a.ytd-search-refinement-card-renderer {
    border: none;
    background-color: #36157a  !important;
}
#card-title.ytd-search-refinement-card-renderer {
    color: wheat  !important;
}
.arrow.ytd-horizontal-card-list-renderer, [arrow].ytd-horizontal-card-list-renderer {
    background-color: #531b71  !important;
}
ytd-watch-card-compact-video-renderer[is-condensed] {
    border-bottom-color: #716e20 !important;
}
#description.ytd-channel-video-player-renderer {
    color: #9031c1 !important;
}
canvas.ytd-section-list-renderer, caption.ytd-section-list-renderer, center.ytd-section-list-renderer, cite.ytd-section-list-renderer, code.ytd-section-list-renderer, dd.ytd-section-list-renderer, del.ytd-section-list-renderer, dfn.ytd-section-list-renderer, div.ytd-section-list-renderer, dl.ytd-section-list-renderer, dt.ytd-section-list-renderer, em.ytd-section-list-renderer, embed.ytd-section-list-renderer, fieldset.ytd-section-list-renderer, font.ytd-section-list-renderer, form.ytd-section-list-renderer, h1.ytd-section-list-renderer, h2.ytd-section-list-renderer, h3.ytd-section-list-renderer, h4.ytd-section-list-renderer, h5.ytd-section-list-renderer, h6.ytd-section-list-renderer, hr.ytd-section-list-renderer, i.ytd-section-list-renderer, iframe.ytd-section-list-renderer, img.ytd-section-list-renderer, ins.ytd-section-list-renderer, kbd.ytd-section-list-renderer, label.ytd-section-list-renderer, legend.ytd-section-list-renderer, li.ytd-section-list-renderer, menu.ytd-section-list-renderer, object.ytd-section-list-renderer, ol.ytd-section-list-renderer, p.ytd-section-list-renderer, pre.ytd-section-list-renderer, q.ytd-section-list-renderer, s.ytd-section-list-renderer, samp.ytd-section-list-renderer, small.ytd-section-list-renderer, span.ytd-section-list-renderer, strike.ytd-section-list-renderer, strong.ytd-section-list-renderer, sub.ytd-section-list-renderer, sup.ytd-section-list-renderer, table.ytd-section-list-renderer, tbody.ytd-section-list-renderer, td.ytd-section-list-renderer, tfoot.ytd-section-list-renderer, th.ytd-section-list-renderer, thead.ytd-section-list-renderer, tr.ytd-section-list-renderer, tt.ytd-section-list-renderer, u.ytd-section-list-renderer, ul.ytd-section-list-renderer, var.ytd-section-list-renderer , ytd-playlist-sidebar-renderer.ytd-browse{
    background-color: #092436 !important;
    border-color: #6e29a7 !important;
}

#stats.ytd-playlist-sidebar-primary-info-renderer , #video-title.ytd-playlist-video-renderer {
    color: #8924aa !important;
}
#items.ytd-playlist-sidebar-renderer > .ytd-playlist-sidebar-renderer:not(:last-child) {
    border-color: #6e29a7 !important;
}
.content.ytd-video-secondary-info-renderer {
    color: #d4e00c !important;
}
/*update*/
.content.content-line-height-override.ytd-metadata-row-renderer , .content.ytd-metadata-row-header-renderer {
    line-height: 2.1rem;
    color: aqua !important;
}
#items.yt-horizontal-list-renderer > ytd-post-renderer.yt-horizontal-list-renderer {
    background-color: #330a48 !important;
}
ytd-post-renderer[surface_="backstage-surface-type-home"] #home-content-text.ytd-post-renderer {
    color: #909 !important;
}
ytd-post-renderer[surface_="backstage-surface-type-home"][attachment="video"] #content-attachment.ytd-post-renderer ytd-video-renderer.ytd-post-renderer, ytd-post-renderer[surface_="backstage-surface-type-home"][attachment="playlist"] #content-attachment.ytd-post-renderer ytd-playlist-renderer.ytd-post-renderer {
    padding: 0;
    background-color: #230731  !important;
}

/*update*/
ytd-compact-playlist-renderer #video-title.ytd-compact-playlist-renderer {
    color: #896af0 !important;
}

#video-title.ytd-rich-grid-video-renderer , #info.ytd-movie-offer-module-renderer {
    color: #cb49ff !important;
}
#video-title.ytd-rich-movie-renderer {
    color: #b3ff7f !important;
}
#title.ytd-rich-grid-renderer , yt-formatted-string[ellipsis-truncate] , #movie-title.ytd-compact-movie-renderer{
    color: khaki !important;
}
#text.ytd-channel-name , #title.ytd-vertical-channel-section-renderer , .title.ytd-mini-channel-renderer {
    color: fuchsia !important;
}

ytd-movie-offer-module-renderer[watch-color-update] #wide-clickable-area.ytd-movie-offer-module-renderer {
    background-color: #144b6f !important;
}

ytd-two-column-browse-results-renderer[page-subtype="history"] #secondary.ytd-two-column-browse-results-renderer {
    background-color: black !important;
}

iron-input.paper-input > input.paper-input {
    color: white !important;
}

.sbfl_b {
    background: black !important;
}

ytd-post-renderer[is-rich-grid_] {
    background-color: #140e3c !important;
}

}
