css:
@-moz-document domain("tutorialspoint.com") {
/* remove shadows */

h1,
h2,
h3,
h4,
h5 {
    text-shadow: none;
}

/* remove noise */

header,
.middle-col .print-btn,
.topgooglead,
.bottomgooglead,
.footer-copyright,
.scroll-to-top,
.scroll-to-top visible,
.mini-logo,
#rightbar {
    display: none !important;
}

/* reorder content */

div.container > div.row {
    display: table;
}
div.container > div.row > div.col-md-2 + div.row {
    display: table-header-group;
}
div.container > div.row > div.col-md-2 {
    display: table-footer-group;
}

/* resize content */

.middle-col {
    width: 100%;
}

/* restyle content */

.nxt-btn a:link,
.pre-btn a:link {
    padding: 10px 10px 10px 15px;
    outline: 1px dotted silver !important;
    margin: 0 1em;
}
.middle-col hr {
    border: 0 !important;
}
}
