css:
@-moz-document domain("kerkdienstgemist.nl") {
/* Header */
    a {
        color: #222222;
    }
	a:hover {
 		color:#e5e5e5;
        background-color: #222222;
 		text-decoration:none
	}
    a:visited {
        color: #222222;
    }
    ul#utilMenu li a:hover{
        color: #222222;                                        
	}
    .wideHeader a:hover {
        color: #ffffff;
        background-color: #222222;
    }
    #bar-bg,
    header#subNav {
        background-color: #222222;
    }
    ul#mainNav li.selected ul li.selected a {
        background-color: #ffffff;
        color: #222222;
    }
    ul#mainNav li.selected ul li a:hover {
        background-color: #ffffff;
        color: #222222;
    }
    ul#mainNav li.selected a:hover {
        background-color: #ffffff;
    }
    ul#mainNav li.selected ul li.selected a:hover {
        background-color: #ffffff;
    }
    ul#mainNav li.selected a:hover {
        background-color: #222222;
    }
    ul#mainNav li.selected {
        background-color: #222222;
    }
    ul#mainNav li a:hover {
        background-color: #222222;
    }
    #subNav ul li.selected a {
        background: #ffffff;
        color: #222222;
    }
    #mainNav ul li a:hover,
    #subNav ul li a:hover {
        background-color: #ffffff;
        color: #222222;
    }
    #mainNav ul li.selected a:hover,
    #subNav ul li.selected a:hover {
        background-color: #222222;
        color: #e5e5e5;
    }
    #mainNav ul li.selected,
    #subNav ul li.selected {
        background-color: #222222;
        color: #e5e5e5;
    }
/* CSS Body Elements */
    #main-holder {
        /* background-color: #cacaca; */
		background-color:#979797;
    }
    .container ul li.selected a:hover {
        background-color: #fff;
        color: #222;
    }
    .box .favorite-top li a {
        color: #222222;
    }
    .box .favorite-top li a:hover {
        color: #ffffff;
        background-color: #222222;
    }
    .box {
         border-radius: 2px;                                            
         background-color: #fff;                                       
	}
    #rightHolder a:hover{
		color:#e5e5e5;                                        
    }
    #favoriteStationsSidebar{
        background-color: #fff;
		border-radius: 2px;                                            
    }
    .provenceHolder {
		border-radius: 2px;                                        
    }
    .denominations {
        background-color: #fff;
		border-radius: 2px;                                           
    }
    .info_bar_holder {
        background-color: #fff;
        min-height: 110px;
        border:1px solid #e5e5e5;                                            
    }
    #playList ol.recordings li span.titleRecording a:hover {
        color: #222222;
    }
    #playList ol.recordings li span.titleRecording a:hover {
        color: #ffffff;
        background-color: #222222;
    }
    #dialog .button.green,
    body .button.green {
        background-color: #222222!important;
        border-color: #49c55a #39b54a #39b54a #49c55a;
        color: #e5e5e5;
        -moz-border-radius: 20px;
        -webkit-border-radius: 20px;
        border-radius: 20px;
        padding: 10px 15px
    }
    #dialog .button.green:hover,
    body .button.green:hover {
        background-color: #222222!important;
        border-color: #49c55a #39b54a #39b54a #49c55a;
        color: #e5e5e5;
    }
    #dialog .button.green:active,
    body .button.green:active {
        background-color: #222222!important;
        border-color: #49c55a #39b54a #39b54a #49c55a;
        color: #294057
    }
    .search-criteria li label:hover,
    .search-criteria li a:hover {
        background-color: #222222;
        color: #e5e5e5;
    }
    #churchAgenda .monthDays li.selected a {
        background: #222222;
        color: #e5e5e5
    }
    #churchAgenda .monthDays li.selected a:hover {
        background: #222222;
        color: #e5e5e5
    }
    .user_assignments tr th {
        background: #222222;
        text-align: left
    }
    .provenceHolder ul.provenceList li a:hover {
        background-color: #222222;
        color: #e5e5e5
    }
    .provenceHolder ul.provenceList li a.hover {
        background-color: #222222;
        color: #e5e5e5
    }
    #bottomBar ol li a {
        color: #222222;
    }
	#dialog .button.green:hover,body .button.green:hover {
        background-color: #222222;
        color: #e5e5e5;
    }
    .pager a.previous_page, .pager a.next_page {
    	border-color: #222222 #222222 #222222 #000000;
    	color:#222222;
    }
    .floatRight a:hover {
    	color: #e5e5e5       
    }
	.denominations a:hover {
    	color: #e5e5e5       
    }
    #churchAgenda #events li a:hover{
    	color: #000000;                                         
    }
    .info_bar_holder .info_bar_content .favorite .buttonFavorite {
		color:#222222;                                        
    }
    .tabContent{
    	background: #222222;                                    
    	color: #e5e5e5;   
    }
    .tabContent a{
    	background: #222222;                                    
    	color: #ffffff;    
    }
    .tabContent a:visited{
    	background: #222222;                                    
    	color: #ffffff; 
    }
    .tabContent a:hover{
    	background: #222222;                                    
    	color: #e5e5e5;   
    }
    h1.wideHeader {
    	color: #000;                                        
    }
    .tab-box h4{
    	color: #e5e5e5;                                        
    }
    label span.counter {
    	color: #222;
        display:none;
    }
/* CSS Search Results & Criteria */
    .searchResults li.live h3 a, .searchResults li.live p a{
    	color:#222222;                                    
    }
    .searchResults li.live a:hover{
    	background-color: #222222;
    	color: #e5e5e5    
    }
    .searchResults li.offline a:hover {
    	background-color: #222222;
    	color: #e5e5e5    
    }
    .searchResults li .information a{
    	color:#222222;                                         
    }
    .searchResults li .information a:hover{
    	color:#000000;                                         
    }
    .search-criteria li label, .search-criteria li a{
    	color:#222222;                                         
    }
    #search-filter ul#search-mode li{
    	color:#222222;      
    }
    #search-box input.button {
        background-color: #222222;
    }
    #mapBackground{
    	display:none;                                
    }
    #search-results-box {
         border-radius: 2px;                                            
         background-color: #fff;                                       
	}
    .channelImageHolder a:hover {
    	background-color:#fff; 
    }
/* Footer */
    #footer a:hover {
        background-color: #222222;
        color: #e5e5e5;
    }
    #footer .footerHeader a {
        color: #e5e5e5;
    }
    #footer .footerHeader {
        color: #e5e5e5;
    }
}
