css:
@-moz-document domain('users.rust-lang.org'), domain('internals.rust-lang.org') {

html {
    color: #ada8a8;
    background: #414141;
}

#topic-title h1 a {
    color: #626262;
}

.d-header {
    background-color: #363636;
}

.menu-panel.drop-down {
    position: absolute;
    top: 100%;
    right: -10px;
    background: #414141;
}

div.notifications ul li.read {
    background: #414141 !important;
}

li.header-dropdown-toggle a {
    background: transparent !important;
}
li.header-dropdown-toggle a:hover {
    background: rgba(255, 255, 255, .26) !important;
}

a.mention, a.mention-group {
    padding: 2px 4px;
    color: #26899e;
    background: #353434;
    border-radius: 8px;
    font-weight: bold;
    font-size: 0.93em;
}

i.fa.fa-heart.d-icon {
    text-indent: 1px;
    transform: scale(1);
    color: red;
}

i.fa.fa-at.d-icon {
    color: #26899e;
}

i.fa.fa-certificate.d-icon {
    color: #9e8c26;
}

div.cooked p {
    color: #8a8787;
}

li a {
    color: #b4b4b4 !important;
}

.list-controls .category-breadcrumb li.bullet > .dropdown-header {
    background: #363636;;
}

.nav-pills > li.active > a, .nav-pills > li > a.active {
    color: #663a3a;
    background-color: #e45735;
}

.title.raw-link.raw-topic-link {
    color: #ababab !important;
}

div.cooked > aside.quote > div.title {
    background-color: #4a4949 !important;
    border-left: 5px solid #5a5858 !important;
}

p > code, li > code, pre > code {
    color: #7b7a7a !important;
    background: #2f2e2e;
}

span.hljs-keyword {
    color: orange;
}

span.hljs-literal {
    color: #226f22;
}


div.topic-map, div.topic-map *, nav.buttons button {
    background-color: #414141 !important;
}

button {
    color: #b1b1b1 !important;
    background: #5a5a5a !important;
}

blockquote {
    border-left: 5px solid #5a5858 !important;
    background: #515151 !important;
}

div.reply-to, 
div#reply-control, 
div#reply-area, 
div.d-editor-button-bar,
textarea.d-editor-input,
div.d-editor-preview, 
button.btn.no-text.btn-icon.ember-view {
    background-color: #272727 !important;
    color: #a4a4a4;
}

span.badge-category.clear-badge {
	color: #818181 !important; 
}

div.select-box-kit-header.combo-box-header {
    background-color: #414040 !important;
    border-color: black !important;
}

div.select-box-kit-filter, div.select-box-kit-filter *,
ul.select-box-kit-collection,
li.select-box-kit-row,
input#reply-title {
    color: beige !important;
    background-color: #414040 !important;
    border-color: black !important;
}

div.details div.primary {
    background: #363636 !important;
}

.user-stream .item, .user-stream .user-stream-item {
    background-color: #363636 !important;
    border-bottom: 1px solid #e9e9e9;

}

section.controls ul li a.btn {
    border: none;
    color: #8e8d8d !important;
    font-weight: normal;
    background: #363636 !important;
}

.user-main .about .secondary {
    background: #363636;
    border-top: 1px solid #e9e9e9;
    border-bottom: 1px solid #e9e9e9;

}

.extra-info-wrapper .topic-link {
    color: #ababab;
    white-space: nowrap;
}

.topic-list-item.visited a.title:not(.badge-notification), 
.latest-topic-list-item.visited a.title:not(.badge-notification), 
.category-topic-link.visited a.title:not(.badge-notification) {
    color: #646464 !important;
}

div.presence-users {
    background: #363636 !important;
    color: beige;
}

}
