css:
@-moz-document domain("www.dramafever.com") {
/* Dark Background Stuff */

body,
home-view__main container,
.home-view__vertical-card,
.home-view,
.home-view__vertical-panel:after,
.watch-view,
.main,
.view-browse .hero,
.journeys-view,
.login-view {
    background-color: #222222 !important;
    color: #ffffff !important;
}
/* Header and Footer Blue Stuff */

.site-header,
.site-header__top-nav .site-header__link-text,
.site-header .login-legacy,
.site-header .user-legacy,
.dropdown-menu__label,
.site-footer,
.button,
.header-main,
#site-footer,
.view-news .block-rxn .item a,
.header-logo.site-header {
    background-color: #375a7f;
    color: #ffffff;
}
.home-view__section-lifted,
.home-view__vertical-card-info-wrap,
.thumbnail,
.thumbnail__description-container,
thumbnail__watchlist,
thumbnail__description-container,
thumbnail__overlay-description,
.carousel__general-wrapper:after,
.carousel__general-wrapper:before,
.watch-view__episode-container,
.watch-masthead__episode-detail-wrapper,
.show-guide .guide-content .guide-content-section .series,
.watch-masthead__bottom,
th,
.th,
.view-browse .content-sidebar .filter .options,
.breadcrumb,
.view-news .list-articles .article {
    background-color: #464545;
}
.site-header__search-input-legacy,
.reviews__user-profile,
.reviews .input-text__input--textarea,
.reviews__list-user-info,
.reviews__select-rating,
.reviews__list-user-info,
textarea,
input[type="text"],
input[type="password"],
input[type="datetime"],
input[type="datetime-local"],
input[type="date"],
input[type="month"],
input[type="time"],
input[type="week"],
input[type="number"],
input[type="email"],
input[type="url"],
input[type="search"],
input[type="tel"],
input[type="color"],
.uneditable-input,
shelf-block,
.list-tags > li a,
.pages a,
.watch-masthead__actor-detail,
.view-news .news-page-navigation,
.view-news .news-page-navigation:hover,
.form--personalize,
.form--regular,
.form-change-password,
.form-forgotpassword,
.form-login,
.form-payment,
.subscribe-form .form-signup {
    background-color: #464545;
    color: #ffffff;
    border: .0625em solid #222222;
}
.home-view__vertical-card-title,
.home-view__latest-episode-title,
.watch-masthead__episode-detail-title,
.block .section-head,
.show-guide .guide-content .label,
.view-browse .content-sidebar .filter .th {
    color: #00bc8c;
}
a,
.site-footer__link,
.show-guide .guide-nav .days .guide-nav-button.active {
    color: #3498db;
}
.site-footer__signoff,
.site-footer__social-links a,
.site-footer__copyright a,
.home-view__latest-episode-detail,
.reviews__list-username,
.reviews__helper-text,
.thumbnail-actor .thumbnail__description-container,
.thumbnail__title,
.watch-masthead__episode-info-wrapper,
.watch-masthead__episode-meta,
.header-main .menu .level-0 a,
.header-main .username,
.show-guide .guide-content .guide-content-section .info .description,
.show-guide .guide-content .guide-content-section .info,
.view-browse .table .info .genre,
.watch-masthead__episode-description,
.watch-masthead__series-description,
.watch-masthead__series-title,
.watch-masthead__series-info-wrapper,
.watch-masthead__series-details,
.watch-masthead__actor-detail .text__metadata-primary,
.watch-masthead__actor-detail .text__metadata-secondary,
.list-options > li a,
.view-news .list-articles .article h2 a,
.view-news,
.input-text__label {
    color: #FFFFFF;
}
.home-view__callout--browse,
.home-view__apps {
    background-color: #464545;
}
/* That Awkward Gradient Stuff */

.show-guide .guide-nav .days:before,
.home-view__latest-episodes-wrapper:after {
    display: none;
    background: linear-gradient(to right, #222222 0%, #464545 100%)
}
/* Hover Over Table Stuff */

.view-browse .table tr:hover,
.reviews__list-item:hover .reviews__list-user-info {
    background-color: #375a7f;
    color: #ffffff;
}
/* Search Stuff In Browse (This stuff does Nothing Right Now)*/
/* Collapsed And Hover */

.view-browse .content-sidebar .filter.collapsed .th,
.view-browse .content-sidebar .filter .th:hover {
    background-color: #605e5e;
}
/* News Borders */

.view-news .list-featured > li {
    border: .0625em solid #222222
}
}
