css:
@-moz-document domain("book.douban.com"), domain("movie.douban.com") {
.comment-item {
    font-size: 15px;
}
.short-content {
    font-size: 15px;
    line-height: 1.7;
}
.related-info .indent {
    font-size:15px;
}
div.comment-item {
    font-size: 15px;
}
.topics .topic-item {
    font-size:15px !important;
}
.review-list .review-item .main-bd h2 {
    font-size:15px;
}
.review-content {
    font-size: 15px !important;
    background-color: #f8f8f8;
    line-height: 1.7 !important;
}
.intro p {
    font-size:15px !important;
}
span.short {
    font-size: 15px;
    line-height: 1.7;
}
ul.blockquote-list li figure {
    font-size: 15px;
    line-height: 1.7;
}
figure {
    font-size: 15px;
    line-height: 1.7;
}
span.hide-item.full {
    font-size: 15px;
    line-height: 1.7;
}
.main-bd h2 a {
    font-size: 16px;
}}
