/* ==UserStyle==
@name           theatre mode transparent chat- Twitch.tv
@namespace      USO Archive
@author         Vartan Bagdasaryan
@description    `Transparent chat, theater / theater mode.Пишите отзывы пожелания.Feel Free to leave comment about improvements (code examples appreciated).FACEBOOK.com/Vartan.bg`
@version        20230503.21.03
@license        NO-REDISTRIBUTION
@preprocessor   uso
@advanced color chatColorPick "Chat Color (pick)" #FFFFFF
@advanced dropdown chatTrans "Chat transparency" {
part "Partially (~30%)*" <<<EOT #root div.tw-flex div.right-column.right-column--theatre{
background: linear-gradient(to right, rgba(0,0,0,0) 5%,rgba(0,0,0,0.20) 30%,rgba(0,0,0,0.75) 75%)!important;
} EOT;
fully "Fully" <<<EOT #root div.tw-flex div.right-column.right-column--theatre{
background: transparent!important;
} EOT;

}
@advanced dropdown chatSize "Chat size" {
default "Default*" <<<EOT .tw-root--theme-dark .simplebar-content .chat-list__list-container{
font-size: 12px;
}
EOT;
small "Small" <<<EOT .tw-root--theme-dark .simplebar-content .chat-list__list-container{
font-size: 9px;
line-height: 13px
}

.tw-root--theme-dark .right-column--theatre.right-column--beside {
width: 26rem;
}
.tw-root--theme-dark .right-column--beside .channel-root__right-column {
width: 26rem;
} EOT;
medium "Medium" <<<EOT .tw-root--theme-dark .simplebar-content .chat-list__list-container{
font-size: 11px;
line-height: 15px
}

.tw-root--theme-dark .right-column--theatre.right-column--beside {
width: 26rem;
}
.tw-root--theme-dark .right-column--beside .channel-root__right-column {
width: 26rem;
} EOT;

}

==/UserStyle== */
@-moz-document domain(www.twitch.tv) {
