css:
@-moz-document url-prefix("https://cran.r-project.org/"), url-prefix("https://cran.r-project.org/"), domain("cran.r-project.org") {
body,
.raw,
.footer {
  background: #2f2f2f;
  color: lightgrey;
}
.container,
.page {
  padding: 0;
  margin: 0;
  width: 100%;
  font-size: 0.95em;
  line-height: 1.2em;
}
body,
h1,
h2,
h3,
h4,
h5,
td,
div,
span {
  font-family: "IBM Plex Sans", "Roboto", "Helvetica", sans-serif;
}
body,
div {
  font-size: 0.95em;
  line-height: 1.2em;
  padding: 5% 5% 5% 5%;
}
table,
tr,
td,
thead,
tbody,
th {
  border: 0px solid #00000000
}
td a[href*="web/packages"] {
  font-family: monospace
}
li,
dd {
  line-height: 1.1em;
  margin-bottom: 0.5em;
}
dt {
  margin-bottom: 0.1em;
}
a:link {
  background: #2f2f2f;
  color: white;
}
a:visited {
  background: #2f2f2f;
  color: rgb(50%, 0%, 50%);
}
h1 {
  background: #2f2f2f;
  color: rgb(55%, 55%, 55%);
  font-size: x-large;
  text-align: center;
}
h2 {
  background: #2f2f2f;
  color: rgb(40%, 40%, 40%);
  font-size: large;
  text-align: center;
}
h3 {
  background: #2f2f2f;
  color: rgb(40%, 40%, 40%);
  font-size: large;
}
h4 {
  background: #2f2f2f;
  color: rgb(40%, 40%, 40%);
  font-style: italic;
  font-size: large;
}
h5 {
  background: #2f2f2f;
  color: rgb(40%, 40%, 40%);
}
h6 {
  background: #2f2f2f;
  color: rgb(40%, 40%, 40%);
  font-style: italic;
}
}
