css:
@-moz-document domain("pagerduty.com") {

.pd-page-container {
    background-color: #1F293A;
}

.pd-incident-counts-widget {
    background-color: #29374e;
}

body {
    color: #aaa;
}

.pd-incident-counts-widget>table td {
    border-color: #494C55;
}

.pd-incident-counts-widget>table td span.acknowledged {
    color: #de470f;
}

.pd-incident-counts-widget>table td span.triggered {
    color: #e81616;
    /* font-weight: 400; */
}

.pd-incidents-table .pd-incidents-table-header {
    border-color: #494C55;
    background-color: #29374e;
}

.pd-incidents-table .status-filter-list li a.active.open {
    color: #eee;
    border-color: #eee;
}

.pd-incidents-table .status-filter-list li a {
    color: #999;
    border-color: #777;
}

.pd-incidents-table .status-filter-list li a.active.all, .pd-incidents-table .status-filter-list li a:hover.all {
    color: #eee;
    border-color: #eee;
}

.pd-incidents-table .pd-incident-table-actions {
    background-color: #29374e;
    border-color: #494C55;
}

.pd-incidents-table tbody tr td, .pd-incidents-table tbody tr th {
    background-color: #202a3a;
    border-color: #494c54;
    box-shadow: none;
}

a {
    color: #318ccc;
}

a:focus, a:hover {
    color: #6cb4e6;
}

.pd-incidents-table thead tr {
    background-color: #2a384e;
    background-image: linear-gradient(to bottom,#2a384e,#212b3b);
}

.pd-incidents-table th:first-of-type {
    border-color: #494c53;
}

.pd-incidents-table th a {
    color: #aaa;
    text-shadow: none;
    font-weight: 400;
}

.pd-incidents-table .paginator td {
    background-color: #2a384e;
    border-color: #494c53;
    box-shadow: none;
}

.pd-incidents-table .pd-incident-table-actions+table+.pd-incident-table-actions {
    background-color: #2b394e;
    border-color: #494c52;
}


.pd-incidents-table th:last-of-type {
    border-color: #494c53;
}

.pd-incidents-table tr {
    border-right: 1px solid #494C52;
}

label.pd-paginator__limit-select-label {
    color: #aaa;
}

table.pd-fancy-list-table.table.table-bordered.table-striped {
    border-color: #494c52;
}

.pd-fancy-list-table th {
    background: #2c394e;
    background-image: linear-gradient(to bottom,#2a384e,#212b3b);
}

.table-striped tbody>tr:nth-child(even)>td, .table-striped tbody>tr:nth-child(odd)>td, .table-striped tbody>tr:nth-child(odd)>th {
    background-color: #202a3a;
    border-color: #494c52;
}

.pd-log-entries-table .activity {
    border-right: none;
}

.btn, .pd-account-onboarding__control--action button {
    background-color: #222c3b;
    background-image: none;
    color: #eee;
    text-shadow: 0 1px 1px rgba(0, 0, 0, 0.5);
    border-color: #494c53;
    box-shadow: none;
}

.btn:hover {
    background-color: #222;
    color: #fff;
    box-shadow: 0 0 5px 1px rgba(255,255,255,0.1);
}

.btn.active, .btn.disabled, .btn:active, .btn:focus, .btn[disabled], .pd-account-onboarding__control--action button.active, .pd-account-onboarding__control--action button.disabled, .pd-account-onboarding__control--action button:active, .pd-account-onboarding__control--action button:focus, .pd-account-onboarding__control--action button:hover, .pd-account-onboarding__control--action button[disabled] {
    background-color: #1e6fab;
    background-image: none;
    color: #eee;
    text-shadow: 0 1px 1px rgba(0, 0, 0, 0.5);
    border-color: #494c53;
    box-shadow: none;
}

.btn.disabled,.btn.disabled:hover, .btn[disabled], .btn[disabled]:hover {
    cursor: not-allowed;
    color: #aaa;
    box-shadow: none;
}

.fake-textarea, .uneditable-input, input[type=text], input[type=password], input[type=datetime], input[type=datetime-local], input[type=date], input[type=month], input[type=time], input[type=week], input[type=number], input[type=email], input[type=url], input[type=tel], input[type=color], input[type=search], textarea {
    background-color: #212b3b;
    border-color: #494c53;
    color: #aaa;
}

.pd-widget {
    background-color: #2a384e;
    border-color: #494c53;
}

.timeline__block--not-on-call {
    background-color: #2b394e;
    border-color: #6f7480;
}

.pd-widget-header {
    background-color: #212b3b;
    background-image: linear-gradient(to bottom,#2a384e,#212b3b);
    border-color: #494c53;
    color: #888;
}

.pd-widget .pd-widget-scrollable-header {
    background-color: #212b3b;
    border-color: #494c53;
}

.pd-incidents-table tr.top-border>td {
    background-color: #222;
    box-shadow: 0px -8px 15px 0px rgba(255, 255, 255, 0.1);
}

.pd-incidents-table td.incident-table-detail-cell {
    background-color: #222;
}

.pd-incidents-table .pd-alerts-inline-table th {
    border-color: #494c54;
    color: #aaa;
}

.pd-incidents-table.pd-alerts-inline-table tr td {
    background-color: #2a384e;
    border-color: #494c53;
}

.pd-incidents-table.pd-alerts-inline-table .details-row .incident-table-detail-cell {
    background-color: #212b3b;
    border-color: #494c53;
}

.pd-incidents-table .details-row table td .nagios-details.pd-key-value-table td {
    background-color: #2a384e;
    border-color: #494c53;
}

.pd-incidents-table.pd-alerts-inline-table .details-row .incident-table-detail-cell tr:last-child td:not(.nagios-key):not(.nagios-value) {
    border-color: #494c52;
}

.pd-incidents-table .details-row tr.details-header-row {
    border-right: none;
}

.pd-secondary-menu .dropdown-menu {
    background-color: #202a3a;
    border: none;
}

.pd-secondary-menu .dropdown-menu>li>a:hover {
    background-color: #3a4c6c;
}

.pd-secondary-menu .dropdown-menu>li>a {
    color: #eee;
}

a.pd-header-lens--title {
    border: none;
}

a.pd-header-lens--index-item {
    background-color: #202a3a;
    color: #eee;
}

.pd-header-lens--index-item:active, .pd-header-lens--index-item:focus, .pd-header-lens--index-item:hover {
    background-color: #2a384e;
}

.pd-header-lens--dropdown {
    background-color: #202a3a;
    border-color: #494c53;
}

.pd-header-lens--index, .pd-header-lens--list-item__union {
    border-bottom: #494c53;
}

.pd-header-lens--list-item a, [class^=pd-header-lens--list-item__] a {
    color: #eee;
}

.pd-header-lens--list-item.selected, .selected[class^=pd-header-lens--list-item__] {
    background-color: #2a384e;
}

.pd-header-lens--list-item:hover, [class^=pd-header-lens--list-item__]:hover {
    background-color: #3b4e6b;
}

a.pd-header-lens--index-item.active {
    border: none;
}

.pd-secondary-menu .dropdown-menu__header {
    background-color: #202a3a;
}

a.pd-paginator__page-switch {
    background-color: #1e70ab;
    border-color: #494c53;
    color: #eee;
}

a.pd-paginator__page-switch--disabled {
    background-color: #1f5a84;
    border-color: #494c51;
    color: #888;
}

.pd-paginator__page-switch:hover {
    color: #fff;
    background-color: #16527d;
}

.modal {
    background-color: #29374e;
}

h3.pd-modal-title {
    color: #eee;
}

.modal-header .close {
    color: #eee;
    text-shadow: none;
}

.modal-header .close:focus, .modal-header .close:hover {
    color: #ccc;
    background-color: #565656;
}

.modal-header {
    border-color: #494C55;
}

.pd-combobox .ember-power-select-trigger {
    border-color: #494C55;
    background-color: #222c3b;
}

.modal-footer {
    background-color: #2a384e;
    border-color: #494c54;
    box-shadow: none;
}

.btn-primary, form:valid .pd-account-onboarding__control--action button {
    background-color: #2fba04;
}

.btn-primary.active, .btn-primary.disabled, .btn-primary:active, .btn-primary:focus, .btn-primary:hover, .btn-primary[disabled], form:valid .pd-account-onboarding__control--action button.active, form:valid .pd-account-onboarding__control--action button.disabled, form:valid .pd-account-onboarding__control--action button:active, form:valid .pd-account-onboarding__control--action button:focus, form:valid .pd-account-onboarding__control--action button:hover, form:valid .pd-account-onboarding__control--action button[disabled] {
    background-color: #0aba04;
    color: #fff;
}

.btn.disabled, .btn.disabled:hover, .btn[disabled], .btn[disabled]:hover {
    background-color: #333;color: #888;box-shadow: none;
}

a.assign.assign-all.btn.active:hover {
    color: #fff;
}

.pd-incidents-table th.ascending, .pd-incidents-table th.descending {
    background-color: #000;
    background-image: none;
    color: white;
    border-color: #494c53;
}

.pd-incidents-table th.ascending a, .pd-incidents-table th.descending a {
    color: #fff;
}

.pd-incidents-table th a:hover {
    color: #ddd;
}

.pd-incidents-table tbody tr.urgency-row-low td {
    background-color: #1e2229;
    color: #888;
}

}
