css:
@-moz-document domain("www.twitch.tv") {
/* Base classes and colors */

    .tw-root--theme-dark a {
        color: #aaa;
    }
    .tw-root--theme-dark .twilight-root, .tw-root--theme-dark body {
        background-color: #141414;
        color: #e1e1e1;
    }
    .tw-root--theme-dark .top-nav__menu {
        background-color: #282828;
        border-bottom: 1px solid #111;
    }
    .tw-root--theme-dark .tw-c-background, .tw-root--theme-dark .channel-info-bar {
        background-color: #1c1c1c !important;
    }
    .tw-root--theme-dark .tw-c-background-alt-2, .tw-root--theme-dark .tw-c-background-alt, .tw-root--theme-dark .chat-viewers__pane, .tw-root--theme-dark .tw-c-background-base {
        background-color: #1e1e1e !important;
    }
    .tw-root--theme-dark .tw-c-background-accent-alt-2 {
        background-color: #282828 !important;
    }
    .tw-root--theme-dark .tw-border, .tw-root--theme-dark .tw-border-t, .tw-root--theme-dark .tw-border-b {
        border-color: #0b0b0b !important;
    }
    .tw-root--theme-dark .__pane, .tw-root--theme-dark .chat-room__pane, .tw-root--theme-dark .chat__pane, .tw-root--theme-dark .chat-room, .tw-root--theme-dark .channel-header, .tw-root--theme-dark .side-nav__overlay-wrapper, .tw-root--theme-dark .side-nav__theme-wrapper, .tw-root--theme-dark .directory-tabs, .tw-root--theme-dark .chat-room__header {
        background: #1e1e1e;
        border-color: #111 !important;
    }
    .tw-root--theme-dark .front-page-carousel {
        background: #0e0e0e!important;
    }
    /* Scrollbars */
    .tw-root--theme-dark .simplebar-scrollbar, .tw-root--theme-dark .scrollable-area .simplebar-track .simplebar-scrollbar {
        border-radius: 0;
        -webkit-box-shadow: none;
                box-shadow: none;
        background: #444;
        border: none;
    }
    /* Textareas, inputs etc */
    .tw-root--theme-dark .tw-textarea, .tw-root--theme-dark .tw-input {
        background-color: #141414;
        -webkit-box-shadow: none;
                box-shadow: none;
        border: 1px solid #333;
        -webkit-transition: border 150ms;
        transition: border 150ms;
    }
    .tw-root--theme-dark .tw-textarea:focus, .tw-root--theme-dark .tw-input:focus {
        -webkit-box-shadow: none;
                box-shadow: none;
        border-color: #111;
    }
    /* Buttons */
    .tw-root--theme-dark .tw-button:focus, .tw-root--theme-dark .tw-button-icon:focus, .tw-root--theme-dark .tw-button--hollow:focus {
        -webkit-box-shadow: none;
                box-shadow: none;
    }
    .tw-root--theme-dark .tw-button-icon--overlay:active, .tw-root--theme-dark .tw-button-icon--overlay:focus, .tw-root--theme-dark .theme--light .tw-button-icon--overlay:active {
        border-color: #111;
        -webkit-box-shadow: none;
                box-shadow: none;
        background: #1e1e1e;
    }
    .tw-root--theme-dark .tw-interactable:focus, .tw-root--theme-dark .theme--light .tw-interactable:focus {
        -webkit-box-shadow: none;
                box-shadow: none;
    }
    .tw-root--theme-dark .follow-btn__dropdown-toggle, .tw-root--theme-dark .theme--light .follow-btn__dropdown-toggle {
        background-color: #141414;
    }
    /* Tooltip balloons */
    .tw-root--theme-dark .tw-balloon {
        border-radius: 0;
        background: #1e1e1e;
        border: 1px solid #111;
        -webkit-box-shadow: none;
                box-shadow: none;
    }
    .tw-root--theme-dark .balloon--tooltip, .tw-root--theme-dark .tw-tooltip, .tw-root--theme-dark .bttv-emote-tooltip {
        background: #333;
        color: #fafafa;
        border: 1px solid #171717;
    }
    .tw-root--theme-dark .balloon--tooltip:after, .tw-root--theme-dark .tw-tooltip::after, .tw-root--theme-dark .bttv-emote-tooltip::after {
        background: #333;
        border: solid #171717;
    }
    .tw-root--theme-dark .tw-tooltip--up::after, .tw-root--theme-dark .balloon--up::after, .tw-root--theme-dark .bttv-emote-tooltip--up::after {
        border-width: 0 1px 1px 0;
    }
    .tw-root--theme-dark .tw-tooltip--down::after, .tw-root--theme-dark .balloon--down::after, .tw-root--theme-dark .bttv-emote-tooltip--down::after {
        border-width: 1px 0 0 1px;
    }
    /* Sidebar */
    .tw-root--theme-dark .side-nav-card__link {
        background: none;
    }
    .tw-root--theme-dark .side-nav-card__link:hover, .tw-root--theme-dark .whispers-list-item:hover, .tw-root--theme-dark .tw-interactable--hover, .tw-root--theme-dark .tw-interactable:hover {
        background: #171717;
    }
    /* Top bar menus */
    .tw-root--theme-dark .persistent-notification__read {
        background-color: #1e1e1e;
    }
    .tw-root--theme-dark .tw-theme--dark .persistent-notification__unread {
        background-color: #333;
    }
    .tw-root--theme-dark .prime-offer-listing__content::after {
        background-image: -webkit-gradient(linear, left top, left bottom, from(rgba(32, 28, 43, 0)), to(#171717));
        background-image: linear-gradient(rgba(32, 28, 43, 0), #171717);
    }
    .tw-root--theme-dark .search-result-section__titlesep {
        background: #141414;
    }
    /* Chat */
    .tw-root--theme-dark .channel-root__right-column {
        background: #1e1e1e;
        border-right: 1px solid #0b0b0b;
    }
    .tw-root--theme-dark .chat-room__header, .tw-root--theme-dark .chat-viewers__header {
        -webkit-box-shadow: none;
                box-shadow: none;
        border-bottom: 1px solid #0b0b0b;
        background: #1e1e1e;
    }
    .tw-root--theme-dark .chat-hosting-notification__container {
        background-color: #333;
    }
    .tw-root--theme-dark .chat-viewers-list__button {
        color: #999;
    }
    .tw-root--theme-dark .chat-list {
        border-bottom: 1px solid #0b0b0b;
        margin-bottom: 1em;
    }
    .tw-root--theme-dark .chat-list .chat-list__more-messages-placeholder {
            margin-left: 0 !important;
            margin-right: 0 !important;
        }
    .tw-root--theme-dark .chat-viewers-list > div:first-child {
        border-bottom: 1px solid #333;
    }
    .tw-root--theme-dark .user-notice-line .chat-line__message {
        background-color: transparent;
    }
    .tw-root--theme-dark .chat-line__message.bttv-split-chat-alt-bg, .tw-root--theme-dark .vod-message.bttv-split-chat-alt-bg {
        background-color: #232323;
    }

    .tw-root--theme-dark .chat-line__message {
        background-color: #141414
    }

    .tw-root--theme-dark .pinned-cheer__bounding-box {
        background-color: #272727;
        border-bottom: 1px solid #0b0b0b;
    }
    .tw-root--theme-dark .t-bits-card__footer {
        background-color: #141414;
    }
    .tw-root--theme-dark .chat-line__message.bttv-split-chat-alt-bg, .tw-root--theme-dark .vod-message.bttv-split-chat-alt-bg, .tw-root--theme-dark .chat-line__message.bttv-split-chat-alt-bg + .chat-line__message, .tw-root--theme-dark .vod-message.bttv-split-chat-alt-bg + .vod-message {
        border-bottom: 1px solid #0b0b0b;
    }
    .tw-root--theme-dark .chat-line__message, .tw-root--theme-dark .chat-line__moderation, .tw-root--theme-dark .chat-line__status, .tw-root--theme-dark .chat-line__raid, .tw-root--theme-dark .chat-line__subscribe {
        border-bottom-color: #2d2d2d;
    }
    /* Vod Chat */
    .tw-root--theme-dark .video-chat {
        background: transparent;
        border-color: #0b0b0b;
    }
    .tw-root--theme-dark .video-chat .vod-message.vod-message--focused, .tw-root--theme-dark .video-chat .vod-message:hover {
            background-color: #282828;
        }
    .tw-root--theme-dark .video-chat .video-chat__header, .tw-root--theme-dark .video-chat .chat-room__header {
            -webkit-box-shadow: none;
                    box-shadow: none;
            border-bottom: 1px solid #0b0b0b;
        }
    .tw-root--theme-dark .video-chat .video-chat__input {
            -webkit-box-shadow: none;
                    box-shadow: none;
            border-top: 1px solid #0b0b0b;
        }
    .tw-root--theme-dark .video-chat .video-chat__sync-button, .tw-root--theme-dark .video-chat .video-chat__sync-button:focus, .tw-root--theme-dark .video-chat .video-chat__sync-button:hover {
            color: #faf9fa;
            background: rgba(0, 0, 0, 0.6);
            width: 100%;
            left: 0;
        }
    /* Whispers */
    .tw-root--theme-dark .thread-header__title-bar-container {
        background-color: #171717;
        -webkit-box-shadow: none;
                box-shadow: none;
        border: 1px solid #0b0b0b;
    }
    /* Channel page */
    
    .tw-root--theme-dark .video-player-hosting-ui__header {
        background-color: #181818 !important;
        border: 1px solid #0b0b0b;
    }
    
    .tw-root--theme-dark .activity-reaction {
        background: #141414;
    }
    .tw-root--theme-dark .channel-panels-container > .default-panel, .tw-root--theme-dark .channel-panels-container .event {
            background-color: #202020;
            -webkit-box-shadow: 0 2px 4px -1px rgba(0, 0, 0, 0.34), 0 2px 2px -2px rgba(0, 0, 0, 0.26), 0 1px 4px 0 rgba(0, 0, 0, 0.28) !important;
            box-shadow: 0 2px 4px -1px rgba(0, 0, 0, 0.34), 0 2px 2px -2px rgba(0, 0, 0, 0.26), 0 1px 4px 0 rgba(0, 0, 0, 0.28) !important;
        }
    .tw-root--theme-dark .channel-panels-container > .default-panel .panel-description, .tw-root--theme-dark .channel-panels-container .event .panel-description {
                margin: 1rem 1rem 0 1rem;
            }
}

@-moz-document domain("www.twitch.tv") {
/* On old ember-powered sites */

.tw-theme--dark .top-nav__menu, .top-nav__menu, .top-nav__drawer-anchor, .top-nav__logo {
    background-color: #282828;
}

.tw-theme--dark .top-nav__menu, .top-nav__menu {
	border-bottom: 1px solid #111;    
}

.tw-theme--dark .top-nav-drawer {
	background-color: #1c1c1c;
}

.tw-theme--dark .top-nav-drawer__status {
	background-color: #1e1e1e;    
}

#large_nav .content {
	background: #1e1e1e;    
}

.tw-theme--dark .cl-container .section-header  {
	background-color: #1c1c1c;
    border-color: #171717;
}

.tw-theme--dark .cl-container .cl-section .cl-subheader {
	background-color: #1e1e1e;
    border-color: #171717;
}

.tw-theme--dark .brick {
	border-color: #171717;    
}

.tw-theme--dark .cl-container fieldset {
	border-color: #171717;    
}

.tw-theme--dark .cl-container .cl-section {
	background-color: #141414;    
}

.premium_setup_contain #turbo_setup {
	border-color: #171717;    
}
}
