css:
@-moz-document domain("toyhou.se") {
.content-main {
	background:url("https://image.freepik.com/free-photo/bright-scenic-day-environment-background_1253-545.jpg");
	background-size: 1600px 800px;
		background-attachment: fixed;
}
.side-nav li a, .side-nav li a:hover, .side-nav li a:focus, .side-nav li a:link{
	font-weight: bold;
color: 	#32CD32;
}
.list-unstyled {
	background:url("https://i.imgur.com/oKQslHH.png");
	background-size: 400px 378px;
	 background-color: transparent;
}
.bg-faded {
background: #eff6ec;
		border: dashed 1px #739655;
}
.bg-faded:after {
	display:block
}
ul.gallery-row {
background: #eff6ec;
	background: transparent;
}
profile-gallery-content{
	background: #f1f8b9;
}
.forum-view-table-wrapper table td, .forum-category-table-wrapper table td, .forum-category-wrapper .forum-category-forum{
	  padding: 5px;
    filter: alpha(opacity=50);
	 background-color: transparent;
  background-color: rgba(255, 255, 255, 0.2);
}
.gallery-thumb .thumb-image > .img-thumbnail, .img-thumbnail {
	background: transparent;
}
magnific-gallery image-gallery auto-clear gallery-row large-block-grid-6 {
	background: transparent;
}
.comment .forum-post-post .forum-post-footer, .forum-post .forum-post-post .forum-post-footer {
border: hidden;
}
.navbar-toggleable-sm .navbar-collapse, .navbar .navbar-brand, .navbar, .navbar .dropdown-menu, .dropdown-item, .footer {
	background-color: #00544b;
}
}
