css:
@-moz-document domain("cemetech.net") {
body {
    font-family: Verdana, Arial, Helvetica, sans-serif;
    font-size: 0.75rem;
    line-height: initial;
    text-align: left;
    background-color: #444;
    margin: 0rem;
    padding: 0rem;
}
a {
    color: #0058ff;
    text-decoration: none;
}
a:hover {
    text-decoration: underline;
}
a.maintitle,
a.largetext,
a.sideheadtop,
a.mainheadmiddle,
a.sideheadmiddle {
    color: #fff;
    text-decoration: none;
    font-size: 1rem;
}
a.sideheadmiddle {
    font-size: 0.875rem;
}
table {
    margin-left: 0rem;
    margin-right: 0rem;
    border: 0rem;
    padding: 0rem;
    vertical-align: top;
}
td {
    vertical-align: top;
}
.outer {
    width: 100%;
    vertical-align: middle;
    margin: 0rem;
    padding: 0rem;
    margin-top: 0.5rem;
}
.container {
    position: relative;
    max-width: 98%;
    width: 996px;
    margin-left: auto;
    margin-right: auto;
}
.banner_container {
    position: relative;
    max-width: 100%;
    width: 996px;
    margin: 0rem;
}
.inner {
    /*	text-align: center; */
    
    margin-left: auto;
    margin-right: auto;
    border: 0rem;
}
#hbot {
    position: relative;
    top: 0.25rem;
    background-color: #0c6cb7;
    border: 0.1875rem solid #272727;
    border-top: none;
    text-align: center;
    display: -webkit-flex;
    display: flex;
    flex-wrap: wrap;
    -webkit-flex-wrap: wrap;
    justify-content: center;
    -webkit-justify-content: center;
}
#htop {
    text-align: left;
    max-width: 996px;
    width: 100%;
    position: absolute;
    top: 0%;
    left: 0%;
    right: 0%;
    bottom: 0%;
    border: 9px solid #961f1f;
    border-top-right-radius: 27px;
    border-top-left-radius: 27px;
}
.sideheadmiddle {
    color: #fff;
    margin: 0rem;
    padding: 0.1875rem;
    margin-top: 0.0625rem;
    margin-bottom: 0.0625rem;
    background: #444;
    text-align: left;
    border: 0.1875rem solid #272727;
    border-top: none;
    border-bottom: none;
}
.mainlowermiddle,
.sideheadbottom {
    padding: 0rem;
    height: 0.5625rem;
    margin: 0rem 0rem 0.375rem 0rem;
    border-bottom: 0.1875rem solid #961f1f;
    background-color: #961f1f;
    text-align: center;
    background-image: none;
}
.sideheadbottom {
    border: 0.1875rem solid #272727;
    border-top: none;
}
.sideheadtop,
.mainheadmiddle {
    position: relative;
    font-size: 1rem;
    font-weight: bold;
    color: #FFF;
    margin: 0rem 0rem 0.0625rem;
    text-align: center;
    padding: 0.375rem 0rem 0rem;
    min-height: 1.25em;
    border: 0.1875rem solid #272727;
    border-bottom: none;
    background-color: #961f1f;
}
.mainbody,
.mainbodynaked {
    text-align: center;
    padding: 0 0.625rem;
}
.mainbody {
    background: #961f1f;
    margin-bottom: 0.0625rem;
    padding: 0.1875rem 0.625rem;
    border: 0.1875rem solid #272727;
    border-top: none;
}
.mainbodyinner {
    color: #000;
    font-size: 0.75rem;
    background: #ddd;
    text-align: left;
    padding: 0.1875rem;
}
.innersidebar {
    background-color: #ccc;
    padding: 0.1875rem;
}
.hdivider_thick {
    background-color: #7b291b;
    height: 0.0625rem;
}
.boldsmalltext {
    font-size: 0.85em;
    font-weight: bold;
}
.main_nav_list {
    list-style: inside none none;
    padding: 0rem;
    margin: 0rem;
    flex: 1 1 auto;
    -webkit-flex: 1 1 auto;
}
.navbutton {
    display: inline-block;
    padding: 0rem;
    margin: 0rem;
    height: 2rem;
    width: 5.5rem;
    color: #ffffff;
    vertical-align: middle;
    border: 0.0625rem solid transparent;
}
.navbutton a,
.navbutton a:hover,
.navbutton a:active {
    display: inline-block;
    color: #fff;
    font-size: 0.875rem;
    font-weight: bold;
    vertical-align: middle;
    text-decoration: none;
    width: 100%;
    padding-top: 0.5rem;
    padding-bottom: 0.5rem;
}
.navbutton:hover {
    border-color: #eee;
    border-top-color: transparent;
    box-shadow: 0rem 0rem 0.1875rem 0rem #000;
    background-size: 1.83rem 1.75rem;
    background-repeat: repeat-x;
    background-image: radial-gradient(1rem 0.875rem at 1rem 0.875rem, rgba(255, 255, 255, 0.33) 0%, rgba(255, 255, 255, 0) 100%);
    background-position: 0rem 1.125rem;
    background-color: transparent;
    background-attachment: scroll;
}
.forum-action-button,
.post-action-button,
.navsearch {
    vertical-align: middle;
    background: #EEE linear-gradient(to right, #cac4c4 0%, #e7e7e7 50%, #cac4c4 100%) no-repeat scroll 0% 0%;
    color: #7B291B;
}
.forum-action-button,
.post-action-button {
    text-align: center;
    border: 1px solid #7B291B;
    border-radius: 0.375rem;
    font-weight: bold;
    font-size: 0.75rem;
    display: inline-block;
    text-transform: lowercase;
    padding: .175rem .25rem;
    min-height: 1.1rem;
}
.post-action-button {
    min-height: .75rem;
    padding: .0625rem .25rem;
    margin: .0625rem .0625rem;
}
.forum-action-button > div {
    vertical-align: middle;
}
.navsearch {
    min-width: 8rem;
    width: 8rem;
    padding: 0rem;
    border-radius: 0.375rem;
    height: 1.625rem;
    margin: 0.1875rem 1rem 0.1875rem 0rem;
    flex: 0 0 auto;
    -webkit-flex: 0 0 auto;
}
#navsearchform {
    margin: 0rem .2rem;
    padding: 0rem;
    height: 100%;
    display: -webkit-flex;
    display: flex;
    -webkit-align-items: center;
    align-items: center;
    -webkit-justify-content: space-around;
    justify-content: space-around;
}
.navsearchinput {
    margin: 0rem;
    background: #54739b none repeat scroll 0% 0%;
    border: medium none;
    color: #EEE;
    font-size: 0.625rem;
    width: 5.75rem;
    height: 1.3em;
}
.navsearchmore {
    display: inline;
    border: 0rem none;
    padding: 0rem;
    color: #7B291B;
    background-color: transparent;
    font-weight: bold;
}
.navbread {
    background-color: #bbb;
}
.smalltext {
    color: #000;
    font-size: 0.9em;
}
.medtext {
    color: #000;
    font-size: 0.75rem;
}
.largetext {
    color: #000;
    font-size: 1.2em;
}
.headlinetext {
    color: #000;
    font-size: 1rem;
}
.admincolor {
    color: #fff;
    background: #000;
}
/* SAX text container */

.sax-timestamp {
    color: #bbb;
}
.sax-username {
    color: #ddd;
}
.sax-message {
    color: #fff;
}
ul.sax-message,
li.sax-message {
    font-family: Verdana, Arial, Helvetica, sans-serif;
    font-size: 0.625rem;
    list-style-type: none;
    list-style-position: inside;
    padding: 0rem;
    margin: 0rem;
    word-wrap: break-word;
}
ul.sax-message {
    margin-top: 0.25rem;
}
.sax-message a {
    background: #961f1f;
    color: #eee;
    text-decoration: none;
}
.sax-message a:hover {
    text-decoration: underline;
}
.saxred {
    color: #bbb;
    font-family: Verdana, Arial, Helvetica, sans-serif;
    font-size: 0.625rem
}
.saxgray {
    color: #ddd;
    font-family: Verdana, Arial, Helvetica, sans-serif;
    font-size: 0.625rem
}
.saxmaroon {
    color: #ddd;
    font-family: Verdana, Arial, Helvetica, sans-serif;
    font-size: 0.625rem
}
.saxblack {
    color: #fff;
    font-family: Verdana, Arial, Helvetica, sans-serif;
    font-size: 10rem;
}
a.saxgray,
a.saxblack {
    background: #600;
    color: #eee;
    font-family: Verdana, Arial, Helvetica, sans-serif;
    font-size: 0.625rem;
    text-decoration: none;
}
a.saxgray:hover,
a.saxblack:hover {
    background: #600;
    color: #eee;
    font-family: Verdana, Arial, Helvetica, sans-serif;
    font-size: 0.625rem;
    text-decoration: underline;
}
/* DO NOT TOUCH. UNDER PENALTY OF DEATH. Love, elf */

.saxhighlight {
    color: #fff;
    background-color: #000;
    font-family: Verdana, Arial, Helvetica, sans-serif;
    font-size: 0.625rem;
}
.saxuser .saxserver {
    display: none;
    font-size: 0.625rem;
}
.saxuser .saxinneruser {
    border-bottom: 0.0625rem dotted #ddd;
    font-size: 0.625rem;
}
.saxuser:hover .saxserver {
    display: inline;
    font-size: 0.625rem;
}
.saxuser:hover .saxinneruser {
    border: none;
    font-size: 0.625rem;
}
/*END DEATH THREATS */

.nowrap {
    white-space: nowrap;
}
/* Quote & Code blocks */

.typepost,
.helpline {
    font-family: Verdana, Arial, Helvetica, sans-serif;
    font-size: 0.75rem;
    background-color: #f8f8f8;
    box-sizing: border-box;
    width: 100%;
    /* Should center if we hit max-width.
	   I can't be bothered to figure out how right now. */
    
    max-width: 40rem;
    margin-left: auto;
    margin-right: auto;
}
textarea.typepost {
    line-height: 1rem;
    height: 12.5rem;
    resize: both;
}
.helpline {
    font-size: 0.625rem;
    background-color: #e8e8e8;
}
.code {
    font-family: Courier, 'Courier New', sans-serif;
    font-size: 0.6875rem;
    color: #006600;
    background-color: #ccc;
    border: #555;
    border-style: solid;
    border-left-width: 0.8em;
    border-top-width: 0.1em;
    border-right-width: 0.1em;
    border-bottom-width: 0.1em;
    padding: 0.25em;
    max-height: 27em;
    max-width: 95%;
    overflow: auto;
    word-wrap: break-word;
}
.quote {
    /* Geometric series on quote pyramids. This seems like a good idea. */
    
    font-family: Verdana, Arial, Helvetica, sans-serif;
    font-size: .675rem;
    color: #222;
    line-height: 125%;
    background-color: #ccc;
    border: #555;
    border-style: solid;
    border-width: .1em;
    border-left-width: 0.8em;
    width: 95%;
    word-wrap: break-word;
    margin: 0 .3rem;
    padding: .2rem;
    box-sizing: border-box;
}
.quote_name {
    width: 95%;
    word-wrap: break-word;
    margin: 0 .5rem;
    padding: .2rem;
}
.saxinput {
    font-size: 0.625rem;
    width: 14em;
}
.saxsubmit {
    font-size: 0.625rem;
    margin: 0.1em;
    padding: 0.1em;
}
.sax_highlights {
    display: none;
    position: fixed;
    bottom: 0.5rem;
    left: 0.5rem;
    z-index: 9001;
    background-color: #222;
    color: #fff;
    border: 0.1875rem solid #f00;
    padding: 0.1875rem;
    cursor: pointer;
}
.sax_highlights a {
    background-color: #ddd;
}
#user_tools_parent,
#sidebar_parent {
    width: 12.5rem;
    vertical-align: top;
    float: left;
    clear: left;
}
#user_tools_parent .sideheadtop {
    margin-top: 0rem;
}
.usertoolslist,
.newslist,
.raquolist,
.file-modreview {
    list-style: none inside none;
    padding-left: 0em;
    margin: 0em;
    text-indent: 0em;
}
.usertoolslist li:before,
.newslist li:before,
.raquolist li:before,
.file-modreview li:before {
    content: " \00bb";
    padding-right: 0.5em;
}
.usertoolslist li a {
    color: #fff;
}
#at-a-glance-highlights li,
#smiley-list li {
    display: inline-block;
}
.glance_boxes,
.topictools_boxes,
.profile_cols {
    padding-top: 1rem;
    display: -webkit-flex;
    display: flex;
    -webkit-justify-content: space-between;
    justify-content: space-between;
}
.topictools_boxes {
    margin: .4rem;
    padding: 0rem;
}
.glance_boxes>div,
.topictools_boxes>div,
.profile_cols>div {
    -webkit-flex: 1 1 0;
    flex: 1 1 0;
}
.topictools_rightbox {
    text-align: right;
}
.profile_brief {
    max-width: 40%;
}
.profile_infocat {
    font-weight: bold;
    display: block;
}
.glance_contents {
    margin: .3em;
    padding: .2em;
    border: 1px solid #888;
    background-color: #ccc;
}
.glance_flex {
    display: -webkit-flex;
    display: flex;
    margin-bottom: .5em;
}
.glance_icon {
    width: 5em;
    height: 5em;
    -webkit-flex-shrink: 0;
    flex-shrink: 0;
}
#smiley-list {
    text-align: center;
}
#login_form {
    display: block;
    margin: 0rem;
    border: none;
    padding: 0rem;
}
#login_button {
    display: block;
    clear: both;
}
#page_content_parent {
    vertical-align: top;
    float: right;
    margin: 0rem;
    padding: 0px;
    width: calc(100% - 13rem);
    overflow: auto;
    position: relative;
}
#content_wrapper {
    margin-left: auto;
    margin-right: auto;
    border: 0 none;
    padding: 0px;
    padding-top: 0.375rem;
    position: relative;
    top: 0.25rem;
    max-width: 80rem;
    width: 98%;
}
.sideheadtop,
.roundedtop {
    border: 0.1875rem solid #383838;
    border-bottom: none;
}
#ajaxinfobox {
    word-wrap: break-word;
    text-overflow: ellipsis;
}
#ajaxinputs {
    position: relative;
    z-index: 20;
    height: 1.25rem;
    top: 0;
    left: 0
}
#ajaxinputs form {
    display: inline;
}
.post {
    background-color: #eee;
}
.post,
.typepost,
.helpline {
    border: 0.0625rem solid #7B291B;
    margin: 0.3125rem 0rem 0.3125rem 0rem;
    padding: 0rem;
}
.post:nth-child(odd) {
    background-color: #e3e3e3;
}
.post-header {
    width: 100%;
    min-height: 3.125rem;
    border-bottom: 0.0625rem solid #999;
    display: -webkit-flex;
    display: flex;
    -webkit-justify-content: space-between;
    justify-content: space-between;
}
.user-info-row {
    height: 1rem;
    padding: 0rem;
    margin: 0rem;
    white-space: nowrap;
}
.awards {
    color: maroon;
    position: relative;
}
.awards input[type=checkbox] {
    display: none;
}
.tooltip {
    display: none;
}
.awards input[type=checkbox]:checked + .tooltip {
    display: block;
    background: #333;
    background: rgba(0, 0, 0, .8);
    border-radius: 0.3125rem;
    color: #fff;
    left: 100%;
    margin-left: 0.3125rem;
    top: 0.3125rem;
    padding: 0.3125rem 0.9375rem;
    position: absolute;
    padding: 0.3125rem;
}
.awards img {
    vertical-align: middle;
}
a.username {
    font-weight: bold;
    color: #000000;
}
img.avatar {
    max-width: 3.125rem;
    max-height: 3.125rem;
    height: auto;
    width: auto;
    border-right: 0.0625rem solid #7B291B;
    border-bottom: 0.0625rem solid #7B291B;
    display: block;
}
.profile_avatar {
    float: left;
    margin: .3rem;
}
.profile_avatar img {
    border: 0.0625rem solid #7B291B;
}
.ppd_graph {
    text-align: center;
}
.ppd_graph img {
    max-width: 95%;
    border: 0.0625rem solid #7B291B;
}
#cemetech-at-a-glance ul.smalltext,
#at-a-glance-highlights ul,
#smiley-list ul,
ul.user-info,
ul.post-info,
.tooltip ul {
    list-style: inside none none;
    margin: 0rem;
    padding: 0rem;
}
.tooltip li {
    min-width: 350px;
    width: 22rem;
    height: 1.25rem;
    margin: 0rem;
    padding: 0rem;
    font-size: 0.625rem;
    vertical-align: middle;
    text-align: left;
}
.left-top-post {
    right: 0px;
    padding: 0rem;
    margin: 0rem;
    border: none;
    flex-shrink: 0;
    -webkit-flex-shrink: 0;
    display: inline-block;
}
.middle-top-post {
    -webkit-flex: 2 0 auto;
    flex: 2 0 auto;
    overflow-x: visible;
    margin: 0 .5rem;
}
.userrank {
    font-size: 0.625rem;
}
ul.post-info {
    height: 2.5rem;
    font-size: 0.625rem;
    text-align: right;
}
li.post-datetime,
li.post-edited {
    font-style: italic;
}
li.post-datetime a,
li.post-datetime a:hover,
li.post-datetime a:active {
    color: #000;
}
.right-top-post {
    margin-right: 1em;
    display: inline-block;
    flex: 0 1 auto;
    -webkit-flex: 0 1 auto;
}
.sig-area {
    -webkit-flex: 2;
    flex: 2;
    border-top: 1px solid #bbb;
    padding: 0 0.8125rem;
    overflow: hidden;
    max-height: 2.75rem;
    font-size: 0.625rem;
    width: auto;
}
.post-body {
    padding: 1em;
}
.post-footer {
    display: -webkit-flex;
    display: flex;
}
.post-action {
    border-top: 1px maroon solid;
    border-left: 1px maroon solid;
    max-width: 8rem;
    height: 2.75rem;
    padding: 0;
    text-align: right;
}
.post-action>div {
    margin: 0px;
    display: inline-block;
}
.post-action img {
    margin: 0.0625rem;
}
.post-action a.nav {
    font-size: 1rem;
    font-weight: bold;
    display: inline-block;
    margin-top: -0.1875rem;
    height: 1rem;
}
.post-bottom-element {
    margin-top: 0em;
    margin-left: 0rem;
    margin-right: 0rem;
    /*max-height: 2.75rem;*/
}
.post-bottom-element span {
    /*padding: 0 0.625rem;*/
}
.copyright {
    text-align: center;
    font-size: 0.75rem;
}
br.clearing {
    clear: both;
}
#quickreply .gen {
    font-weight: bold;
}
#quickreply label,
#quickreply .gen {
    display: block;
}
.topictail {
    margin: 0;
    padding: 1%;
}
.topictail>div {
    padding: 0;
    margin: 0;
    border: medium none;
    display: inline-block;
    width: 48%;
    vertical-align: top;
}
td.row1,
td.row2,
td.row3 {
    overflow: hidden;
}
#cemetech-at-a-glance {
    position: relative;
}
#cemetech-at-a-glance .mainbodyinner {
    border: none;
    padding: 0;
    margin: 0;
}
#cemetech-at-a-glance .innersidebar {
    float: right;
    width: 40%;
}
/*
#cemetech-at-a-glance .introparagraph {
	float: left;
	width: 60%;
}*/

#cemetech-at-a-glance .newfiles {
    float: left;
    clear: both;
}
hr.hdivider_thick {
    height: 3px;
    clear: both;
    color: #7b291b
}
#tools,
#reflinks {
    width: 49.5%;
    margin: 0;
    padding: 0;
}
#tools {
    float: left;
}
#reflinks {
    float: right;
}
.tinyreplace {
    display: none;
}
@media all and (max-width: 60em) {
    #ajaxinfobox, #ajaxinputs form {
        display: none;
    }
    #ajaxinputs {
        height: auto;
    }
    #ajaxinputs:before {
        content: "SAX has been disabled due to small screen size. Please use the pop-out chat to continue chatting.";
    }
    #tools,
    #reflinks,
    #cemetech-at-a-glance .mainbodyinner,
    #page_content_parent,
    #user_tools_parent,
    #sidebar_parent {
        float: none;
        position: static;
        width: auto;
        margin-left: auto;
        margin-right: auto;
        /*padding-left: 0.25rem;
		padding-right: 0.25rem;*/
    }
    #user_tools_parent .sideheadtop,
    #user_tools_parent .sideheadmiddle,
    #user_tools_parent .sideheadbottom {
        width: auto;
    }
    #user_tools_parent .sideheadmiddle {
        height: auto;
    }
    .usertoolslist,
    .usertoolslist li {
        display: inline;
    }
    .usertoolslist li:before {
        padding-left: 0.5em;
        content: "\2022";
    }
    #login_button {
        display: inline;
    }
    .topictail>div {
        display: block;
        width: auto;
    }
    .post-bottom-element {
        width: auto;
    }
    .post-action {
        border-left: none;
        height: auto;
        max-width: 100%;
    }
    .glance_boxes,
    .topictools_boxes,
    .profile_cols {
        -webkit-flex-direction: column;
        flex-direction: column;
    }
    .glance_boxes>div,
    .topictools_boxes>div,
    .profile_cols>div {
        -webkit-flex: 1 1 auto;
        flex: 1 1 auto;
    }
    .topictools_rightbox {
        text-align: left;
    }
    .profile_brief {
        max-width: 100%;
    }
    .profile_infocat {
        display: inline;
    }
    .indextralow {
        display: none;
    }
    .post-footer {
        -webkit-flex-direction: column;
