css:
@-moz-document domain('habrahabr.ru') {
  .logo .svg {
      fill: #fafafa;
  }

  #TMpanel .container .bmenu>a.current,
  .page-header__info-title,
  .page-header__info-desc,
  code,
  .user-info__fullname,
  .user-info__specialization,
  .post-share__title,
  .comments-section__head-title,
  .defination-list__label_subscribe-panel,
  .defination-list__value_subscribe-panel,
  .checkbox__label {
      color: #fafafa !important;
  }

  .post__text-html h1, 
  .post__text-html h2 {
      text-shadow: 0 1px 1px #000;
  }

  .layout,
  .layout__row_navbar,
  .default-block,
  .comment__message, 
  .comment-form__preview,
  .h-popover,
  .h-message__controls {
      color: #cacaca;
      background-color: #2e2e2e;
      border-color: #4c4c4c;
  }

  #TMpanel,
  .post-stats,
  .post-share,
  .default-block__header,
  .content-list__item_devided,
  .author-panel,
  .comments-section__head,
  .h-popover__stats,
  .toggle-menu__most-read, 
  .toggle-menu_most-comments,
  .page-header_bordered {
      border: 0;
      background: none;
  }

  #TMpanel,
  .layout__elevator:hover,
  .post__translatation {
      background: rgba(0,0,0,.2);
  }

  .post__title-text,
  .post__text-html,
  .post-info__title,
  code {
      color: #cacaca !important;
  }

  code,
  .h-message {
      background-color: #2f2f2f !important;
      border-color: #4c4c4c !important;
  }

  code .hljs-keyword {
      color: #f85a32 !important;
  }

  .content-list_comments .comment__folding-dotholder:before {
      position: absolute;
      top: -8px;
      right: 0px;
      width: 500px;
      height: 5px;
      content: '.......................';
      color: rgba(145, 145, 145, 0.47);
      background: none;
      font-size: 60px;
      white-space: nowrap;
      line-height: 0;
      text-align: right;
      letter-spacing: 5px;
  }

  .comment__head_topic-author,
  .comment__head_topic-author * {
      background-color: #2c2919;
  }


  /* sidebar */
  .default-block__header {
      margin: 0;
      padding-left: 20px;
  }



  /* ad */
  .dfp-slot {
      display: none;
  }


  /* popup */
  .webui-popover {
      box-shadow: 0 0 0 4px rgba(0,0,0,.2);
      border-radius: 2px;
  }
}
