css:
@-moz-document domain("www.youtube.com") {
@font-face {
  font-family: edoregular;
  src: url("/wp-includes/fonts/edo-webfont.eot");
  src: url("/wp-includes/fonts/edo-webfont.eot?#iefix") format("embedded-opentype"), url("/wp-includes/fonts/edo-webfont.woff") format("woff"), url("/wp-includes/fonts/edo-webfont.ttf") format("truetype"), url("/wp-includes/fonts/edo-webfont.svg#edoregular") format("svg");
  font-weight: normal;
  font-style: normal; }
.yt-uix-button, .yt-uix-button-default {
  background-color: #111;
  border-color: #000;
  box-shadow: inset 0 1px 0 rgba(100, 100, 100, 0.1), 0 1px 2px rgba(0, 0, 0, 0.1);
  color: #666; }

.yt-uix-button-default:hover, .yt-uix-button-text:hover {
  background-color: #151515;
  border-color: #000;
  box-shadow: inset 0 1px 0 rgba(100, 100, 100, 0.1), 0 1px 2px rgba(0, 0, 0, 0.1); }

.compact-shelf .yt-uix-button-shelf-slider-pager {
  background: #222;
  border-color: #000; }

.compact-shelf .yt-uix-shelfslider-next-arrow, .rtl .compact-shelf .yt-uix-shelfslider-prev-arrow {
  opacity: 1;
  -webkit-filter: invert(40%); }

.browse-list-item-container:hover .compact-shelf .yt-uix-button-shelf-slider-pager, .compact-shelf:hover .yt-uix-button-shelf-slider-pager {
  border-color: #000;
  box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.5); }

.yt-uix-button .yt-uix-button-icon {
  -webkit-filter: invert(100%); }

.exp-invert-logo .yt-uix-button-subscribe-branded {
  background-color: #1b1b1b; }

.yt-uix-button-subscribe-branded:before {
  -webkit-filter: invert(75%); }

.exp-invert-logo .yt-uix-button-subscribe-branded:active, .exp-invert-logo .yt-uix-button-subscribe-branded:hover {
  background-color: #222; }

.yt-subscription-button-subscriber-count-branded-horizontal {
  background-color: #111;
  border-color: #000; }

.addto-watch-later-button:before, .addto-watch-later-button-sign-in:before {
  -webkit-filter: invert(100%); }

.yt-uix-subscription-preferences-button:before {
  -webkit-filter: invert(70%); }

.like-button-renderer-like-button:before, .like-button-renderer-dislike-button:before {
  -webkit-filter: invert(70%); }

::-webkit-scrollbar {
  width: auto; }

::-webkit-scrollbar-track {
  background: #111;
  border: 1px solid #000; }

::-webkit-scrollbar-thumb {
  background: #0b0b0b !important;
  border: 1px solid #000; }

.exp-invert-logo #header:before, .exp-invert-logo .ypc-join-family-header .logo, .exp-invert-logo #footer-logo .footer-logo-icon, .exp-invert-logo #yt-masthead #logo-container .logo, .exp-invert-logo #masthead #logo-container, .exp-invert-logo .admin-masthead-logo a, .exp-invert-logo #yt-sidebar-styleguide-logo #logo {
  -webkit-filter: invert(100%) brightness(65%); }

.exp-invert-logo .resume-playback-progress-bar {
  background: #009999; }

#yt-masthead-container {
  background: #0b0b0b;
  border-bottom: 1px solid #000; }

#masthead-search-terms {
  background-color: #111;
  border: 1px solid #000;
  box-shadow: inset 0 1px 2px #000; }

#masthead-appbar {
  background-color: #0b0b0b;
  border-bottom: 1px solid #000; }

#appbar-nav .yt-uix-button.yt-uix-button-epic-nav-item {
  color: #666;
  box-shadow: none; }

.epic-nav-item-heading {
  color: #999; }

.exp-invert-logo .epic-nav-item-heading, .exp-invert-logo a.yt-uix-button-epic-nav-item:hover {
  border-color: #009999; }

#yt-masthead-user .yt-uix-button {
  background-color: transparent; }

#appbar-guide-menu {
  background: #0b0b0b; }

.guide-pinned .guide-pinning-enabled #appbar-guide-menu {
  border-color: #000; }

.guide-item {
  color: #666; }

.guide-item:hover, .exp-invert-logo .guide-item.guide-item-selected, .guide-item.guide-item-selected, .guide-item.guide-item-selected:hover {
  background: #111;
  color: #999; }

.exp-invert-logo .guide-item .guide-video-youtube-red-icon, .guide-item .guide-unplugged-icon {
  -webkit-filter: invert(100%) brightness(65%); }

.guide-section-separator {
  border-bottom: 1px solid #222; }

.exp-invert-logo li.guide-section h3 a {
  color: #009999; }

body #footer-container {
  background-color: #0b0b0b;
  border-color: #000; }

#footer-main {
  border-color: #222; }

#body-container {
  background: url("https://i.imgur.com/AU4yGdu.png"); }

.yt-card {
  background: #0b0b0b;
  border: 1px solid #000;
  box-shadow: 0 1px 5px 2px #000, inset 0 0 2px 0px rgba(255, 255, 255, 0.05); }

#browse-items-primary .item-section > li > .yt-lockup-tile, .branded-page-v2-primary-col .branded-page-box, .section-list li .item-section .branded-page-box {
  border-bottom: 1px solid #000; }

.yt-ui-ellipsis {
  background-color: transparent; }

.yt-lockup-title a, .yt-lockup:hover a, .yt-lockup:hover .yt-lockup-meta a, .yt-lockup:hover .yt-lockup-description a {
  color: #999; }

.branded-page-module-title, .branded-page-module-title a:visited, .branded-page-module-title a {
  color: #009999; }

.multirow-shelf .yt-uix-expander-head {
  color: #009999; }

#theater-background {
  height: 482px; }

.html5-video-player:not(.ytp-transparent) {
  box-shadow: 0 0 5px 2px #000;
  border: 1px solid #000;
  width: calc(100% - 2px);
  max-height: 478px !important; }

h1.watch-title-container {
  color: #999; }

#watch-header .yt-user-info a {
  color: #666; }

.video-extras-sparkbar-likes {
  background: #009999; }

#watch8-action-buttons {
  border-color: #000; }

#watch8-secondary-actions .yt-uix-button {
  box-shadow: none; }

.addto-button:before, .action-panel-trigger-share:before, #action-panel-overflow-button:before, .action-panel-trigger-overflow:before {
  -webkit-filter: invert(70%); }

#watch-description {
  color: #666; }

#action-panel-details a {
  color: #006666; }

#action-panel-details:hover a {
  color: #009999; }

.yt-card .yt-uix-button-expander {
  background-color: #111;
  border-color: #000;
  box-shadow: inset 0 1px 0 rgba(100, 100, 100, 0.1), 0 1px 2px rgba(0, 0, 0, 0.1);
  color: #666;
  margin-bottom: 0 !important; }
  .yt-card .yt-uix-button-expander:hover {
    color: #999;
    background-color: #151515; }

.comment-simplebox-renderer-collapsed-content {
  background: #111;
  border-color: #000;
  color: #666; }

.comment-simplebox-arrow {
  display: none; }

.yt-uix-button-primary, .yt-uix-button-primary[disabled] {
  background: #111;
  border-color: #000; }

.comment-simplebox {
  background-color: transparent; }

.comment-simplebox-frame {
  border-color: #000;
  background-color: #111;
  box-shadow: inset 0 0 5px #000; }

.comment-simplebox:not(.yt-uix-form-error).focus .comment-simplebox-frame {
  border-color: #000;
  box-shadow: inset 0 0 5px #000; }

.comment-simplebox-text {
  background-color: transparent;
  color: #666; }

.comment-simplebox-renderer {
  border-color: #000; }

.comment-author-text {
  color: #999; }

.comment-renderer-text {
  color: #666; }

.comment-renderer-like-count {
  color: #009999; }

.comment-renderer-reply {
  padding: 0 10px;
  opacity: 1; }

#comment-section-renderer .sprite-comment-actions:before {
  opacity: 1; }

.comment-section-renderer-paginator {
  background-image: none; }

.watch-playlist {
  background: #0b0b0b;
  border: 1px solid #000;
  box-shadow: 0 1px 5px 1px #000, inset 0 0 2px 0px rgba(255, 255, 255, 0.05);
  color: #666; }

#player-playlist .watch-playlist {
  margin-left: 0; }

#player-playlist .playlist-title {
  color: #009999; }

#player-playlist .yt-uix-button-icon-watch-appbar-autoplay, #player-playlist .yt-uix-button-icon-watch-appbar-shuffle-video-list {
  -webkit-filter: invert(30%); }

#player-playlist .playlist-videos-list .yt-ui-ellipsis {
  background-color: transparent;
  color: #999; }

#player-playlist .playlist-videos-list li:hover {
  background-color: #151515; }

#player-playlist .playlist-videos-list li:hover .yt-ui-ellipsis {
  background-color: transparent;
  color: #999; }

#player-playlist :not(.watch-queue-nav) .playlist-videos-list li.currently-playing {
  background-color: #151515; }

#player-playlist :not(.watch-queue-nav) .playlist-videos-list li.currently-playing .yt-ui-ellipsis {
  background-color: transparent;
  color: #999; }

.yt-scrollbar-dark ::-webkit-scrollbar-track {
  border-left-color: transparent;
  background: #111;
  -webkit-box-shadow: none; }

.yt-scrollbar-dark ::-webkit-scrollbar-thumb {
  border-left-color: #000; }

#watch7-sidebar .watch-sidebar-head {
  color: #999; }

.autoplay-bar .autoplay-info-icon {
  -webkit-filter: invert(70%); }

.yt-uix-checkbox-on-off input[type=checkbox]:checked + label {
  background-color: #009999; }

#watch7-sidebar .watch-sidebar-separation-line {
  border-color: #000; }

.video-list .video-list-item .title, #watch7-sidebar .video-list-item a:hover .title {
  color: #999; }

.yt-badge {
  background-color: #111;
  border-color: #000;
  color: #999; }

}
