css:
@-moz-document domain("webnovel.com") {

* :not(select):not(:hover)>.cha-header:not(:hover),* > .cha-header:not(:hover),
* :not(select):not(:hover)>.cha-fly:not(:hover),* > .cha-fly:not(:hover)
    {
    opacity: 0 !important;
    transition: all .5s !important;
    }

.g_footer,  .g_txt_over ._readmore,
.bt-membership, .m-gt-box, .chapter-pop-tip, .operate-bar, .user-links-wrap, .cha-info p, .m-comment-bd ._readmore, .para-comment-num
    {
    display: none !important;
    }

.cha-page, .m-comment-bd, .g_txt_over
    {
    min-width: 0% !important;
    max-width: 100% !important;
    flex: 100% !important;
    min-height: 0px !important;
    max-height: none !important;
    }

body, .g_header, .cha-page-in, .pl24, .cha-modal-bd, .mb12, .m-comment-ft, .m-comment-tools .m-comment-tool + .m-comment-tool, .pt16
    {
    margin: 0px !important;
    padding: 0px !important;
    }

.cha-page
    {
    padding-left: 5px !important;
    padding-right: 10px !important;
    }

zzz
    {
    font-size: 16px;
    line-height: 1.25;
    }

.cha-page-in, .cha-words, .cha-tit h3
    {
    font-size: 40px;
    line-height: 1.25em;
    }

.cha-words .cha-paragraph + .cha-paragraph
    {
    margin-top: 0em;
    margin-bottom: 1em;
    }

.cha-page, .cha-modal
    {
    border: none !important;
    }

/*---------------------------------------------- General ----------------------------------------------*/

#modalReview .cha-modal-bd
    {                                            /*  Comment > remove header spacing  */
    top: 100px;
    }

.m-comment-bd, .g_txt_over
    {                                            /*  Comment + Blurb > always reveal  */
    overflow: visible;
    }

.g_good span, .g_good strong, .m-comment-tool.g_good span, svg
    {                                            /*  Comment + Chapter Break > icon align  */
    vertical-align: top;
    }

.m-comment-qt
    {                                            /*  Comment > remove quote spacing  */
    margin-top: 0px;
    }

.para-comment-allowed .hover-light:hover
    {                                            /*  Content > remove text hover highlight  */
    transition: none !important;
    }

.pf
    {                                            /*  Content > scroll fix  */
    position: absolute;
    }

/*---------------------------------------------- Color ----------------------------------------------*/

.g_mod, .content-list li:nth-child(4n), .content-list li:nth-child(4n-1),
.cha-page, .page, .para-comment-allowed .hover-light:hover
    {                                            /*  BG = black  */
    background-color: #000;
    }

.g_header, .det-hd, .g_msg_spo._retract, .g_msg_spo, .ui-page, 
.cha-header, .cha-fly, .cha-modal, #modalReview > div:nth-child(1)
    {                                            /*  BG = dark blue-grey  */
    background-color: #445;
    }

.m-comment-qt, .ui-select-button, .ui-select-datalist-li
    {                                            /*  BG = grey  */
    background-color: #666;
    }

.ui-page-current, .ui-page:hover
    {                                            /*  BG = blue  */
    background-color: #46F;
    }

.det-tab-nav a._on span, .g_msg_spo._retract::before, .g_msg_hd, .g_msg_hd a, a.ui-page, .ui-page, .det-hd-detail a, .det-hd-detail strong, .det-info address, .det-info address a, 
.cha-content, button, html, input, select, .c_000, .c_l, .c_s, .ui-select-button
    {                                            /*  Text = white  */
    color: #FFF;
    }

.c_m_base, .m-comment-tool
    {                                            /*  Text = light grey  */
    color: #AAA;
    }

a, .j_bookDetailModal, .m-comment-tool
    {                                            /*  Text = blue  */
    color: #46F !important;
    }



}/*





*/
