css:
@-moz-document domain("translate.googleusercontent.com"), domain("translate.google.com"), domain("translate.google.com.au"), domain("ncode.syosetu.com") {

* :not(select):not(:hover)>#novel_header:not(:hover),* > #novel_header:not(:hover) {
    opacity: 0 !important;
    transition: all .5s !important;
}

#pageBottom, #pageTop, .contents2, .koukoku_728, #novel_attention, #recommend, #footer,
div.novel_bn:nth-child(1), .chapter_title {
    display: none !important;
}

#novel_color, .index_box, #novel_p, #novel_honbun, #novel_a {
    width: 99% !important;
}

#novelnavi_right #menu_on, #novelnavi_right #menu_off, .novel_title, .novel_subtitle, .novel_writername, #novel_ex, dl.novel_sublist2 dd, dl.novel_sublist2 dt,
#novel_no, #novel_p, #novel_honbun, #novel_a, .contents1 {
    margin: 0px !important;
    padding: 0px !important;
}

.novel_title, #novel_ex, .index_box, 
#novel_no, #novel_p, #novel_honbun, #novel_a, .contents1 {
    padding-left: 5px !important;
    padding-right: 10px !important;
}

#novelnavi_right #menu_on, #novelnavi_right #menu_off {    /*  Display Options > Button  */
    padding-top: 5px !important;
    top: 0px;
    right: 10px;
    width: 100px !important;
    height: 41px !important;
    font-size: 100%;
    vertical-align: middle;
}

.novelview_navi {                                          /*  Display Options > Menu  */
    top: 48px;
    background: #fdfdfd;
    font-size: 100%;
    line-height: 1.5;
    width: 180px;
} 

#container {                                               /*  Index > Title > position  */
    margin-top: 44px;
}

.novel_writername {                                        /*  Index > Title > Author > position + spacing  */
    text-align: center;
    padding-top: 10px !important;
    padding-bottom: 10px !important;
}

#novel_ex {                                                /*  Index > Blurb > spacing  */
    padding-bottom: 3em !important;
}

.index_box {                                               /*  Index > Table > width fix workaround  */
    width: 98% !important;
}

dl.novel_sublist2 dd, dl.novel_sublist2 dt {               /*  Index > Table > Cell > spacing  */
    padding-top: 1px !important;
    padding-bottom: 1px !important;
}

dl.novel_sublist2 dd {                                     /*  Index > Table > Column > width  */
    width: 70% !important;
}

dl.novel_sublist2 dt {                                     /*  "  */
    width: 20% !important;
}

.contents1 {                                               /*  Content > Header > size  */
    margin-left: auto !important;
    margin-right: auto !important;
    width: -moz-fit-content;
}

.novel_subtitle {                                          /*  Content > Title > spacing  */
    padding-bottom: 2em !important;
}

#novel_p, #novel_honbun, #novel_a {                        /*  Content > Segment spacing  */
    padding-bottom: 3em !important;
}

#novel_footer {                                            /*  Content > Footer > appearance  */
    margin-top: 10px;
    border-top-width: 2px;
    border-top-style: solid;
    border-bottom-width: 2px;
    border-bottom-style: solid;
    width: -moz-fit-content;
}

#novel_footer .undernavi li {                              /*  Content > Footer > link spacing  */
    margin-left: 20px;
    margin-right: 20px;
}

#novel_ex, div.chapter_title, dl.novel_sublist2 dd, dl.novel_sublist2 dt, .novel_writername,
#novel_footer .undernavi li {
    font-size: 15px;                                       /*  Subtext  */
    line-height: 1.4;
}

#novel_color, .novel_title, .novel_subtitle, .novel_view, .contents1 > span, .contents1 {
    font-size: 30px;                                       /*  Text  */
    line-height: 1.4;
}

a, ul.novelview_menu a {                                   /*  Link color  */
    color: #00aeffe6;
}

a:hover {                                                  /*  Link color - hover  */
    color: #55f19ccc;
}




/*  Color scheme  */

body {
    background-color: #FFFFFF !important;
}

#novel_color, #novel_no {
    color: #000000 !important;
}




