css:
@-moz-document domain("vk.com") {

div[class*="thumb"],
img {
	border-radius: 2px !important;
}

@font-face {
    font-family: 'Roboto';
    font-style: normal;
    font-weight: 400;
    src: local('Verdana');
}
@font-face {
    font-family: 'Roboto';
    font-style: normal;
    font-weight: 500;
    src: local('Verdana Bold');
}
@font-face {
    font-family: 'Roboto';
    font-style: normal;
    font-weight: 700;
    src: local('Verdana Bold');
}

.bp_text,
.wall_post_text,
.wall_reply_text {
    text-align: justify !important;
    -moz-hyphens: auto !important;
    -webkit-hyphens: auto !important;
    hyphens: auto !important;
}

}
